css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rl-trades.com"){
	body {
		background-color: #111;
		padding: 0 20px;
	}

	body * {
		color: #adadad;
	}

	td {
		background-color: #272727;
		color: #e7e7e7;
		border: 1px solid black;
	}

	#rs_table tr:hover td{
		color: #e7e7e7;
		background-color: #323232;
	}

	input,
	button,
	select,
	#sites span,
	#sites_sel label{
		color: black;
	}

	#welcome,
	#intro,
	#logo,
	#rs_table tbody:first-child{
		display: none;
	}

	.new td,
	.new td a {
		color: #e7e7e7;
		background-color: #082715;
	}

	#trade_results a {
		font-weight: bold;
	}

	#trade_results tr td:nth-child(1) a:visited {
		color: #8e44ad;
	}

	#trade_results tr td:nth-child(2) span {
		color: #c0392b;
	}

	#contents {
		width: 80%;
		max-width: initial; min-width: initial;
	}


	#rs_table {
		width: 100%;
		max-width: initial; min-width: initial;
	}

	#sidebar {
		width: 20%;
		max-width: initial; min-width: initial;
	}

	#search_container {
		width: 100%;
		max-width: initial; min-width: initial;
	}

	input[type="text"] {
		width: 400px;
	}
}
