css:
@-moz-document domain("steemit.com") {
/**
 * Style mods for Steemit.com,
 *
 * Author: https://steemit.com/@ura-soul
 *
 */
  
    body, .App__content, .theme-original {
    background: hsl(92.7, 33.3%, 19.4%);
    background: radial-gradient(circle at bottom, hsl(36.4, 82.5%, 22.4%) 0%,hsl(36.4, 82.5%, 22.4%) 5%,hsl(29.5, 31.3%, 61.8%) 7%,hsl(57.1, 32.8%, 50.4%) 14%,hsl(57.5, 42%, 45.3%) 43%,hsl(82.4, 66.3%, 32.5%) 49%,hsl(82.4, 66.3%, 32.5%) 53%,hsl(104.2, 34.1%, 32.7%) 53%,hsl(104.2, 34.1%, 32.7%) 100%);
    color: hsl(57.4, 100%, 86.3%);
    background-attachment: fixed;
  }

  a, a:focus, a:visited, a:active, .steemit a, table thead, .VotesAndComments__votes 
  {
        color: hsl(28.7, 100%, 82%) !important;
  }

  a:hover {
    color: hsl(109.3, 100%, 94.5%) !important;
  }
  
  hr {
    border-bottom: 1px solid hsl(70, 84.8%, 38.8%);
    margin: 1rem auto 1.2rem;
  }
  
  label {
    color: hsl(21, 100%, 26.9%);
  }
  
  table thead, table tbody, table tfoot {
    background-color: transparent;
  }

  .Markdown blockquote > p, .ReplyEditor__body.rte blockquote > p, .theme-original .Markdown blockquote > p, .theme-original .ReplyEditor__body.rte blockquote > p, blockquote * {
    color: hsl(33.1, 100%, 82.9%);
  }
  
  .theme-original .Markdown blockquote, .theme-original .ReplyEditor__body.rte blockquote
  {
    border-color:  hsl(33.1, 100%, 82.9%);
  }
  
  .info {
    color:hsl(118.8, 41.3%, 47.5%);
  }
  
  /* logo */
  
  #steem path:nth-of-type(2){
    fill:hsl(101.9, 100%, 77.8%)!important;
  }
  
  #steem path {
    fill: hsl(113.1, 23.4%, 56.5%);
  }
  
  .beta {
    color: hsl(71.9, 85.6%, 62%)!important;
  }
  
  /* header */
  
  .UserProfile__banner > div.column {
       text-shadow:1px 1px 2px hsla(0, 0%, 0%, 0.5) !important;
   }


  .Header {
    box-shadow: 0px 0 5px 0 hsla(0, 0%, 20%, 0.7);
  }
  
  .Header__top, .header {
    border-bottom: 1px solid hsla(88.7, 65.1%, 21.4%, 0.7);
  }

  .Header .menu, .Header__top, .Header__sub-nav, .top-bar, .top-bar ul {
    background-color: hsl(82.1, 70%, 23.5%);
  }   
  
  .Header__top ul > li.delim {
    color: hsla(81.9, 32.3%, 38.2%, 0.7);    
  }
  
  .Header__sub-nav {
    border-bottom: 1px solid hsla(93.2, 35.8%, 20.8%, 0.6);
  }
  
  li.Header__userpic .Userpic {
    border: 1px solid hsla(82.8, 52.3%, 43.5%, 0.7);
  }

  .Header .menu .hamburger::after {
     background: hsl(28.7, 100%, 82%);
     box-shadow: 0 7px 0 hsl(28.7, 100%, 82%),0 14px 0 hsl(28.7, 100%, 82%);
  }  
  
  .Header .menu .hamburger:hover::after{
     background: hsl(28.7, 100%, 82%);
     box-shadow: 0 7px 0 hsl(28.7, 100%, 82%),0 14px 0 hsl(28.7, 100%, 82%);
  }
  
  .UserProfile__banner{
      text-shadow: 0px 0px 2px hsla(27.3, 100%, 57.8%, 0.7);
      color: hsl(27.7, 27.7%, 18.4%);
  }
  
  .submit-story a{
    background: hsl(108.7, 65.6%, 36.5%) !important;
    color: hsl(20.8, 72.2%, 92.9%) !important;
    border-radius: 6px;
  }
  
  
  /* search icon */
  .Header svg > path {
    fill: hsl(28.7, 100%, 82%);
   }
  
  /* navigation menu */
  
  .Header__sub-nav li > a {
    color: hsl(213, 29%, 72.9%);
  }   
  
  .HorizontalMenu li > a:hover {
    color: hsl(242.2, 41.5%, 87.3%);
  }
  
  .Header__sub-nav li > a {
    background: transparent;
  }
  
  .UserProfile__top-nav .menu > li > a.active {
    background-color: hsl(104.2, 34.1%, 32.7%);
    color: hsl(50.4, 100%, 86.5%) !important;
  }
  
  .HorizontalMenu .active > a {
    color: hsl(91.4, 22.6%, 18.2%);
  }
  
  .UserProfile__top-nav {
    background-color: hsl(93.3, 56.8%, 21.8%);
  }
  
  .UserProfile__top-nav .menu > li > a:hover, .UserProfile__top-menu > .shrink > .menu > li:hover {
    background-color: hsl(104.2, 34.1%, 32.7%);
    color: hsl(50.4, 100%, 86.5%) !important;
  }
  
  .VerticalMenu svg path, .VerticalMenu svg polygon {
    fill: hsl(53.1, 66.7%, 71.8%);
  }
  
  .VerticalMenu > li.title {
    border-bottom: 1px solid hsla(0, 0.4%, 55.9%, 0.5);
  }
    
  .theme-original .articles__layout-selector:hover .icon-svg--accent {
    fill: white;
  }
  
  /* drop down menu */
  
  .Icon.dropdown-arrow polygon {
    fill: hsl(51.5, 88.6%, 65.7%);
  }
  
  .dropdown-pane, .gsc-control-cse .gsc-option-menu {
    background-color: hsl(104.2, 34.1%, 32.7%);
    border: 1px solid hsla(87.7, 29.7%, 42.9%, 0.7);
  }
  
  .gsc-option-menu-item {
    color: hsl(240, 68.2%, 91.4%) !important;    
  }

  .VerticalMenu > li > a:hover, .gsc-option-menu-item-highlighted {
    background-color: hsl(109.7, 37.6%, 36.5%);
  }
  
  .DropdownMenu > .VerticalMenu {
    background-color: hsl(120, 31.9%, 18.4%);
    border: 1px solid hsla(118.7, 18.5%, 47.6%, 0.8);
  }

  
  /* Sidebar menu */
  
  .SidePanel > div {
    background-color: hsl(94.9, 40.7%, 26.5%);
  }
  
  .SidePanel > div .menu > li > a {
    color: hsl(254.3, 70%, 88.2%);
    border-top: 1px solid hsla(113.5, 23.2%, 38.8%, 0.7);
   }

  .SidePanel > div .menu > li > a:hover {
    background-color: hsl(97.8, 23.7%, 38%);
    color: hsl(42.4, 100%, 86.7%) !important;
  }
  
  .SidePanel .menu > li.last {
    border-bottom: 1px solid hsl(120, 23.8%, 45.3%);
  }
  
  .SidePanel > div .menu > li path {
   fill: hsl(36.1, 93.7%, 62.7%);  
  }

    .theme-original .c-sidebar__module, .c-sidebar__module {
    background-color: transparent;
    border-color: transparent;
  }

  /* notifications */
  
  .NotifiCounter {
    background-color: hsl(18.9, 91.7%, 42.7%);
  }
  
  /* user profile */
  
  .UserProfile__banner > div.column, .UserProfile__banner > div.columns {
   background-image: url('http://i.imgur.com/ujexZV6.jpg')!important;
   background-size: contain;
   background-repeat: no-repeat;
   background-position: right;
   background-color: #bec2a1;
  }
  
  .UserProfile__banner .UserProfile__stats,
  .UserProfile__banner .UserProfile__info 
  {
    font-weight:bold;
  }
  
  .UserProfile__banner .Userpic {
    border: 1px solid hsla(82.8, 52.3%, 43.5%, 0.7);
  }
  
  .UserProfile__banner a{
    color: hsl(0, 56%, 42.7%) !important;
  }
  
  .UserProfile__banner .Icon svg{
    fill: hsl(27.7, 27.7%, 18.4%)!important;
  }
  
  /* user data */
  
  .Reputation {
    background-color: hsla(0, 68.9%, 46.7%, 0.6);
    border: 1px solid hsla(0, 100%, 65.7%, 0.7)!important;
    border-radius: 50%;
    color: hsl(0, 0%, 100%)!important;
  }
  
  /* Posts */
  
  .articles__tag-link {
    color: white !important;
  }
  
  .layout-list .articles, .theme-original .articles {
    background-color: transparent!important;
    border: 0px solid !important;
  }  
  
  .PostFull {
    border-bottom: 1px solid hsla(109.6, 74.3%, 21.4%, 0.8);
    background: hsl(93.7, 33.9%, 23.7%);
  }
  
  .layout-list .articles__summary, .theme-original .articles__summary {
    padding: 0.7rem;
    background-color: hsla(106.5, 55.1%, 17.5%, 0.9);
    border: 1px solid hsla(92.7, 86.1%, 22.5%, 0.5);
    border-radius: 11px;
    box-shadow: 0px 0px 4px 0 hsla(0, 0%, 0%, 0.1);
    border-bottom:0px solid !important;
  }
  
  .entry-title > a, .PostSummary__header > h3 > a
  {
   color: hsl(92.2, 75.3%, 71.4%) !important;    
  }
  
  .PostSummary__body {
    color: hsl(240, 3%, 74.1%);
  }
  .PostSummary__image {
    background-color: transparent;
    border: 1px solid hsla(121, 42.3%, 27.8%, 0.5);
    border-radius: 5px;
  }
  
  .articles__resteem-text  {
    color: hsl(57, 99.1%, 43.5%);
  }
  
  .PostSummary__reblogged_by .Icon path, .reblog path {
    fill: hsl(57, 99.1%, 43.5%);
  }

  .PostSummary__reblogged_by, .articles__resteem-text{
    color:  hsl(57, 99.1%, 43.5%)!important;
  }
  
  .PostsList__post_overlay {
    background-color: hsla(120, 37.9%, 5.7%, 0.9);
  }

  .PostsList__post_container, .PostsList__post_container .PostFull, .PostsList__post_container  {
    background-color: hsl(93.7, 33.9%, 23.7%)!important;
    color: hsl(0, 0%, 99.6%);
  }
  
  .Preview .MarkdownViewer{
    border: 1px solid hsla(110.2, 85.9%, 25.1%, 0.9)!important;
  }
  
    .theme-original .link--secondary, .theme-original .PlainLink, .theme-original .PostFull__header a, .PostFull__header .theme-original a, .theme-original .articles__resteem-username, .theme-original .articles__tag-link a, .articles__tag-link .theme-original a, .theme-original .user__username, .theme-original .user__reputation, .theme-original .timestamp__time, .theme-original .articles__resteem .username, .articles__resteem .theme-original .username {
    color: hsl(110.8, 51.3%, 70.2%);
  }
  
  .theme-original .link--secondary:hover, .theme-original .PlainLink:hover, .theme-original .PostFull__header a:hover, .PostFull__header .theme-original a:hover, .theme-original .articles__resteem-username:hover, .theme-original .articles__tag-link a:hover, .articles__tag-link .theme-original a:hover, .theme-original .user__username:hover, .theme-original .user__reputation:hover, .theme-original .timestamp__time:hover, .theme-original .articles__resteem .username:hover, .articles__resteem .theme-original .username:hover, .theme-original .link--secondary:focus, .theme-original .PlainLink:focus, .theme-original .PostFull__header a:focus, .PostFull__header .theme-original a:focus, .theme-original .articles__resteem-username:focus, .theme-original .articles__tag-link a:focus, .articles__tag-link .theme-original a:focus, .theme-original .user__username:focus, .theme-original .user__reputation:focus, .theme-original .timestamp__time:focus, .theme-original .articles__resteem .username:focus, .articles__resteem .theme-original .username:focus {
    color: hsl(110, 100%, 92.9%);
  }
  
  .theme-original .articles__summary-footer {
    border-top: 1px solid hsl(120, 22.1%, 30.2%);
  }
  
  .theme-original .articles__summary-header {
     border-bottom: 1px solid hsl(120, 22.1%, 30.2%);
  }
  
  #svg-icon-symbol-layout * {
    fill: hsl(120, 36.2%, 72.9%);
  }
  
  /* voting interface */
  
  .Voting__adjust_weight .rangeslider-horizontal .rangeslider__fill, 
  .Voting__adjust_weight_down .rangeslider-horizontal .rangeslider__fill
  {
      background: hsl(21.3, 85.6%, 62%);
  }

  .PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
    border-right: 1px solid hsl(40, 5%, 52.9%);
  }
  
  .PostSummary__time_author_category, .PostFull__time_author_category, .Comment__header .updated{
    border-right: 1px solid hsla(106.4, 8.8%, 51%, 0.6) !important
  }
  
  .PostFull__footer .FoundationDropdownMenu__label, .PostFull__footer a, .PostFull__views, .Comment__footer a{
    color: hsl(30, 20.8%, 71.8%);    
  }

  .DropdownMenu.show > .VerticalMenu{
     color: hsl(0, 100%, 90.4%);
  }

  .PostSummary__time_author_category,.PostFull__time_author_category_large, .PostFull__time_author_category, .Comment__header .updated, .PostFull__time_author_category_large a, .PostFull__time_author_category_large strong{
    color: hsl(48.8, 71.8%, 69.4%)!important;
   }

  .FormattedAsset .decimal {
    opacity: .8;
    color: #6CA000;
   }

  .Voting__inner, .VotesAndComments__votes, .PostSummary__time_author_category .Reblog__button, .PostFull__time_author_category,.PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
    border-right: 1px solid hsl(0, 0%, 46.7%);
  }
  
  .PostSummary__time_author_category
  {
    border-left: 1px solid hsl(0, 0%, 46.7%);
  }
  
  #Chevron_Up_Circle path {
    stroke: hsl(35.5, 63.3%, 76.5%)!important; 

  }
  
  #Chevron_Up_Circle circle {
   
    fill:transparent!important;
    stroke: hsl(134.6, 83.9%, 41.4%)!important;
  } 

  .Icon_1x #Chevron_Up_Circle circle, .Voting__button--upvoted   #Chevron_Up_Circle circle {
    fill: hsl(120.7, 60.3%, 27.6%)!important;
    stroke: hsl(105.8, 47.2%, 75.5%)!important;
  }
 
   
  
   .Voting__button-up > a:hover path, a.confirm_weight:hover path {
    fill: hsl(67.9, 100%, 26.9%)!important;
    stroke: hsl(70.1, 88.6%, 65.7%)!important;
  }
  
  .Reblog__button path{
    fill: hsl(17.1, 100%, 77.3%) !important
  }
  
  .Reblog__button-active path {
    fill: hsl(57, 99.1%, 43.5%)!important;
  }
  
  .VotesAndComments path {
  fill: hsl(17.1, 100%, 77.3%) !important;
  }
  
  .PostFull__footer {
    border-bottom: 1px solid hsl(96, 5.6%, 35.3%)!important;
    z-index: 99;
  }

  /* labels and markers */
  
  .PostFull__footer svg {
    fill: hsl(17.1, 100%, 77.3%) !important;
  }
  
  .nsfw-flag {
    color: hsl(0, 95.1%, 75.9%);
    border: 1px solid hsl(0, 95.1%, 75.9%);
  }
  
  .Voting__button path {
    fill: hsl(236.5, 10.3%, 67.6%);
  }

  .Voting__button-down a:hover path {
    fill: hsl(0, 95.1%, 75.9%);
  }
  
  .theme-original .layout-list .articles__hr{
    border-bottom: 1px solid hsla(77.3, 53.2%, 75.7%, 0.9);
  }

  /* Post Index */
  
  .PostsIndex__topics {
    border-left: 1px solid hsla(59.1, 29.4%, 46.1%, 0.4);
   }

  .Topics__title {
    color: hsl(42.3, 98.8%, 67.6%) !important;
  }
  
  /* comments */
  
  .Comment__header-user, .Post__comments_sort_order {
    color: hsl(30, 20.8%, 71.8%);    
  }
  
  .callout {
    border: 1px solid hsla(0, 0%, 20%, 0.3);
    background-color: hsla(0, 0%, 100%, 0.1);
    color: hsl(0, 0%, 92.5%); 
  } 

  .Comment .highlighted {
    background: hsla(120.6, 44.1%, 46.3%, 0.3);
  }
  
  /* buttons */
  
  .close-button:focus, .close-button:hover {
    color: hsl(0, 86.4%, 79.8%);
  }
  
  .button.disabled, .button[disabled] {
    opacity: .4;
  }
  
  .button {
     background-color: hsl(109.9, 100%, 18.6%);
  }

  .button:focus, .button:hover {
    background-color: hsl(110.2, 100%, 26.5%)!important;
  }
  
  .button.hollow {
    border: 1px solid hsl(120, 42.9%, 5.5%) !important;
    color: hsl(120, 42.9%, 5.5%) !important;
  }
  
  .button.hollow a{
    color: hsl(120, 35.1%, 14.5%)!important;
  }
  
  .button.hollow:focus, .button.hollow:hover {
    border-color: hsla(18.4, 57.4%, 52.2%, 0.7) !important;
    color: hsl(37.5, 20.5%, 84.7%) !important;
   }

  .button.hollow:focus a, .button.hollow:hover a {
      color: hsl(125.5, 100%, 27.6%) !important;
  }
    
  .button.disabled, .button.disabled:focus, .button.disabled:hover, .button[disabled], .button[disabled]:focus, .button[disabled]:hover {
    background-color: hsl(111.4, 21.9%, 12.5%);
  }

  /* tags */
  
  .TagList__horizontal a {
    background: hsla(124.8, 34.9%, 42.2%, 1);
    color: hsl(39.7, 41.3%, 72%);
   }

  .TagList__horizontal a:hover {
      background: hsla(124.8, 30.7%, 60.4%, 0.6);
  }
  
  /* inputs */
  
  input:disabled, input[readonly], textarea:disabled, textarea[readonly] {
    background-color: hsl(0, 0%, 24.7%);
    color: hsl(0, 0%, 65.5%);
   }
  
  [type="color"]:focus, [type="date"]:focus, [type="datetime-local"]:focus, [type="datetime"]:focus, [type="email"]:focus, [type="month"]:focus, [type="number"]:focus,    [type="password"]:focus, [type="search"]:focus, [type="tel"]:focus, [type="text"]:focus, [type="time"]:focus, [type="url"]:focus, [type="week"]:focus, textarea:focus {
    outline: none;
    border: 1px solid hsla(112.5, 4.9%, 68.2%, 0.6);
    background-color: hsl(86.3, 28.4%, 39.4%);
    box-shadow: inset 0 1px 2px hsla(113.2, 90.6%, 54.3%, 0.3);
  }
  [type="color"], [type="date"], [type="datetime-local"], [type="datetime"], [type="email"], [type="month"], [type="number"], [type="password"], [type="search"], [type="tel"], [type="text"], [type="time"], [type="url"], [type="week"], textarea {
    border: 1px solid hsla(112.2, 43.4%, 10.4%, 0.6);
    background-color: hsl(107.6, 33.7%, 33.7%);
    box-shadow: inset 0 1px 2px hsla(113.2, 90.6%, 54.3%, 0.3);
    color: hsl(0, 0%, 100%);
  }
  
  .drag-and-drop {
    color: hsl(0, 0%, 97.3%);
    background-color: hsl(118.7, 33.3%, 27.6%);
    border-color: hsl(116.2, 52.8%, 17.5%);
  }
  
  select, select:focus {
    background-color: hsl(129.2, 48.9%, 26.1%);
    border: 1px solid hsla(113.7, 100%, 26.3%, 0.8);
    color: hsl(0, 0%, 92.5%);
    box-shadow: 0 0 5px hsl(245.2, 18.4%, 24.5%);
  }
  
  /* editor */
  
  .ReplyEditor .Preview .Markdown {
    border: 1px solid hsl(253.3, 43.7%, 59.6%);
    border-radius: 8px;
  }
  
  .ReplyEditor__draft {
    background: hsla(125.6, 34%, 18.4%, 0.9);
    color: hsl(0, 0%, 73.3%);
  }
  
  .upload-enabled {
    border-bottom: 1px dashed hsl(101.8, 100%, 6.5%);
  }
  
  .Preview > h6, .Preview > .float-right > a {
    color: hsl(114.5, 76.7%, 8.4%)!important;
  }
  
  .RichTextEditor__root___33zoV {
      background: hsl(124.5, 40.6%, 32.4%);
      border-color: hsl(124.3, 74.2%, 18.2%);
  }

  /* google search */
  
  .cse .gsc-control-cse, .gsc-control-cse {
    background: transparent;
    border: 0px solid;
  }
  .gsc-input, .gsc-completion-container table {
    color: black!important;
  }
  
  .gsc-result-info, .gsc-orderby-label {
    color: hsl(0, 0%, 81.2%);
  }
  
  .gsc-above-wrapper-area, .gsc-webResult.gsc-result {
    border-color: hsla(0, 0%, 91.4%, 0.5);
  }
  
  .gsc-preview-reviews, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em {
    color: hsl(0, 0%, 84.7%);
  }

  .gs-result .gs-title, .gs-result .gs-title * {
    color: hsl(37.6, 100%, 75.9%);
  }

  .gsc-results .gsc-cursor-box .gsc-cursor-page {
    color: hsl(0, 19.6%, 82%);
  }
  
  /* wallet */
  
  .UserWallet {
    color: hsl(20, 100%, 4.1%);
  }
  
  .UserWallet .Trans a, .SavingsWithdrawHistory a, .UserList a {
    color:hsl(0, 100%, 25.7%)!important;
  }
  
  .WalletSubMenu a {
    color:hsl(26.2, 90.7%, 8.4%) !important;
  }
  
  .WalletSubMenu{
    border-bottom-color: hsl(26.2, 90.7%, 8.4%) !important;
  }
  
  .UserWallet__balance {
    color: hsl(17, 100%, 11.8%);
  }
  
  .UserWallet__claimbox {
    background: hsla(30, 64.8%, 27.8%, 0.9);
    border: 1px solid hsla(21.2, 75.8%, 70.8%, 0.5);
    color: hsl(28.3, 100%, 92.9%);
  }
  
  .button.hollow:focus, .button.hollow:hover, .button.hollow.secondary:hover, button.hollow.secondary:focus  {
    border-color: hsl(0, 0%, 100%);
    color: hsl(0, 0%, 100%);
  }
  
  .button.hollow, .button.hollow:focus, .button.hollow:hover {
    background-color: transparent;
  }
  
  .button.hollow, .button.hollow.secondary {
    border: 1px solid hsl(214.6, 64.8%, 82.2%);
    border-top-color: hsl(214.6, 64.8%, 82.2%);
    border-right-color: hsl(214.6, 64.8%, 82.2%);
    border-bottom-color: hsl(214.6, 64.8%, 82.2%);
    border-left-color: hsl(214.6, 64.8%, 82.2%);
    color: hsl(214.6, 64.8%, 82.2%);
  }
  .button:focus, .button:hover {
    background-color: hsl(86.8, 59.4%, 59.4%) !important;
  }
