/***** Media print specific styles *****/
@media print {
  #top-menu, #header, #main-menu, #sidebar, #footer, .contextual, .other-formats { display:none; }
  #main { background: #fff; }
  #content { width: 99%; margin: 0; padding: 0; border: 0; background: #191919; overflow: visible !important;}
  #wiki_add_attachment { display:none; }
  .hide-when-print { display: none; }
  .autoscroll {overflow-x: visible;}
  table.list {margin-top:0.5em;}
  table.list th, table.list td {border: 1px dotted #585a57;}
}

/* Accessibility specific styles */
.hidden-for-sighted {
  position:absolute;
  left:-10000px;
  top:auto;
  width:1px;
  height:1px;
  overflow:hidden;
}


/*888*/
#context-menu { position: absolute; z-index: 40; font-size: 0.9em;}

#context-menu ul, #context-menu li, #context-menu a {
  display:block;
  margin:0;
  padding:0;
  border:0;
}

#context-menu ul {
  width:150px;
  border: 1px dotted #585a57;
  background:white;
  list-style:none;
  padding:2px;
  border-radius:2px;
}

#context-menu li {
  position:relative;
  padding:1px;
  z-index:39;
  border:1px dotted #585a57;
}
#context-menu li.folder ul { position:absolute; left:168px; /* IE6 */ top:-2px; max-height:300px; overflow:hidden; overflow-y: auto; }
#context-menu li.folder>ul { left:148px; }

#context-menu.reverse-y li.folder>ul, #context-menu li.folder.up>ul { top:auto; bottom:0; }
#context-menu.reverse-x li.folder ul { left:auto; right:168px; /* IE6 */ }
#context-menu.reverse-x li.folder>ul { right:148px; }

#context-menu.reverse-y li.folder.down>ul {
  position:absolute; top:-2px; bottom: auto; max-height:300px; overflow:hidden; overflow-y: auto;
}

#context-menu a {
  text-decoration:none !important;
  background-repeat: no-repeat;
  background-position: 1px 50%;
  padding: 2px 0px 2px 20px;
  width:100%; /* IE */
}
#context-menu li>a { width:auto; } /* others */
#context-menu a.disabled, #context-menu a.disabled:hover {color: #aaa;}
#context-menu li a.submenu { padding-right:16px; background:url("http://www.redmine.org/images/bullet_arrow_right.png") right no-repeat; }
#context-menu li:hover { border:1px dotted #585a57; background-color:#eef5fd; border-radius:3px; }
#context-menu a:hover {color:#2A5685;}
#context-menu li.folder:hover { z-index:40; }
#context-menu ul ul, #context-menu  li:hover ul ul { display:none; }
#context-menu li:hover ul, #context-menu li:hover li:hover ul { display:block; }
#context-menu a.icon-checked {background: url(http://www.redmine.org/images/toggle_check.png) no-repeat 3px 40%;}

/* selected element */
.context-menu-selection { background-color:#fcd581 !important; color:#222 !important; }
.context-menu-selection a, .context-menu-selection a:hover { color:#222 !important; }
.context-menu-selection:hover { background-color:#fcd581 !important; color:#222  !important; }
