    stroke-width: 1px;
}
.key12, .fill12 {
    fill: #9966ff;
    fill-opacity: 0.5;
    stroke: none;
    stroke-width: 1px;
}
.keyText {
    fill: #fff;
    font-family: "Arial",sans-serif;
    font-size: 10px;
    font-weight: normal;
    text-anchor: start;
}


}
