css:
@-moz-document domain("www.gta5-mods.com") {
#content {
    padding-top: 40px;
    padding-bottom: 15px;
    min-height: 400px;
    background: #161D21;
}

#file h1 {
    color: #fff;
}

.panel-body {
    padding: 15px;
    background: #37474F;
    color: #fff;
}

.panel-default {
    border-color: #000;
}

#file .file-version-container {
    padding: 8px;
    margin-bottom: 8px;
    width: 100%;
    background: #263238;
}

.btn-primary {
    color: #fff;
    background-color: #03A9F4;
    border-color: #03A9F4;
}

.btn.btn-o-primary.active, .btn.btn-o-primary.active:hover, .btn.btn-o-primary.active:focus {
    border-color: #03A9F4;
    background-color: #03A9F4;
    color: #fff;
}

#main-nav {
    border-radius: 0;
    border-bottom: 0;
    margin-bottom: 0;
    background-color: #03A9F4;
    position: fixed;
    left: 0;
    width: 100%;
    z-index: 3;
}

a {
    color: #fff;
    text-decoration: none;
}
.user-panel .username {
    color: #fff;
    font-size: 18px;
    line-height: 18px;
    vertical-align: middle;
}

.user-panel .user-social a {
    color: #fff;
    font-size: 20px;
    padding: 0 1px;
}

.btn.btn-o-primary {
    border-color: #5bc0de;
    color: #5bc0de;
}

.btn.btn-o-info {
    border-color: #5bc0de;
    color: #5bc0de;
}

.btn-default, .btn.btn-o-primary, .btn.btn-o-info, .btn.btn-o-danger {
    background-color: #263238;
}

.file-list .file-list-obj .details .author {
    display: block;
    font-weight: 200;
    color: #fff;
    font-style: italic;
    margin-bottom: 7px;
    background: #263238;
}

.file-list .file-list-obj:hover {
    border-color: #000;
    box-shadow: 0 1px 1px rgba(0,0,0,0.1);
}

h4, .h4 {
    font-size: 18px;
        color: #fff;
}

#file .panel {
    margin-bottom: 10px;
    background: #263238;
}

.file-list .file-list-obj .details .name {
    display: block;
    font-size: 20px;
    line-height: 22px;
    color: #fff;
}

#navigation>li a:hover .label-border, #navigation>li.active .label-border {
    background: #03A8F3;
}

#file .file-stats .download-icon {
    font-size: 140%;
    color: #fff;
}

#file .file-stats .file-stat label {
    display: block;
    font-size: 50%;
    font-weight: bold;
    color: #fff;
}#file .file-stats .file-stat span {
    display: block;
    font-size: 75%;
    color: #fff;
}#file .file-stats .like-icon {
    font-size: 140%;
    padding-left: 8px;
    color: #fff;
}.comment-average-container {
    text-align: right;
    line-height: 50%;
    color: #fff;
}

.file-list .file-list-obj {
    display: block;
    margin-bottom: 15px;
    padding: 4px;
    text-decoration: none;
    background: #37474F;
}

.btn-default, .btn.btn-o-primary, .btn.btn-o-info, .btn.btn-o-danger {
    background-color: #263238;
    color: #fff;
}
}
