css:
/*Autohide Channels*/
@-moz-document domain(discordapp.com) {

	.channels-wrap {
		width: 50px !important;		-webkit-transition: width 1s !important; /* Safari */
		transition: width 0.2s !important;
		-webkit-transition-timing-function: cubic-bezier(0.0, 0.0, 0.2, 1) !important;
		transition-timing-function: cubic-bezier(0.0, 0.0, 0.2, 1) !important;
	}

	.channels-wrap:hover {
		width: 240px !important;
	}

/*Autohide Users*/
	.channel-members-wrap {
    max-width: 40px !important;
    min-width: 40px !important;
		width: 40px !important;		-webkit-transition: width 1s !important; /* Safari */
		transition: width 0.2s !important;
		-webkit-transition-timing-function: cubic-bezier(0.0, 0.0, 0.2, 1) !important;
		transition-timing-function: cubic-bezier(0.0, 0.0, 0.2, 1) !important;
	}

	.channel-members-wrap:hover {
		width: 200px !important;
		max-width: 200px !important;
		min-width: 200px !important;
	}

  .channel-members .member {
    padding-left: 5px !important;
    padding-right: 0px !important;
  }
  .channel-members {
    width: 100% !important;
    max-width: 100% !important;
  }
  .message-group-blocked {
    display:none !important;
  }
}
