css:
@-moz-document domain('www.omegle.com'){
body
{
	background: rgb(241,241,241);
}

#header
{
	background: white;
    box-shadow: none;
    border-bottom: solid rgb(204, 204, 204) 1px;
}

.logwrapper, .disconnectbtnwrapper, .sendbtnwrapper, #intro
{
	border-radius: 1px;   
    box-shadow: none;
}

#intro
{
 	position: relative;
    left: 0;
    right: 0;
    margin-left: auto;
}

#monitoringnotice, #monitoringnotice h2, #monitoringnotice #adultalternative, #monitoringnotice #unmonitoredvideobtn
{
	border-radius: 1px;   
    box-shadow: none;
    background: #1976D2;
    color: white;
}

#monitoringnotice
{
    border: solid #0D47A1 1px;
}

#feedback
{
    position: relative;
	width: 114%;
    text-align: center;
}

tbody img, tbody a
{
	display: none;
}

::-webkit-scrollbar
{
	background: white;
    width: 9px;
}

::-webkit-scrollbar-thumb
{
	background: rgb(204,204,204);    
}

::-webkit-scrollbar-thumb:hover
{
	background: rgb(154,154,154);
}
}
