css:
@-moz-document url-prefix("http://12306.cn"), url-prefix("http://www.12306.cn"), url-prefix("https://www.12306.cn"), url-prefix("https://12306.cn") {
@font-face {
	font-family: 'Material Icons';
	font-style: normal;
	font-weight: 400;
	src: local('Material Icons'), local('MaterialIcons-Regular'), url(https://fonts.gstatic.com/s/materialicons/v22/2fcrYFNaTjcS6g4U3t-Y5ZjZjT5FdEJ140U2DJYC3mY.woff2) format('woff2');
}
* {
	font-family: "Microsoft YaHei", "Roboto", sans-serif !important;
	font-size: 16px;
}
body {
	background-color: #f7f7f7;
}
div#top.flash-update,div#top.flash-replaced {
	display: none;
}
#page {
	margin-top: 72px;
	background:none;
	background-color:transparent;
}
#menu {
	width: 100%;
	position: fixed;
	left: 0;
	top: 0;
	display: block;
	height: 48px;
	background: none;
	background-color: #2196f3;
	box-shadow: 0 0 5px 2px rgba(0, 0, 0, .3);
	z-index: 99999;
}
#menu > ul.menubg {
	width: 960px;
	height: 48px;
	position: absolute;
	left: 50%;
	margin-left: -480px;
	background: none;
	background-color: transparent;
	padding-left: 16px;
}
#menu > ul.menubg li {
	text-align: left;
	line-height: 48px;
	background: none;
	background-color: transparent;
}
#menu > ul.menubg li#currentDate {
	display: none;
}
#menu > ul.menubg li a {
	color: #ffffff;
	font-size: 1em;
}
#menu > ul.menubg li a:hover {
	color: #e0e0e0;
	text-decoration: none;
}
div#appDown {
	display: none;
}
div#newLeft {
	display: none;
}
div#indexLeft {
	width: 100%;
}
div#indexRight {
	display: none;
}
div#newContent {
	width: 100%;
	float: none;
	padding: 32px;
	box-sizing: border-box;
	background-color: #fff;
	box-shadow: 3px 4px 6px 0px rgba(0, 0, 0, .1),-1px -1px 6px 0px rgba(0, 0, 0, .1);
	border-radius: 2px;
	height: auto;
}
div#newContent div.newsTitle {
	background: none;
	font-size: 1.5em;
	font-weight:normal;
	margin-bottom: .25em;
	color: #424242;
	padding: 0;
}
div#newContent div.newH2#newContent2 > div:first-child {
	margin-bottom: 0.75em;
}
div#newContent div.newH2#newContent2 > div:first-child > span {
	font-size: 14px !important;
	color: #aeaeae !important;
	margin-left: -8px;
}
div#newContent div.newH2#newContent2 > div:first-child > span > a u {
	font-size: 14px !important;
	text-decoration: none;
	margin: 0 4px;
}
div#newContent div.newH2#newContent2 > div:first-child > marquee {
	display: none;
}
div#newContent div#zxdt {
	padding: 0;
	height: auto;
}
div#newContent div#zxdt ul {
	margin: 0;
	padding: 0;
}
div#newContent div#zxdt ul li {
	list-style: none;
	margin: 0;
	padding: 0;
	background: none;
	height: 32px;
	line-height: 32px;
}
div#newContent div#zxdt ul li span.zxdt_time {
	font-size: 0.85em;
	color: #aeaeae;
}
div#newContent div#zxdt ul li a {
	font-size: 1em;
	background: none;
	margin: 0;
	padding: 0;
	color: #757575;
}
#newContent2 > div.textRight > a {
	font-size: 0.85em;
	color: #aeaeae;
}
#newContent2 > div.textRight > a:hover {
	text-decoration: none;
	color: #757575;
}
div#indexLeft div#indexLeftB div#indexLeftBL {
	display: block;
	box-shadow: 3px 4px 6px 0px rgba(0, 0, 0, .1),-1px -1px 6px 0px rgba(0, 0, 0, .1);
	border-radius: 5px;
	width: 216px;
	padding: 32px;
	box-sizing: border-box;
	float: left;
	margin: 32px 0;
	background-color:#FFFFFF;
}
div#indexLeft div#indexLeftB div#indexLeftBR {
	display: block;
	box-shadow: 3px 4px 6px 0px rgba(0, 0, 0, .1),-1px -1px 6px 0px rgba(0, 0, 0, .1);
	border-radius: 5px;
	width: 728px;
	padding: 32px;
	box-sizing: border-box;
	float: left;
	background-color:#FFFFFF;
	margin: 32px 0 0 16px;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem {
	padding: 0;
	margin: 0;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li {
	height: 32px;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li a:hover {
	color: #757575;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li:first-child a img {
	display: none;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li:first-child {
	height: 32px;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li:first-child a::before {
	float: left;
	font-family: 'Material Icons';
	font-size: 30px;
	display: block;
	content: "\E570";
	line-height: 32px;
	color: #757575;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li:first-child a {
	width: 100%;
	display: block;
	height: 32px;
	line-height: 32px;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li:first-child a::after {
	margin-left: 8px;
	content: "购票入口";
	font-size: 16px;
	height: 32px;
	display: block;
	float: left;
	line-height: 32px;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem {
	width: auto;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li {
	background: none;
	background-color: transparent;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li a {
	background-image: none;
	background-color: transparent;
	padding: 0;
	border: none;
	font-size: 16px;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li a:hover {
	text-decoration: none;
}
div#indexLeft div#indexLeftB div#indexLeftBL ul.leftItem li:last-child {
	display: none;
}

div#indexLeft div#indexLeftB div#indexLeftBR div#search{
	border:none;
	background:none;
	text-align:left;
	height:48px;
	line-height:48px;
	font-size:16px;
}
div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch{
	width:560px;
}

div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch::before{
	content:"全文搜索：";
	display:block;
	float:left;
}

div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch::after{
	content:"\E8B6";
	font-family: 'Material Icons';
	font-size: 30px;
	width:30px;
	float:right;
	height:48px;
	margin-top:-46px;
	margin-right:24px;
	display: block;
	line-height: 48px;
	color: #757575;
}
div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch lable,div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch label{
	display:none;
}
div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch input#searchwordl{
	border:none;
	margin:0;
	border-bottom:1px solid #e0e0e0;
	transition:border-bottom-width ease 300ms,border-bottom-color ease 300ms;
}
div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch input#searchwordl:focus{
	outline:none;
	border-bottom:2px solid #757575;
}
div#indexLeft div#indexLeftB div#indexLeftBR div#search form#fmsearch input[type=image]{
	opacity:0;
	width:60px;
}

#indexLeftBR > div.margin_top3px.clearfix{
	display:none;
}
#indexLeftBR > div.slideTxtBox{
	border:none;
	padding:0;
	width:100%;
	margin-top:16px;
}
#indexLeftBR > div.slideTxtBox > div.hd {
	background:none;
	padding:0;
	height:32px;
	line-height:32px;
	border:none;
}

#indexLeftBR > div.slideTxtBox > div.hd > ul{
	float:none;
	position:static;
	top:0;
	left:0;
}
#indexLeftBR > div.slideTxtBox > div.hd > ul li{
	font-size:16px;
	height:32px;
	line-height:32px;
	border:none;
	text-align:left;
	padding:0 16px;
	width:auto;
	box-sizing:border-box;
	user-select:none;
}
#indexLeftBR > div.slideTxtBox > div.hd > ul li.on{
/* 	background-color:#f3f3f3; */
	box-shadow:1px -2px 2px 1px rgba(0,0,0,.2);
	border-top-left-radius:2px;
	border-top-right-radius:2px;
}
#indexLeftBR > div.slideTxtBox > div.tempWrap > div > ul{
	padding:0 20px 20px 0;
}
#indexLeftBR > div.slideTxtBox > div.tempWrap > div > ul li{
	height:32px;
	line-height:32px;
}
#indexLeftBR > div.slideTxtBox > div.tempWrap > div > ul li.info_item > a{
	background:none;
	padding-left:0;
	font-size:12px;
}

#indexRight > #slideRBox.slideRBox,
#indexRight > div.slideRTxt {
	display: none !important;
}
#indexLeftBR > div.slideTxtBox > div.tempWrap > div > ul:nth-child(1) > li{
	height:auto;
}
#indexLeftBR > div.slideTxtBox > div.tempWrap > div > ul:nth-child(1) > li > iframe{
	min-height:356px;
}


div.bottom_copy {
	width: 960px;
	height: 24px;
	display: block;
	overflow: hidden;
	transition: height ease 300ms, margin-top ease 300ms;
	margin-top: 24px;
	background-color:transparent;
	color:#757575;
}
div.bottom_copy:hover {
	height: 128px;
	margin-top: 0;
}
div.bottom_copy div.bottom_line1,
div.bottom_copy div.bottom_line2 {
	display: none;
}
div.bottom_copy div:nth-child(2) {
	transition: margin-top ease 300ms;
	margin-top: -52px;
	user-select: none;
	cursor: default;
}
div.bottom_copy:hover div:nth-child(2) {
	margin-top: 0px;
}
}

@-moz-document url-prefix("http://dynamic.12306.cn"), url-prefix("https://dynamic.12306.cn") {
@font-face {
	font-family: 'Material Icons';
	font-style: normal;
	font-weight: 400;
	src: local('Material Icons'), local('MaterialIcons-Regular'), url(https://fonts.gstatic.com/s/materialicons/v22/2fcrYFNaTjcS6g4U3t-Y5ZjZjT5FdEJ140U2DJYC3mY.woff2) format('woff2');
}
* {
	font-family: "Microsoft YaHei", "Roboto", sans-serif !important;
	font-size: 16px;
}


body table#board_table.ypxx-con th,table#board_table.ypxx-con td{
	padding:0 16px;
	height:32px !important;
	line-height:32px;
	text-align:left;
	font-size:14px;
	width:auto!important;
}


body > div.ypxx-box > div.ypxx-page {
	height:24px;
	line-height:24px;
	text-align:left;
}

body > div.ypxx-box > div.ypxx-page a#board_gotoMore{
	color:#757575;
}

body > div.ypxx-box > div.ypxx-page a#board_gotoMore:hover{
	color:#aeaeae;
}
body > div.ypxx-box > div.ypxx-page span#leftTicketTip{
	padding-left:16px;
	color:#aeaeae;
	font-size:12px;
}
body > div.ypxx-box > div.ypxx-page #board_gotoMore{
	font-size:14px;
}
}
