css:
@-moz-document regexp("https://chat\\.stackexchange\\.com/rooms/\\d+/.*"), regexp("https://chat\\.meta\\.stackoverflow\\.com/rooms/\\d+/.*"), regexp("https://chat\\.stackoverflow\\.com/rooms/\\d+/.*") {
#roomdesc,
#room-tags,
#room-ad,
#footer-legal,
#footer-logo {
    display: none !important;
}
.ob-tweet-image {
    max-width: 200px !important;
}
.hat {
    display: none !important;
}
div.sidebar-widget:nth-child(5) > a:nth-child(1) {
    display: none !important;
}
.user-container .signature .username {
    color: #cccccc !important;
}
.sidebar {
    background-color: rgba(0, 0, 0, 1) !important;
}
#inputerror {
    bottom: 30px;
    background-color: #16161D;
}
#my-rooms .room,
#no-other-rooms {
    background-color: #333333;
}
.flags-popup li.flagged-message div.content {
    background-color: inherit;
}
.popup,
#main.message-admin-mode .message-controls,
#conversation-sel {
    background-color: #0F0F14;
    color: #CCCCCC;
}
.popup .btn-close,
#conversation-sel .btn-close {
    background-color: #cccccc;
    color: #0f0f14;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #FFFFFF !important;
}
small {
    color: lightgray;
}
#my-rooms .room .actions [for|="actions-trigger"],
#rooms .room .actions [for|="actions-trigger"] {
    background-image: url(https://s.ave.zone/ico-more.svg) !important;
}
#feed-ticker,
.notification {
    background-color: #333333 !important;
}
.sidebar-left nav ul li a .badge,
.sidebar-left nav ul li label .badge {
    color: #FFFFFF;
    border: 1px solid rgba(255, 255, 255, 0.2);
}
#header {
    background: #0F0F14;
}
.room-mini .room-mini-header .room-mini-description {
    color: #A7DBD8 !important;
}
.room-mini {
    background-color: #16161D !important;
}
.easy-navigation-selected {
    background-color: #333333 !important;
}
#tabcomplete li {
    color: #fff !important;
    background-color: #3c3c3c !important;
    border-color: #4c4c4c !important;
}
#tabcomplete li.chosen {
    background-color: #000 !important;
    border-color: #000 !important;
}
.ob-user-tag {
    background-color: #0f0f14 !important;
	color: #D3D3D3 !important;
	border-color: #333 !important;
}
div#loading-message  {
    background-color: #16161D !important;
}
@media (min-width: 600px) {
    .monologue.catchup-marker {
        border-top-width: 1px !important;
    }
    #loading {
    	background: rgba(0, 0, 0, 0.7);
	}
    .user-gravatar64 > a:nth-child(1) > img:nth-child(1) {
    	width: 32px !important;
    	height: 32px !important;
    }
    .overlay {
    	background-color: rgba(0, 0, 0, 0.5);
	}
    #tabcomplete-container {
        bottom: 35px;
    }
    #present-users {
        display: none !important;
    }
    #input-area {
        /* width: auto !important; */
        height: 32px !important;
        padding: 0;
    }
    #sidebar #info {
        padding: 0 0px;
    }
    .system-message-container {
        margin-bottom: 0px;
    }
    #sidebar #widgets .sidebar-widget {
        padding: 0 0px;
        margin: 0px;
    }
    #container {
        padding: 0px 0px !important;
    }
    #sidebar {
        padding-right: 0px !important;
        width: 20% !important;
        padding: 5px 5px 0px 0px !important;
    }
    #main {
        width: 83.5%;
    }
    .message .flash {
        right: -27px;
    }
    .monologue .signature,
    .system-message-container .system-message-spacer {
        width: 5.5%;
        margin-right: 0.33%;
    }
    .ob-post .ob-post-body {
        margin: 7px 0px;
    }
    #reply-count,
    #flag-count,
    #modflag-count {
        left: 15px;
        top: -10px;
    }
    .user-gravatar64,
    #active-user > div > img,
    #input-table > tbody > tr:nth-child(1) > td:nth-child(1) {
        width: 32px !important;
        height: 32px !important;
    }
    #chat-buttons {
        width: 135px !important;
    }
    #input-table td.chat-input {
        width: calc(100% - 135px);
    }
    #input {
        width: 100% !important;
        height: 32px !important;
    }
    #chat {
        padding-bottom: 33px;
    }
    .onebox.ob-image {
        opacity: 0.02 !important;
    }
    .onebox.ob-image:hover {
        opacity: 1 !important;
    }
    .messages,
    .message .meta {
        background-color: #16161D !important;
    }
    .messages {
        width: 88% !important;
    }
    .mine .messages {
        background-color: #0f0f14 !important;
    }
    #input-table td {
        padding: 1px;
    }
    .system-message-container .system-message {
        padding-top: 1px !important;
        padding-bottom: 0px !important;
        font-size: x-small !important;
    }
}
@media (max-width: 600px) {
    .overlay {
    	background-color: rgba(0, 0, 0, 0);
	}
    .username.owner {
        font-style: italic;
    }
    #loading {
    	background: #333333 !important;
	}
    #chat .monologue .message .reply-info {
        background-image: url(https://s.ave.zone/ico-reply.svg) !important;
    }
    .monologue,
    #chat .monologue {
        background-color: #16161D;
    }
    .mine .messages,
    .monologue.mine {
        background-color: #0f0f14 !important;
    }
    body.mode-select-message main .monologue .message.current-message {
        color: #cccccc;
    }
}
.onebox {
    margin: 0px 0px;
}
.monologue,
#chat .monologue {
    margin-top: 1px !important;
    margin-bottom: 1px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}
#input-area,
#input-area textarea {
    color: #ffffff !important;
    background-color: #0F0F14 !important;
    border: 0 !important;
}
main {
    background-color: #333333 !important;
}
.action_clip {
    background-image: url('https://cdn.ave.zone/stackexchangechatsprites.png') !important;
    background-repeat: no-repeat !important;
    background-position: top left !important;
    background-position: 0 -77px !important;
    margin-right: 4px !important;
    width: 10px !important;
    height: 10px !important;
    cursor: pointer !important;
}
#chat .messages {
    -moz-border-radius: 1px !important;
    -webkit-border-radius: 1px !important;
    border-radius: 1px !important;
    padding: 0px 0px 0px 0px !important;
}
.messages,
.message .meta {
    color: #D3D3D3 !important;
    border: 0 !important;
    padding: 0 !important;
    -moz-border-radius: 1px;
    -webkit-border-radius: 1px;
    border-radius: 1px;
}
#chat .monologue {
    border-color: #273339 !important;
}
.message.pending,
.message.neworedit,
.message.posted {
    color: #E0E4CC !important;
}
.message.highlight {
    color: #333 !important;
    background-color: #e9903c !important;
}
.message .timestamp {
    background: none !important;
    background-color: #333 !important;
    color: #D3D3D3 !important;
}
div.message:hover .action-link {
    background: #505C63 !important;
}
div.message pre,
div.message code {
    background: #667985;
    color: rgba(255, 255, 255, 0.97);
    padding: 1px;
    border-radius: 1px;
}
.reply-parent,
.reply-child {
    background-color: #3e3e3e !important;
    color: #D3D3D3 !important;
}
body,
div#chat {
    background-image: none !important;
    background-color: #383838 !important;
    color: #D3D3D3 !important;
    font-size: small;
}
#main #input-area {
    background-image: none !important;
    background-color: #222 !important;
}
#main #input-area #input {
    background-color: #222 !important;
    color: #D3D3D3 !important;
}
#main #sidebar {
    background-image: none !important;
    background-color: #383838 !important;
    color: #D3D3D3 !important;
}
.onebox {
    background-color: #16161D !important;
    color: #D3D3D3 !important;
}
.onebox.ob-image,
.onebox.ob-youtube,
.mine .onebox.ob-image,
.mine .onebox.ob-youtube,
.conversation-info,
.onebox img {
    color: #D3D3D3 !important;
    background-color: inherit !important;
}
.ob-post-tag,
.tag {
    background-color: #0f0f14 !important;
    color: #D3D3D3 !important;
    border-color: #333 !important;
}
.ob-tweet .ob-status-text {
    color: #D3D3D3 !important;
}
body a,
.more,
.roomcard h3,
.usercard h3 {
    color: #A7DBD8 !important;
}
body a.signature {
    color: #D3D3D3 !important;
}
body #container .button,
.button {
    background-image: none !important;
    background: none !important;
    text-shadow: none !important;
    background-color: #444 !important;
    color: #D3D3D3 !important;
    border-color: #333333 !important;
}
.mention {
    background-color: #e9903c !important;
    color: #333;
    color: #111;
    padding: 2px;
    border-radius: 2px;
}
.msg-small,
.user-container .flair {
    color: #D3D3D3 !important;
}
.user-container .signature .username.moderator {
    color: #e9903c !important;
}
.user-container .moderator:after {
    content: " â™¦";
}
.deleted {
    color: #383838 !important;
}
#roomtitle {
    text-shadow: none !important;
}
body .monologue.mine .message:hover .action-link {
    background-color: #e9903c;
}
#chat .monologue.catchup-marker-1,
#chat .monologue.catchup-marker-2,
#chat .monologue.catchup-marker-3 {
    border-top-color: #7c8990 !important;
}
.monologue .timestamp {
    background-color: #16161D !important;
    color: #D3D3D3 !important;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.2);
}
#present-users,
ul#my-rooms,
div#starred-posts > div > ul > li {
    border-bottom: 1px dotted #666 !important;
}
.reputation-score,
#silence-note .system-message,
div.system-message-container div.system-message {
    color: #D3D3D3 !important;
}
ul#my-rooms > li.activity-0 {
    color: #e9903c !important;
}
ul#my-rooms > li.activity-1,
ul#my-rooms > li.activity-2,
ul#my-rooms > li.activity-3 {
    color: #D3D3D3 !important;
}
.vote-count-container.stars .img,
.vote-count-container.stars.user-star .img {
    background-image: url('https://cdn.ave.zone/stackexchangechatsprites.png') !important;
}
body #container a.button {
    background-color: #444;
}
.sprite {
    background-image: url('https://cdn.ave.zone/stackexchangechatsprites.png') !important;
}
.wmd-prompt-dialog {
    background-color: #333 !important;
    border: 3px solid #555 !important;
}
.wmd-mini-button.selected {
    background: #555 !important;
}
.wmd-mini-button:hover,
.wmd-mini-button.selected:hover {
    background: #777 !important;
}
#reply-count,
.reply-count {
    background: #e9903c !important;
    text-shadow: none;
}
#reply-count,
#flag-count,
#modflag-count,
.reply-count,
#annotation-count {
    border: 3px solid #333 !important;
    box-shadow: none !important;
}
}
