css:
@-moz-document domain("www.war.farm") {
body {
    background-color: #232323 !important;
    color: #ddd;
}
.text_container {
    background-color: #444;
    border-radius: 5px;
}
a,
.mailadd {
    color: #998352 !important;
}
.content {
    padding-bottom: 160px;
}
.bg-primary {
    background-color: #424242 !important;
}
.item_container,
.item_header {
    background-color: #4a4a4a !important;
    border: none !important;
    border-radius: 5px;
}
.rare {
    border: 3px solid #FFD700 !important;
}
.uncommon {
    border: 3px solid #C0C0C0 !important;
}
.common {
    border: 3px solid #CD7F32 !important;
}
.form-control {
    background-color: #323232;
    border-radius: 5px;
    color: #ddd !important;
    border: none;
}
.vaulted {
    color: #aaa !important;
    font-style: italic;
}
.form-control:focus {
    background-color: #3a3a3a;
    border: none;
    -webkit-box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.3) inset;
    -moz-box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.3) inset;
    box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.3) inset;
}
.result {
    background-color: #323232;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border: 1px solid #555555;
}
.result p {
    border: 1px dotted #555;
}
.result p:hover {
    background-color: #424242;
}
.footer {
    position: fixed;
    width: 100%;
    padding: 15px;
    bottom: 0;
}
.btn-srch {
    background-color: #796332;
    border-color: #796332;
    color: #fff;
}
.btn-srch:hover {
    background-color: #998352;
    border-color: #998352;
    color: #fff;
}
.td {
    background-color: #333;
}
.item_col_header {
    border: 1px solid #777;
}
.td.loc {
    border-right: 1px solid #777;
}
.item_location {
    border: 1px solid #777;
}
/* Scrollbar shit starts here and never ends */

::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}
::-webkit-scrollbar-button {
    width: 0px;
    height: 0px;
}
::-webkit-scrollbar-thumb {
    background: #aaa;
    border: 0px none #ffffff;
    border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover {
    background: #ffffff;
}
::-webkit-scrollbar-thumb:active {
    background: #000000;
}
::-webkit-scrollbar-track {
    background: #666666;
    border: 0px none #ffffff;
    border-radius: 0px;
}
::-webkit-scrollbar-track:hover {
    background: #666666;
}
::-webkit-scrollbar-track:active {
    background: #333333;
}
::-webkit-scrollbar-corner {
    background: transparent;
}
}
