css:
@-moz-document domain("print24.com") {
/* PRINT24 */

#CALC_productInformationText {
    color: #888;
    font-size: 10px;
}

#CALC_productInformationText p:first-of-type {
    margin-top: 330px ;
}

#CALC_product_sample_Link, #CALC_factsheet_Link {
    top: 2.3em;
}

.header_bg {
    background-color: #eee;
}

#calcContent .leftColumn, .rightColumn {
    margin-top: 35px;
}

.button347 {
    background: #43657b;
    border-radius: 3px;
    height: 40px;
    line-height: 41px;
    width: 200px;
}

.calculationLink {
    margin: .5em 0 0 .5em
;
}

.formHeadline {
    font-size: 11px;
    font-weight: normal;
    margin-bottom: 4px;
    margin-top: 4px;
    text-transform: uppercase;
}

.formLine {
    height: 60px;
}

.myOrdersHeadlines label, .myOrdersHeadlines b {
    background: #43657b;
    border-color: #43657b;
}

.orderCell, .OrderStatusCell, .DeliveryCell, .EditCell, .myOrdersHeadlines, .myOrder_content, .myOrder_content_line, .myOrdersHeadlines, .myOrder_content, .myOrder_content_line {
    border-color: #43657b;
}

.prop_header {
    border-radius: 2px;
}

a#CALC_inToShoppingCart_Btn {
    background: #43657b;
    border-radius: 20px;
    font-family: roboto;
    font-weight: bold;
    height: 40px;
    letter-spacing: 1px;
    line-height: 40px;
    text-transform: uppercase;
}

body,#farbContainer,#farbumschalthintergrund {
    background-color: #333;
}

header, #head_bereich {
    background: #eee;
}


}
