css:
@-moz-document domain("quizlet.com") {
/*-----------VARIABLES------------*/

/**** V: 5 ****/
/*
:root {
    -banner: #323f79;
}*/

/*--------------------------------*/



/*========== ORDER SENSITIVE ENTITIES ==================*/

/*Generic Cyan Text; Can change others*/
.UILink, .UILink:visited {
    color: #94c7c7;/*#2b6969;*/
}

/*Generic Buttons; Can change transparent buttons as well*/
/*Make sure to also redefine Transparent buttons defn's*/
/* afterwards */
.UIButton, .UIButton:visited {
    /*default: #3ccfcf*/
    background-color: #1b4444;
    color: #e2e2e2;
}

/*Generic Gray Text*/
.BlogNav-nav li a,
html, body {
    color: #babbad;
}


/*Pop Up Tool Tip*/
.UITooltip {
    background: #323f79;
    color: #fff;
}
/*ToolTip Beak*/
.UITooltip::after {
    background: #323f79;
}

body.no-tan {
    background: #061727 !important;
}


/*=============== BASE COLOUR ==========================*/

.UIModalHeader,
.ModeControls-modeHeader,
.SiteHeader,
.SiteFooter,
.UIDropdown--inverted, /*Footer DD menu*/
.UIDropdown,
.SetPageTerm
{
    background: #323f79;
}


/*The Yellow Hover*/
.UILink:active,
html.cursor .UILink:hover,
html.cursor .UILink:hover:visited {
    /*Teal*/
    color: #2d888e;
}
.UILinkBox-link .UILink:active,
.UILinkBox-link .UILink:focus,
.UILinkBox-link .UILink:hover {
    /*Teal*/
    border-bottom-color: #2d888e;
}



/*================== HEADER ============================*/

/*Header Text*/
.UILink--inverted, .UILink--inverted:visited {
    color: #f1f1f1;
}
/*QuizPro Button*/
.SiteHeaderChiclet, .SiteHeaderChiclet:visited {
    background-color: #207696;
    /*Text*/
    color: #f1f1f1;
}

/*==================== FOOTER ==========================*/

/*Footer button BG*/
.UIButton--inverted, .UIButton--inverted:visited {
    background-color: #323f79;
}

/*Not Sure*/
a:visited {
    color: #323f79;
}

/*================ STUDY SET EDITOR ====================*/
/*f BG*/
.CreateSetHeader {
    background: #7d7d7d;
}

.SetPage-modesBottomBackground,
.SetPageHeader,
.SetPage-headerContainer,
.has-adz .SetPage-headerWrapper,
.SetPage-menu,
.SetPageModeButton,
/*Create New Set Text BG*/
.DashboardLayout-sidebar,
.CreateSetHeader-sticky {
    background: #7d7d7d;
}

/*LeaderBoard PU BG*/
.HighscoresList {
    background-color: #0c0c0c;
}

/*Flashcard Mode Progress Bar*/
.UIProgressBar {
    background-color: rgba(0, 19, 99, 0.3);
}
.UIProgressBar-fill {
    background-color: #232935;
}
.UIModeIcon-modeIcon {
    fill: #232935;
}
/*Incorrect Progress Bar*/
.UIProgressBar--invalid.UIProgressBar {
    background-color: rgba(173, 43, 22, 0.3);
}
.UIProgressBar--invalid .UIProgressBar-fill {
    background-color: #6b2e2e;
}
/*Correct Progress Bar*/
.UIProgressBar--valid.UIProgressBar {
    background-color: rgba(80, 187, 135, 0.3);
}
.UIProgressBar--valid .UIProgressBar-fill {
    background-color: #2e5844;
}

/*MatchMode Card Border*/
.MatchModeQuestionScatterTile {
    border: 0.125rem solid #030b1b;
}

/*Leaderboard NameCard BG*/
.HighscoreRow,
/*MatchMode Card BG*/
.MatchModeQuestionScatterTile,
/*TestMode Card BG*/
.TestModePage-sections,
/*SpellMode Card BG*/
.SpellModeLayout-view:not(:empty).SpellModeLayout-view--withBackground,
/*LearnMode Card BG*/
.LearnModeMain,
/*Flashcard Mode Card BG*/
.ie11 .CardsList.CardsList--showSingle .CardsItem .CardsItemSide .CardsItemInner,
.webkit .CardsList.CardsList--showSingle .CardsItem .CardsItemSide .CardsItemInner,
/*Flashcard Mode Img BG*/
.CardsItemImage-background,
.UISwitch-label, /*Select this one button*/
.StudyableLinkBox-child, /*Studied Card*/
.Divider-label, /*Settings divider*/
.setting .box, /*Settings Card*/
/*Card BG*/
.TermContent-inner {
    background-color: #232935;
}
/*Card Input Text*/
.TermsPreview-word,
.UITextarea-textarea {
    color: #e0b82b;
}
/*Active TextBox Underline*/
.SpecialCharacterTextarea.is-focused .UITextarea-border {
    box-shadow: 0 0.25rem 0px 0px #dcb016;
}
/*Image BG*/
.UploadedImage-backgroundImage {
    background-color: #232935;
}
/*Card Image DD Box BG*/
.TermContent-context {
    background-color: #232935;
}
.TermsPreview-words,
.TermContent-context { 
    border-top: 0.125rem solid #7d4e6d;
}

.TermsPreview-word + *,
.TermsPreview-definition + *,
.TermsPreview-imageWrapper + *,
* + .TermsPreview-word,
* + .TermsPreview-definition,
* + .TermsPreview-imageWrapper,
.TermsPreview-word + .TermsPreview-word,
.TermsPreview-definition + .TermsPreview-definition,
.TermsPreview-imageWrapper + .TermsPreview-imageWrapper {
    border-left: 0.125rem solid #7d4e6d;
}



/*Image Search Text*/
.UIInput-input {
    color: #dadada;
}

/*Transparent Buttons*/
.UIButton--borderless, .UIButton--borderless:visited {
    background-color: transparent;
    color: #82a091;
}
/*Transparent Button Active*/
.ContextToggle.is-active .UIIcon {
    color: #dcb016 !important;
}

/*Page BG*/
.UIModal.is-white .UIModal-box,
.CreateSetPage-container {
    background: #061727;
}



/*Flashcard Menu*/
.SetPageModeButton {
    border-bottom: 0.3125rem solid #1b4444;
    color: #1b4444;
}

/*********************Gravity Mode**********************************/

.GravityTypingPrompt-input,
.GravityCopyTermView-input {
    background: rgba(6, 8, 17, 0.6);
    color: #b1b1b1;
}
 
/*================ YOUR STUDY SETS ====================*/
/*Created/Studied Inactive*/
/*Portrait Mode Buttons Inactive*/
.UIToggle-optionLabel {
    background-color: #4c4c4c;
}
/*Portrait Disabled Button Text*/
.UIToggle[aria-disabled="true"] .UIToggle-optionLabel {
    color: #828282;
}
/*Disabled Button Inner Border*/
.UIToggle[aria-disabled="true"] .UIToggle-option {
    border-left-color: #828282;
}
/*Disabled Button Border*/
.UIToggle[aria-disabled="true"] {
    border-color: #828282;
}
/*OptionsMenu Button BG*/
.UIToggle {
    border: 0.125rem solid #313131;
}


/*Card Bottom*/
.StudyableLinkBox-studiedActivity {
    background: #232935;
    border-top: 0.125rem solid #061727;
}
/*PU hover txt*/
.StudiedActivityStrip-status {
    color: #405156;
}

/*=================== LIST VIEWER =====================*/  

/*User DDm*/
.UIPopover {
    background: #949494;
}
.SiteHeader-userDropdownOptionSection {
    border-bottom: 1px solid #7d7d7d;
}


.ModeLayout,
/*Page BG*/   
.SetPage-termsWrapper, .SetPage-diagramWrapper {
    background: #061727;
}
/*Sort DD, Carg BG*/
.UIDropdown,
.SetPageTerm {
    background: #232935;
}
.UISwitch-label, /*Select this one button*/
.UIDropdown {
    border: 0.125rem solid #4a4a4a;
}
/*Card seperator line*/
.SetPageTerm-sideContent {
    border-right: 0.125rem solid #061727;
}
/*Card text: .right, .left*/
.SetPageTerm-definitionText,
.SetPageTerm-wordText {
    color: #b7aa69;
}


.AdminPost,
.BlogNav-nav,
.BlogFeedItem,
.AboutContainer,
.MobilePage,
/*Floating Card View*/
.OnboardingCard {
    background: #232935;
}
.UIFloatedCardBackdrop {
    background: #7d7d7d;
}
.UIFloatedCardBackdrop-bottom {
    background: #061727;
}
.UIButton[aria-disabled="true"], .UIButton[disabled] {
    background-color: #232935;
    color: #babbad;   
}
/*================== LATEST ACTIVITY ==================*/
.DashboardHeader {
    background: #7d7d7d;
}
/*Page BG*/
.DashboardLayout {
    background: #061727;
}
/*Join Class FB Card BG*/
.ClassSearchPage-facebookWrap,
/*Study set namecard BG*/
.UILinkBox {
    background-color: #232935;
}

/*======================= SETTINGS =====================*/
/*Setting card headers*/
.setting h2 {
    color: #a2a96f;
}
/*Subtext*/
.setting p {
    color: #a7a7a7;
}
.checkboxes,
.profile-photo-upload .the-label {
    background: #1b252d;
}
/*=================== BLOGS/ARTICLES ===================*/
/* Author Info Text */
.BlogSidebar h3 {
    color: #aab5b9;
}
.BlogSidebar h3, .BlogSidebar p, .BlogSidebar ul {
    color: #acb0b3;
    text-shadow: 0 1px 0 rgba(0, 0, 0, 0.8);
}
.PostPage-recentPosts li a {
    color: #acb0b3;
}
/*View All Posts Button*/
.button {
    background: #323f79;
    background-color: #323f79;
}
/*Page BG*/
body.no-tan,
body.no-tan .site, body.no-tan .page {
    background: #001629;
}

/*Blogs right sidebar*/
.BlogNav-nav li.is-active {
    background: rgba(255, 255, 255, 0.25);
}
.BlogNav-nav li:hover {
    background: rgba(255, 255, 255, 0.05);
}

/*============== NAVIGATION BAR UNDER HEADER ===========*/

/*Help section*/
section.main,
/*Flashcard Mode Sidebar*/
.ModeControls,
.ModeControls-actions,

/*Top NavBar*/
.top-nav {
    background: #7d7d7d;
}
.top-nav {
    border-bottom: 1px solid #165777;
}
.top-nav .pages li a {
    border-bottom: 5px solid #7d7d7d;
    color: #f3f3f3;
}
.top-nav .pages li.is-active a {
    border-bottom: 5px solid #323f79;
}

.top-nav .pages li a:hover {
    background: rgba(0, 0, 0, 0.25);
    border-color: rgba(0, 0, 0, 0.25);
}

/*Help Section Titles*/
a {
    color: #afb6d7;
}
.HelpCenterFeedbackPrompt {
    background: rgba(0, 0, 0, 0.25);
}



/*Sidebar*/
/*.DashboardLayout-sidebar {
    background: #74757b;
}*/

/*Sidebar - Selection HL*/
.SiteNav-menuItem--active .UIMenuItem-inner {
    background: #af9437;
}

/*Sidebar Active Item Icon*/
.SiteNav-menuItemIcon {
    color: #2e343c;
}

/*Sidebar Item Icon*/
.SiteNav-menuItem:not(.SiteNav-menuItem--active) .UILink:not(:hover) .SiteNav-menuItemIcon {
    color: #95b9c5;
    fill: #95b9c5;
}

/*Sidebar Text*/
.SiteNav-menuItemTitle {
    color: #fdfdfd;
}

/*Sidebar BLog Text*/
.SiteNav-blogSection {
    color: #15172d !important;
}

/*Sidebar "Your" Heading Label*/
.SiteNavSectionHeading {
    color: #acceda;
}

/*Sidebar "Create" Text*/
.SiteNav-createButtonWrapper .SiteNav-menuItemTitle {
    color: #6fdac6;
}


/*Search Text*/
.TermsPreview-definition {
    color: rgba(165, 171, 135, 0.6);
}
}
