@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://cod.ext") {

/* v0.7 */

/* default page style */
body, p, td, input, textarea, select {
  font-family: arial, sans-serif !important;
}

/* page header styles */
.header_back_table_first_row {
  background-image: url(http://www.google.com/hostednews/img/google-logo-hosted.gif) !important;
  height: 65px !important;
  background-repeat: no-repeat !important;
  background-position: 10px 31px !important;
}

.header_back_table_first_row:before {
  display: block !important;
  content: "gserver";
  color: #78c;
  height: 0 !important;
  padding-left: 95px;
  font: normal 23px/83px "arial";
}

.login_body form > table > tbody > tr:first-child * {
  height: 0 !important;
  padding: 0 !important;
}

.login_body .button {
  position: relative;
  left: -52px;
}

.login_body > form > table > tbody > tr+tr > td+td > p > table {
  position: relative !important;
  left: -29px !important;
}

.login_body > form > table > tbody > tr+tr > td+td > p > table > tbody > tr:first-child > .login_version {
  padding-left: 72px !important;
}

.login_body .link_default {
  position: relative !important;
  left: -11px !important;
}

.header_back_table_first_row table tbody td[valign="bottom"] {
  position: absolute !important;
  top: -2px !important;
  left: 0 !important;
  background-color: transparent !important;
  height: 24px !important;
  padding: 0 !important;
  width: auto;
  border-bottom: 1px solid #ddd;
  z-index: 1 !important;
}

.header_back_table_second_row {
  position: absolute !important;
  top: 33px !important;
  right: 0 !important;
  padding: 0 !important;
  float: right !important;
  text-align: right !important;
  width: auto !important;
}


.header_back_table_third_row {
  height: 0 !important;
  background-image: none !important;
}

.header_tab_active {
  background-image: none !important;
  background-color: transparent !important;
  padding-left: 10px !important;
  padding-right: 0 !important;
  border: none !important;
}

.header_tab_inactive {
  background-image: none !important;
  background-color: transparent !important;
  padding-left: 10px !important;
  padding-right: 0 !important;
  border: none !important;
}

.header_tab_active a {
  font-weight: 700 !important;
  color:  #000 !important;
  display: block !important;
  text-decoration: none !important;
}

.header_tab_inactive a {
  color: #20c !important;
  font-weight: 400 !Important;
}

.header_tab_active a:hover {
  font-weight: 700 !important;
  color: #20c !important;
}

.header_tab_inactive a:hover {
  color: #20c !important;
  font-weight: 400 !Important;
}

.header_tab_text, .header_tab_text:hover {
  display: block !important;
  font-size: 9pt !important;
  text-decoration: underline !important;
}

.header_page_title {
  display: none !important;
}

.header_link {
  color: #20c !important;
  padding-left: 5px !important;
  padding-right: 5px !important;
  border-right: none !important;
  font-weight: 400 !important;
  white-space: nowrap !important;
  text-decoration: underline !important;
}

.header_link:hover {
  color: #20c !important;
  text-decoration: underline !important;
}

.header_breadcrumb {
  color: #888 !important;
  text-align: right !important;
}

a.header_breadcrumb:hover {
  color: #20c !important;
  text-decoration: none !important;
}

/* table styles */

/* table style for the box component */
.box_table_section {
  background-color: #fff !important;
  background-image: none !important;
  color: #222 !important;
  font-size: 11pt !important;
  border: 0 !important;
  text-align: left !important;
  padding-left: 9px !important;
  padding-top: 5px !important;
  padding-bottom: 0 !important;
}

.box_table {
  color: #888 !important;
  border: 0 !important;
  padding-right: 8px !important;
  padding-bottom: 10px !important;
  padding-top: 0 !important;
  padding-left: 10px !important;
}

.boxtext {
  padding-left: 7px !important;
}

/* table style for the tabbed table component */
.tabbed_table {
  border-top: #ddd 1px solid !important;
  background-color: #f3f5fc !important;
}

.table_tab_inactive, .table_tab_active {
  border-right: #f2f2f2 1px outset !important;
  border-bottom: #f2f2f2 1px inset !important;
}

.table_tab_inactive {
  background-color: #e3e9ff !important;
}

.table_tab_active {
  background-color: #bcf !important;
}

.table_tab_active:hover {
  background-color: #bcf !important;
}

.table_tab_inactive:hover {
  background-color: #e3e9ff !important;
}

a.table_tab_inactive, a.table_tab_active {
  color: #222 !important;
}

/* table style for the per locale table component */
.locale_table_header {
  border-top: #ddd 1px solid !important;
  border-left: #ddd 1px solid !important;
  border-right: #ddd 1px solid !important;
  background: #ddd !important;
  color: #222 !important;
}

/* standard table color used as a background for a table */
.table_bg_color {
  background-color: #fff !important;
  border-bottom: 1px solid #ddd !important;
}

/* additional table color used to alternate table row colors */
.table_bg_color_ex {
  background-color: #fff !important;
  border-bottom: 1px solid #ddd !important;
}

/* additional table color used to indicate a clickable table row */
.table_bg_color_clickable {
  background: #e3e9ff !important;
}

/* style used for split table header */
.split_table_header {
  background: #e3e9ff !important;
}

/* pageindex color of pagination */
.pageindex_pagination_color_span {
  color: #20c !important;
}

/* paged table styles */
.paged_table_header,
.paged_table_header_sorted {
  background: #e3e9ff !important;
  text-align: left !important;
  font-weight: 400 !important;
  color: #222 !important;
  border-bottom: 1px solid #cad4e6 !important;
  border-collapse: collapse !important;
  padding-left: 2px !important;
  white-space: nowrap !important;
}

tr > .paged_table_header:first-child {
  padding-left: 10px !important;
}

.table_bg_color > td[valign="top"]:first-child,
.table_bg_color_ex > td[valign="top"]:first-child {
  padding-left: 10px !important;
}

#scrollable_segments_table table {
  border-spacing: 0 !important;
}

#scrollable_segments_table table thead tr .paged_table_header:last-child {
  background: none !important;
}

