.paged_table_header a {
  color: #222 !important;
  display: block !important;
}

.paged_table_header_sorted a {
  color: #000 !important;
  font-weight: 700 !important;
  display: inline !important;
}

.paged_table_header_sorted a:hover {
  color: #20c !important;
  display: inline !important;
  text-decoration: none !important;
}

a.paged_table_header_sorted:hover {
  text-decoration: none !important;
  color: #20c !important;
}
    
.paged_table_header_inv {
  border: none !important;
  background: none !important;
  color: #999 !important;
  text-align: left !important;
}

.paged_table_header_cell_inv {
  border: none !important;
  text-align: left !important;
}

.paged_table_header_inv:hover {
  text-decoration: none !important;
}

/* used for headers of composite columns which contain 2 or more sub-columns */
.paged_table_aggregate_header {
  background: #fff !important;
  color: #999 !important;
  text-align: left !important;
}

.paged_table_content {
  padding-left: 2px !important;
}

.paged_table_page_nums {
  color: #20c !important;
}

.paged_table_page_nums:hover {
  color: #20c !important;
  text-decoration: none !important;
}

a.paged_table_page_nums {
  color: #20c !important;
}

a.paged_table_page_nums:hover {
  color: #20c !important;
  text-decoration: none !important;
}

/* styles for search panel*/
.searchPanelCheckbox {
  color: #888 !important;
  font-size: 8pt !important;
}

.searchPanel {
  border: none !important;
  background-color: #fff !important;
  padding-top: 29px !important;
}

.searchPanel * {
  font-size: 9pt !important;
}

.searchPanelDatatypePanelVisible {
  padding-left: 5px !important;
}

.labelSearchWhat {
  color: #222 !important;
}

.labelSearchTo {
  color: #222 !important;
}

/* property tables */
html body form > table tr+tr > td[valign="top"][style="padding: 10px;"] > div[style="padding-top: 10px; padding-bottom: 10px;"]+table tr > td.prop_table_section,
.prop_table_section {
  background-color: #fff !important;
  background-image: none !important;
  color: #222 !important;
  border-bottom: #ddd 1px dotted !important;
  border-right: none !important;
  border-left: none !important;
  border-top: none !important;
  font-size: 13pt !important;
  text-align: left !important;
  display: block !important;
  margin-left: 10px !important;
  margin-bottom: 5px !important;
}

html body form > table tr+tr > td[valign="top"][style="padding: 10px;"] > div[style="padding-top: 10px; padding-bottom: 10px;"]+table tr+tr > td.prop_table_left_side {
  background: #fff !important;
  color: #222 !important;
  font-weight: 400 !important;
  display: block !important;
  padding-left: 10px !important;
}

.prop_table_left_side {
  background: #fff !important;
  color: #222 !important;
  font-weight: 400 !important;
}


.prop_table_right_side {
  background: #fff !important;
}

.login_version {
  color: #999 !important;
}

.login_body {
  background-image: none !important;
}

/*     style for search panel   */
.search_panel {
  border: 0 !important;
  background-color: #fff !important;
}

.searchPanel_border {
  background-color: #fff !important;
  border: 0 !important;
  height: 29px !important;
}

.search_tab_active {
  background: #fff !important;
  border: none !important;
  height: 28px !important;
  padding: 0 15px 0 5px !important;
}

.search_tab_inactive {
  background: #fff !important;
  padding: 0 15px 0 5px !important;
}

.search_tab_text {
  color: #222 !important;
}

.search_tab_text:hover{
  color: #20c !important;
  text-decoration: none !important;
}

.search_tab_text_inactive {
  color: #999 !important;
  font-weight: 400 !important;
}

.search_tab_text_inactive:hover {
  color: #20c !important;
  text-decoration: none !important;
}

.search_content {
  background: #fff !important;
  padding-left: 0 !important;
  vertical-align: middle !important;
}

.search_content_noright {
  background: #f5f6f9 !important;
}

#standard_row1 td table {
  padding: 0 !important;
}

#standard_row2 td table{
  padding: 0 !important;
}

/* browse ais styles */
.ais_browse_path {
  color: #222 !important;
  background-color: #f2f2f2 !important;
}

.ais_browse_path_selected {
  color: #222 !important;
  background-color:  #bcf !important;
}

/* window related styles */

/* popup dialogs headers style */
.popup_dialog_header {
  color: #222 !important;
}

/* generic styles */

/* page error */
.page_error {
  color: #af0000 !important;
}

/* page success */
.page_success {
  color: #8fd19e !important;
}

/* page instructions */
.page_instructions {
  color: #999 !important;
}

div[style="padding-top: 10px; padding-bottom: 10px;"] {
  position: absolute !important;
  right: 140px !important;
  padding-top: 0 !important;
  left: 310px !important;
  top: 5px !important;
  font-size: 8pt !important;
  white-space: nowrap !important;
  overflow: hidden !important;
  text-align: right !important;
  z-index: 0;
  width: auto !important;
}

/*     use to indicate required fileds on forms */
.required_mark {
  color: #af0000 !important;
}

/* link related styles */

/* default style to be used for all links if no other style is specified */
.link_default, link_default_bold, link_big, link_small {
  color: #20c !important;
  text-decoration: none !important;
}

/* section link on the tab menus */
.link_menu_section,
a.link_menu_section:hover {
  color: #20c !important;
  font-size: 13px !important;
  font-weight: 400 !important;
  text-decoration: underline !important;
}

/*    marina - don't know where these are used  */
.inherited {
  color: #888 !important
}

.inherited_disabled {
  color: #af0000 !important
}

/* buttons and menus */
.button,
.menu_button,
.combined_menu_button {
  background: url(data:image/gif;base64,R0lGODlhFgAWAKIAAN3d3evq6%2Ff29%2Bbm5v%2F%2F%2F%2B%2Fv7wAAAAAAACH5BAAHAP8ALAAAAAAWABYAAAM4SLrcTiLKSau9OOvNOy1gKI5kaZ5oqqJB675wLM90bQ94ru987%2F%2FAoHBI3AGOyKRyyWw6n9DoMwEAOw%3D%3D) !important;
}

.button {
  border: 1px solid #aaa !important;
  color: #222 !important;
  text-decoration: none !important;
  -moz-border-radius: 2px !important;
}

.button_in_table {
	margin-left: 10px !important;
}

div .button_in_table:hover {
  border-color: #000 !important;
}

div .button_in_table:active {
  border-color: #000 !important;
}

.button:hover {
  border: 1px solid #000 !important;
  color: #222 !important;
}

.button:active {
  border: 1px solid #000 !important;
}

a.button {
  color: #222 !important;
  font-weight: 400 !important;
  font-size: 9pt !important;
  padding: 3px 7px !important;
}

.disabled_button {
  border-color: #ddd !important;
  border-style: solid !important;
  border-width: 1px !important;
  background-color: #eee !important;
  color: #999 !important;
}

.customize_link {
  color: #20c !important;
  font-size: 9pt !important;
  vertical-align: bottom !important;
}

.toolbar_separator {
  border-color: #bcf !important;
  border-style: solid !important;
  border-width: 1px !important;
}

/* drop down menu styles */
.menu_button {
  -moz-border-radius-bottomleft: 2px !important;
  -moz-border-radius-bottomright: 2px !important;
  -moz-border-radius-topleft: 2px !important;
  -moz-border-radius-topright: 2px !important;
  border: 1px solid #aaa !important;

}

.menu_button:hover {
  border-color: #000 !important;
  border-style: solid !important;
  border-width: 1px !important;
}

.menu_button:active {
  border-color: #000 !important;
}

.combined_menu_button {
  -moz-border-radius-bottomright: 2px !important;
  -moz-border-radius-topright: 2px !important;
  -moz-border-radius-bottomleft: 0 !important;
  -moz-border-radius-topleft: 0 !important;
  border-color: #aaa !important;
  border-style: solid !important;
  border-width: 1px 1px 1px 0 !important;
}

.combined_menu_button:hover {
  border-color: #000;
}

.combined_menu_button:active {
  border-color: #000;
}

a.menu_button {
  color: #222 !important;
  font-weight: 400 !important;
  font-size: 9pt !important;
  padding: 3px 7px !important;
}

a.menu_button:hover {
  border-color: #000 !important;
  color: #000 !important;
}

td[nowrap=""] > table+div.menu_dropdown {
  left: 309px !important;
  top: 91px !important;
}

td[nowrap=""] > div+div.menu_dropdown {
  left: 675px !important;
  top:  91px !important;
}

td.menu_dropdown_border {
  background-color: #fff !important;
}

td.menu_dropdown_back {
  background-color: #fff !important;
}

a.menu_dropdown_list {
  color: #222 !important;
  border-width: 0 1px !important;
  background-color: #fff !important;
  display: block !important;
  padding-right: 0 !important;
}

a.menu_dropdown_list:hover {
  background-color: #68e !important;
  color: #fff !important;
}

td.menu_dropdown_separator {
  background-color: #fff !important;
  font-weight: 700 !important;
  color: #000 !important;
}

td.menu_submenu {
  color: #222 !important;
  background-color: #fff !important;
  padding-right: 10px !important;
}

div.menu_dropdown {
  border: 1px solid #aaa !important;
}

/* browser workbench */
.bwb_details_history_label {
  color: #888 !important;
}

.bwb_details_comment_label {
  color: #888 !important;
}

.bwb_details_error_label {
  color: #888 !important;
}

.bwb_comment_stamp {
  color: #888 !important;
}

.bwb_error_tooltip {
  background: #fff2c6 !important;
}

.bwb_details_label_ex {
  color: #888 !important;
}

.bwb_paragraph_separator {
  background: #fff !important;
}

/* tm populates segments style (guranteed match) */
.seg_guarant_tm_match {
  border-color: #8618b5 !important
