css:
@-moz-document domain("docs.google.com")
{
  .docs-explore-widget
  {
    width: 0px !important;
    height: 0px !important;
    margin: 0px !important;
  }

  .waffle-assistant-entry
  {
    width: 0px !important;
    height: 0px !important;
    padding: 0px !important;
    margin: 0px !important;
  }

  .waffle-assistant-entry-inactive
  {
    border-left: 0px !important;
  }

  #docs-branding-container
  {
    width: 52px !important;
    margin-left: 8px !important;
    margin-top: 8px !important;
    height: 52px !important;
    border-radius: 52px !important;
    background-color: /*[[accent-color]]*/ !important;
  }

  #docs-branding-container a
  {
    width: 60px !important;
  }

  #docs-drive-logo
  {
    width: 60px !important;
  }

  #docs-branding-logo
  {
    width: 52px !important;
    height: 52px !important
  }

  #docs-titlebar-container
  {
    margin-left: 60px !important;
  }

  #docs-menubar
  {
    margin-left: 72px !important;
  }

  .docs-titlebar-buttons
  {
    padding: 0 16px 0 0 !important;
  }

  .gb_ib
  {
    padding-left: 16px !important;
    padding-right: 16px !important;
  }

  #docs-toolbar-wrapper
  {
    border-top: 0px !important;
    border-bottom: 0px !important;
    box-shadow: 0px 0px 0px grey !important;
    background-image: -webkit-linear-gradient(top,#FAFAFA,#FAFAFA);
    background-color: #FAFAFA !important;
    padding-left: 76px !important;
  }

  #docs-chrome
  {
    background-color: #FAFAFA !important;
  }

  .docs-title-input
  {
    background-color: #FAFAFA !important;
  }

  .range-border
  {
    border-color: /*[[accent-color]]*/!important;
  }

  .autofill-handle
  {
    background-color: /*[[accent-color]]*/ !important;
    width: 8px !important;
    height: 8px !important;
    border: 0px !important;
    border-radius: 4px !important;
  }

  .jfk-button-action
  {
    background-color: /*[[accent-color]]*/!important;
    border: 1px solid /*[[accent-color]]*/ !important;
    background-image: -webkit-linear-gradient(top,/*[[accent-color]]*/,/*[[accent-color]]*/) !important;
  }

  .selection
  {
    background-color: #FFCDD2 !important;
  }

  .docs-squarehandleselectionbox-handle
  {
    background-color: /*[[accent-color]]*/!important;
    border: 0px !important;
    border-radius: 4px !important;
    width: 8px !important;
    height: 8px !important;
  }

  .waffle-obj-drag-component
  {
    background-color: /*[[accent-color]]*/!important;
  }

  #waffle-editorsized-bar
  {
    width: 0px !important;
    height: 0px !important;
  }

  #t-formula-bar-label
  {
    width: 0px !important;
    height: 0px !important;
  }

  .input-box
  {
    border-color: /*[[accent-color]]*/!important;
  }

  .resizer-handle
  {
    background: /*[[accent-color]]*/!important;
  }

  .docs-title-input:focus
  {
    border-color: /*[[accent-color]]*/!important;
  }

  .waffle-input-box-decorator
  {
    background-color: /*[[accent-color]]*/!important;
  }

  .kix-selection-overlay
  {
    background-color: #FFCDD2 !important;
    border-color: #FFCDD2 !important;
  }

  header .gb_td
  {
    background-color: /*[[accent-color]]*/!important;
  }

  .docs-homescreen-docs .docs-homescreen-templates-templateview-preview::after
  {
    box-shadow: inset 0px 0px 0px 2px /*[[accent-color]]*/ !important;
  }
}
