css:
@-moz-document 
  url-prefix("about:"), 
  url-prefix("about:accounts"), 
  url-prefix("about:addons"), 
  url-prefix("about:buildconfig"), 
  url-prefix("about:cache"), 
  url-prefix("about:checkerboard"), 
  url-prefix("about:config"), 
  url-prefix("about:crashes"),
  url-prefix("about:credits"),
  url-prefix("about:debugging"), 
  url-prefix("about:devtools-toolbox"),
  url-prefix("about:downloads"), 
  url-prefix("about:healthreport"), 
  url-prefix("about:home"), 
  url-prefix("about:license"), 
  url-prefix("about:logo"), 
  url-prefix("about:memory"), 
  url-prefix("about:mozilla"), 
  url-prefix("about:networking"), 
  url-prefix("about:newtab"), 
  url-prefix("about:performance"), 
  url-prefix("about:plugins"), 
  url-prefix("about:preferences"), 
  url-prefix("about:privatebrowsing"), 
  url-prefix("about:profiles"), 
  url-prefix("about:rights"), 
  url-prefix("about:robots"), 
  url-prefix("about:serviceworkers"), 
  url-prefix("about:sessionrestore"), 
  url-prefix("about:stylish-edit"), 
  url-prefix("about:support"), 
  url-prefix("about:sync-log"), 
  url-prefix("about:telemetry"), 
  url-prefix("about:url-classifier"), 
  url-prefix("about:webrtc"), 
  url-prefix("about:welcom") {
 
 /*   theme for firefox by achrafmontage */
    
  /* main background and bg color */
    html,  #contentAreaDownloadsView{background-color: #00527e !important}
    
      stack>hbox>vbox.main-content{background-color: #23a9ceb3}  
      
    body,  div#stage{background-color: #fba14a !important;text-align: center !important}
      
    /* images radius */
      body>img{border-radius: 12% !important;box-shadow: 0 0 15px #0000001a !important}
    div#aboutLogoContainer,div#aboutLogoContainer img{
      border-radius: 12% !important;
      background-color: #fedc24 !important;position: relative;left: 25%;top: -94%}
      
     /* style for like of about:license page*/ 
     div>ul li>a {color: #000 !important}
      
      #searchText{background: #625555e6 padding-box!important}
    /* adjust radius and position for about images and containers */ 
    div#aboutLogoContainer img{
      border-radius:12%!important; position:relative!important;left:0; top:0}
    
     /* adjust font size for Ul, li, a and em elements*/ 
    ul#aboutPageList, em, .description{font-size: medium;font-size: medium;font-weight: bold} 
    ul#abouts > li > a {color: gray; font-size: larger !important}
    
     /* style for li in about pages  */ 
    ul#abouts > li:nth-child(1) > a:nth-child(1){color: azure; font-size: medium}
    

    /* background of about:adons page */
    vbox#headered-views{background-color: #0596c6 !important}
    

    
    description{color: #bfbfbf !important}
      
      
     /* modify debugging page */  
      .addon-target-container {background: #efcb95;box-shadow: 0 0 11px #0000001a !important}
     
  
}
