css:
@-moz-document domain("kissanime.ru") {

	/* COLORS */

	/** DESCRIPTION : set the colors **/
	:root {
		--body-bg: #F9F8F8 !important;
		--card-bg: #F0F1F5 !important;
		--border-color: #BEBEBE !important;
		--link-color: #546A7B !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}

	/* GENERAL */

	/** DESCRIPTION : set main font and background, plus remove some elements **/
	* {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
	}

	*::-moz-focus-inner {
		border: 0;
	}

	a {
		color: #009949;
	}

	#result_box a span,
	span[style="font-weight: bold; color: #d5f406"] {
		color: #009949 !important;
		font-weight: normal !important;
	}

	body,
	body > #containerRoot {
		background: var(--body-bg) !important;
		overflow-x: hidden !important;
	}

	a,
	a:hover {
		transition: all 0.3s ease-in-out 0s !important;
	}

	a:hover {
		opacity: .7 !important;
	}

	select {
		-webkit-appearance: none;
		-moz-appearance: none;
		text-indent: 0px;
		text-overflow: ellipsis;
		white-space: nowrap;
	}

	#formVerify * {

		/*** VERIFY BOX ***/
		font-size: 14pt !important;
	}

	#adsIfrme7,
	iframe[id*="ads"],
	div[style="text-align: center;"] > div[style="display: inline-table;"],
	#divAds2,
	#divFloatLeft,
	#divFloatRight,
	.divCloseBut,
	.videoAdClose {

		/*** ADS CONTAINER ***/
		display: none !important;
	}

	#footer {

		/*** FOOTER ***/
		display: none !important;
	}

	/* HEADER */

	/** DESCRIPTION : change the elements of the header/upper part **/
	#head h1 {
		background: no-repeat url('https://i.imgur.com/3IVM7E4.jpg') center !important;
		background-size: cover !important;
		border-top: 10px solid #1ABC9C !important;
		border-bottom: 65px solid #005057 !important;
		height: 200px !important;
		width: 100% !important;
		position: absolute !important;
		top: -10px !important;
		left: 0px !important;
	}

	#head h1 a.logo {
		background: url("https://i.imgur.com/6FvGLoa.png") no-repeat scroll !important;
		background-size: auto 135px !important;
		width: 80% !important;
		padding-bottom: 10px !important;
		margin-top: 25px !important;
		margin-left: 20px !important;
		z-index: 9999 !important;
	}

	#navbar {

		/*** NAVBAR ***/
		background: transparent !important;
	}

	#navcontainer {
		display: none;
	}

	#navsubbar {

		/*** SUB-BAR ***/
		background: #005057 !important;
		padding: 20px 0px !important;
		position: absolute !important;
		margin-top: 106px !important;
	}

	#navsubbar p {
		color: transparent !important;
	}

	#navsubbar p a {
		font-size: 15pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 18pt !important;
		margin-top: -7px !important;
		max-width: 30% !important;
		overflow: hidden !important;
		text-overflow: ellipsis !important;
		white-space: nowrap !important;
	}

	#navsubbar p a:nth-of-type(1) {
		position: absolute !important;
		left: 16px !important;
	}

	#navsubbar p a:nth-of-type(2) {
		position: absolute !important;
		left: 165px !important;
	}

	#navsubbar p a:nth-of-type(3) {
		position: absolute !important;
		left: 325px !important;
	}

	#navsubbar p a:nth-of-type(4) {
		position: absolute !important;
		left: 450px !important;
	}

	#navsubbar p a:nth-of-type(5) {
		position: absolute !important;
		left: 555px !important;
	}

	#search {

		/*** SEARCHBAR ***/
		position: absolute !important;
		top: 215px !important;
		right: 30px !important;
		z-index: 999 !important;
	}

	#search input.text {
		background: #313B4B !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		font-size: 15pt !important;
		font-stretch: condensed !important;
		line-height: 30px !important;
		padding-left: 10px;
	}

	div[style="margin: 10px 48px 10px 0"] {
		width: 0px !important;
		padding: 0px !important;
		margin-top: -37px !important;
		margin-left: 380px !important;
	}

	div[style="margin: 10px 48px 10px 0"] a {
		background: no-repeat url("https://i.imgur.com/dLYlFRO.png") center !important;
		background-size: 28px auto !important;
		color: transparent !important;
		font-size: 11pt !important;
		font-weight: bold !important;
	}

	#result_box {
		background-color: var(--body-bg) !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .3) !important;
		opacity: 1;
		padding: 10px 20px !important;
		width: 88.7% !important;
		margin-top: 16px !important;
		margin-left: -56px !important;
	}

	#result_box a {
		color: var(--primary-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
		padding: 10px 0px !important;
		position: static !important;
	}

	#result_box a:hover {
		background: transparent;
	}

	.dIa,
	.bp,
	#widget_bounds,
	#plusone,
	#___plusone_0,
	#search input.button,
	a[href="http://kissanime.ru/AdvanceSearch"] img {
		display: none !important;
	}

	#topHolderBox {

		/*** ACCOUNT ***/
		background-color: transparent !important;
		color: transparent !important;
		position: fixed !important;
		top: 40px !important;
		right: -45px !important;
		text-align: left !important;
		z-index: 9999 !important;
	}

	#topHolderBox span {
		color: transparent !important;
	}

	#topHolderBox img {
		display: none !important;
	}

	#aDropDown {
		background: no-repeat url("https://i.imgur.com/JSW9JDg.jpg") center !important;
		background-color: #181818 !important;
		background-size: cover !important;
		border: none !important;
		border-radius: 50% !important;
		padding: 40px 0px 0px 40px !important;
		height: 0px !important;
		width: 0px !important;
	}

	#aDropDown small {
		display: none !important;
	}

	#menu_box {
		background: #025159 !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		position: absolute !important;
		top: -25px !important;
		margin-left: -390px !important;
	}

	#menu_box a {
		border: none !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		padding: 0px 5px 5px 5px !important;
	}

	#menu_box a:first-child {
		padding-top: 10px !important;
	}

	#menu_box a:last-child {
		padding-bottom: 10px !important;
	}

	#menu_box a:hover {
		background: transparent !important;
		opacity: .7 !important;
	}

	/* HOME */

	/** DESCRIPTION : change/remove the elements in the front page (aka home) **/
	#leftside,
	#rightside {

		/*** LEFT/RIGHT SIDE ***/
		margin-top: 200px !important;
	}

	#leftside {

		/*** LEFT SIDE ***/
		margin-left: -20px !important;
	}

	#leftside .banner {

		/*** RANDOM BOX ***/
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
		min-height: 300px !important;
		max-height: 300px !important;
		margin-bottom: 15px !important;
	}

	#leftside .banner .bigChar {
		color: var(--primary-color) !important;
		font-size: 18pt !important;
		font-stretch: condensed;
		font-weight: normal !important;
		letter-spacing: 0px !important;
		text-shadow: none !important;
	}

	.info {
		color: var(--secondary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		text-transform: lowercase !important;
	}

	#leftside .dotUnder {
		background: #71BA51 !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: #FFF !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 31pt !important;
		padding: 5px 10px 6px 10px !important;
		margin-left: 5px;
		margin-right: -7px;
	}

	#leftside #btnBookmarkManager,
	#leftside #spanBookmarkAdd,
	#leftside #spanBookmarkRemove {
		border-radius: 2px !important;
		color: #FFF !important;
		font-size: 14pt !important;
		font-stretch: condensed;
		padding: 7px 12px 8px 12px !important;
	}

	#leftside #btnBookmarkManager {
		background: #16A086 !important;
	}

	#leftside #spanBookmarkAdd {
		background: #16A086 !important;
	}

	#leftside #spanBookmarkRemove {
		background: #7E7E7E !important;
	}

	#leftside #spanBookmarkAdd #btnAddBookmark,
	#leftside #spanBookmarkRemove #btnRemoveBookmark {
		color: #FFF;
	}

	.banner p {
		color: var(--secondary-color);
		font-size: 14pt !important;
		font-stretch: condensed;
		line-height: 18pt !important;
	}

	#leftside .banner #btnReloadRandom img {
		background: no-repeat url("https://i.imgur.com/2n3rpjO.png") center !important;
		background-size: 50% auto !important;
		border: none !important;
		box-shadow: none !important;
		height: 0px !important;
		width: 0px !important;
		padding: 30px 0px 0px 70px !important;
		margin-top: 85px !important;
		margin-left: 24px !important;
		filter: brightness(0) var(--invert) !important;
		opacity: .5;
	}

	#leftside .banner #btnReloadRandomMain img {
		background: no-repeat url("https://i.imgur.com/2n3rpjO.png") center !important;
		background-size: 50% auto !important;
		border: none !important;
		box-shadow: none !important;
		height: 0px !important;
		width: 0px !important;
		padding: 30px 0px 0px 70px !important;
		margin-top: -3px;
		margin-left: 12px !important;
		filter: brightness(0) var(--invert) !important;
		opacity: .6;
	}

	#divShowGenre {
		top: 260px !important;
		left: 540px !important;
	}

	#divShowGenre select {
		background: var(--body-bg);
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: var(--primary-color);
		font-size: 14pt !important;
		font-stretch: condensed;
		margin-left: 30px;
		margin-right: -30px;
	}

	.details a img,
	div[style="text-align: center"] img,
	.scrollable .items a img,
	#subcontent div a img,
	.tooltip img {
		border: 1px solid var(--border-color) !important;
		padding: 0px !important;
	}

	#leftside .bigBarContainer {

		/*** LATEST UPDATE BOX ***/
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
	}

	.barTitle {
		background: transparent !important;
		border-radius: 2px !important;
		padding: 9px 13px 7px 10px !important;
		margin-bottom: 8px !important;
	}

	.barTitle .scrollable_title {
		color: var(--primary-color);
		font-size: 18pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
		text-shadow: none !important;
	}

	a.prev {
		background: url("https://i.imgur.com/tKOgREQ.png") !important;
		background-size: 30px auto !important;
		background-position: center center !important;
		margin-top: 5px !important;
		filter: brightness(0) var(--invert) !important;
		opacity: .5;
	}

	a.next {
		background: url("https://i.imgur.com/d5zq2fu.png") !important;
		background-size: 30px auto !important;
		background-position: center center !important;
		margin-top: 5px !important;
		margin-left: -8px !important;
		filter: brightness(0) var(--invert) !important;
		opacity: .5;
	}

	.navi {
		font-size: 16pt !important;
		font-weight: normal !important;
		text-transform: lowercase;
	}

	.arrow-general {
		display: none !important;
	}

	.barContent {
		background: transparent !important;
		color: var(--primary-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
		line-height: 20pt !important;
	}

	.scrollable {
		height: 230px !important;
	}

	.scrollable .items a {
		color: #009949 !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 18pt !important;
	}

	.scrollable .items a img {
		margin-bottom: 5px !important;
	}

	#tabmenucontainer {

		/*** ANIMELIST BOX ***/
		background: #444 !important;
		width: 100% !important;
	}

	#submenu {
		margin-top: -8px !important;
	}

	#tabmenucontainer li a {
		background: #174C4F !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 10px 30px 10px 28px !important;
		text-align: left !important;
		text-transform: uppercase !important;
		white-space: nowrap !important;
	}

	#tabmenucontainer li a.tabactive {
		background: var(--card-bg) !important;
		color: var(--secondary-color);
	}

	#leftside #subcontent {
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px;
		border-top-left-radius: 1px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
	}

	#subcontent div div {
		background: var(--card-bg) !important;
	}

	#subcontent div div a,
	#subcontent div div a span {
		color: #009949;
	}

	#subcontent p a {
		font-size: 14pt !important;
		font-stretch: condensed !important;
	}

	#subcontent span.title {
		color: var(--primary-color) !important;
		font-size: 17pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
	}

	img[src*="newupdate"] {
		background: no-repeat url("https://i.imgur.com/xUijfgF.png") left !important;
		background-size: cover !important;
		height: 0px !important;
		width: 0px !important;
		padding: 20px 0px 0px 20px !important;
		margin-bottom: 1px;
		margin-left: -7px;
	}

	img[src*="plus.png"],
	img[src*="minus.png"],
	img[src*="Content/images/numbers"] {
		display: none !important;
	}

	div[style="text-align: right; font-size: 16px"] {
		text-align: center !important;
	}

	div[style="text-align: right; font-size: 16px"] a[href="/AnimeList/NewAndHot"] {
		background: #60646D !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-stretch: condensed !important;
		padding: 8px 18px !important;
		text-transform: uppercase !important;
	}

	div[style="text-align: right; font-size: 14px; padding-top: 5px"] a[href="/Status/Ongoing/LatestUpdate"],
	div[style="text-align: right; font-size: 14px; padding-top: 5px"] a[href="/AnimeList/LatestUpdate"] {
		background: #557780 !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-stretch: condensed !important;
		font-style: normal !important;
		font-weight: bold !important;
		padding: 8px 18px !important;
		text-transform: uppercase !important;
		margin-right: -10px !important;
	}

	#rightside .rightBox {

		/*** RIGHT SIDE BOXES ***/
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 10px !important;
		margin-bottom: 12px !important;
	}

	#rightside .rightBox .barTitle {
		color: var(--primary-color);
		font-size: 18pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

	div[style="margin-top: -5px; font-weight:bold;"] {
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 18pt !important;
	}

	#rightside .rightBox:nth-child(7) {
		display: none;
	}

	#rightside .rightBox .barContent a,
	#rightside .rightBox .barContent a b {
		color: var(--link-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-style: italic;
		font-weight: normal;
	}

	img[src*="bullet.png"] {
		background: no-repeat url("http://icon-park.com/imagefiles/link_icon_blue.png") center !important;
		background-size: cover !important;
		height: 0px !important;
		width: 0px !important;
		padding: 15px 0px 0px 15px !important;
		filter: brightness(0) var(--invert) !important;
		margin-top: 5px !important;
		margin-right: 3px !important;
	}

	/* SEARCH ENGINE */

	/** DESCRIPTION : change the elements in the search page **/
	.listing {
		margin-top: -40px !important;
	}

	#leftside .barTitle {
		color: var(--primary-color);
		font-size: 18pt;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

	.listing .head {
		visibility: hidden !important;
	}

	.listing td {
		background: var(--card-bg) !important;
		color: var(--primary-color);
		z-index: 9999 !important;
	}

	.listing td a {
		color: var(--primary-color);
		font-style: italic;
		font-weight: normal !important;
	}

	.listing td a:hover,
	.episodeVisited {
		color: #2CC990 !important;
	}

	.listing th {
		color: var(--primary-color);
		font-stretch: condensed !important;
		font-weight: normal;
	}

	.tooltip {
		background: var(--body-bg) !important;
		border-radius: 2px;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 30px 30px 80px 30px !important;
	}

	.tooltip .bigChar {
		font-size: 18pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

	.tooltip p {
		color: var(--secondary-color);
		font-size: 14pt !important;
		font-stretch: condensed;
		line-height: 18pt !important;
		max-height: 100% !important;
		overflow: hidden !important;
		text-overflow: ellipsis !important;
	}

	.tooltip div {
		margin-left: 15px !important;
	}

	#formProfile span[style="font-size: 15px; font-weight: bold"],
	.barContent span[style="font-size: 15px; font-weight: bold"] {
		font-size: 15pt !important;
		font-weight: normal !important;
	}

	#animeName,
	#status,
	#password,
	#username,
	#email,
	#retypePassword {
		background: var(--body-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: var(--primary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		padding: 5px 6px !important;
	}

	.lblAdvanceSearch {
		color: var(--primary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
	}

	#formAdvanceSearch #genres li {
		margin-bottom: 10px !important;
	}

	#formAdvanceSearch a {
		background: transparent !important;
		font-size: 14pt !important;
		font-stretch: condensed;
		white-space: nowrap;
	}

	#btnReset,
	#btnSubmit {
		margin-top: -20px !important;
	}

	/* ANIME LIST */

	/** DESCRIPTION : change the elements of the anime list page **/
	.alphabet {
		background: transparent !important;
		color: transparent !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		text-align: left !important;
		margin-top: -10px !important;
		margin-bottom: 10px !important;
	}

	.alphabet a {
		font-weight: bold;
		margin-right: -8px !important;
	}

	img[title="Latest update"],
	img[title="Popular anime"] {
		display: none !important;
	}

	div.pagination ul.pager li {
		background: transparent !important;
		border: none !important;
		color: var(--primary-color) !important;
		font-weight: bold !important;
	}

	div.pagination ul.pager li,
	div.pagination ul.pager li a {
		color: var(--primary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed;
		font-weight: normal;
	}

	div.pagination ul.pager li a:hover {
		border: none !important;
		background: transparent !important;
		margin-top: 0px !important;
		margin-left: 0px !important;
	}

	/* ANIME DETAIL */

	/** DESCRIPTION : change the elements of the anime details page **/
	#leftside .bigChar {
		color: var(--primary-color) !important;
		font-size: 17pt !important;
		font-stretch: condensed;
		letter-spacing: 0px !important;
