css:
@-moz-document domain('www.youtube.com'), url-prefix('https://apis.google.com')
{ 

:root
{
    --accent  : /*[[colors]]*/;
    --bg      : #2d2d2d;
    --bgdark  : #232323;
    --borders : #424242;
    --txt     : #a3a3a3;
    --txtdark : #767676; 
    --txtlight: #cecece;
    --bginput : rgb(200,200,200);
} 


/**************************/
/*****  -000-General  *****/
/**************************/
/*TODO: red loading bar at homepage when switching pages */
/*TODO: this is such a mess, clean it up! */ 


.compact-shelf .yt-uix-button-shelf-slider-pager
{
    background-color: var(--accent) !important;
    border-color: var(--borders) !important;
}


.feed-item-container
{
    border-color: var(--borders) !important;
}


/* head navigation menu - shows on multiple pages */
#channel-navigation-menu .epic-nav-item-heading
{
    border-color: var(--accent) !important;
    color: var(--accent) !important;
}
#channel-navigation-menu .yt-uix-button-epic-nav-item:hover
{
     border-color: var(--accent) !important;
}


/* category of videos collection header */
.branded-page-module-title-text, .branded-page-module-title-text span
{
    color: var(--txtlight) !important;
}
.branded-page-module-title a.branded-page-module-title-link:hover .branded-page-module-title-text, .branded-page-module-title-link:hover .branded-page-module-title-text span
{
    color: var(--accent) !important;
}


/* buttons - not interested (3 dots when hovering over thumbnail) */
.yt-uix-button-action-menu, .yt-uix-button-lockup-action-menu
{
	filter: invert(100%) !important;
}
.yt-uix-button-action-menu:hover, .yt-uix-button-lockup-action-menu:hover
{
	filter: brightness(200%) !important;
}


/* buttons - dismiss (cross) */
.yt-uix-button-icon-dismissal
{
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-hitchhiker-vflXBOoAR.webp) -512px -88px !important;
    background-size: auto !important;    
    width: 10px !important;
    height: 10px !important;
    filter: invert(100%) !important;
}


/* icons - play (triangle) */
.play-all-icon-btn:before
{
	filter: invert(100%) !important;
}
/* icons - rollout (little downard arrow) */
.yt-uix-button-arrow
{
    filter: invert(100%) brightness(60%) !important;
}
/* icons - verified channel (little checkmark) */
.yt-channel-title-icon-verified:hover
{
	background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-hitchhiker-vflXBOoAR.webp) 0 0 !important;
    filter: brightness(150%) !important;
}







/* hypertext links */
a
{
	background-color: transparent !important;
	color: var(--accent) !important;
}
/* meta data */
.g-hovercard, .shelf-annotation, .yt-lockup-meta 
{
	color: var(--txtdark) !important;
}
/* seperator */
hr
{
	border-color: var(--borders) !important;
    color: var(--txt) !important;    
}
/* containers semi-transparent */
div, span, h1, h2, p
{
	color: var(--txt) !important;
}
/*background that dims the screen for a popup window */
.yt-dialog-base
{    
    background-color: rgba(0,0,0,0.8) !important;
}
/* containers - common containers */
.branded-page-v2-primary-col .yt-card, .branded-page-v2-header, .branded-page-related-channels
{    
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
    box-shadow: 0 0 0 1px var(--borders) !important;
}
.branded-page-module-title
{
    color: var(--txtlight) !important;
}
/* container - darker background */
#body-container
{
    background-color: var(--bgdark) !important;
    border-color: var(--borders) !important;
}
/* badge - live, new, 4k, channel, cc */
.yt-badge
{
    background-color: transparent !important;
    color: var(--accent) !important;
    border-color: var(--accent) !important;   
}
/* badge - promising author */
.shelf-featured-badge
{
    background-color: var(--accent) !important;
    color: black !important;
    border-color: var(--borders) !important;
}
/* badge - collection ??? */
.standalone-collection-badge-renderer-icon
{
    background-color: var(--accent) !important;
    color: black !important;
    border-color: var(--borders) !important;
}
/* badge - money */
.standalone-ypc-badge-renderer-icon-available
{
    background-color: var(--accent) !important;
    color: black !important;
    border-color: var(--borders) !important;
}
::-webkit-progress-bar
{
    background-color: blue !important;
    color: orange !important;
}
/* scrollbar */
::-webkit-scrollbar
{
    width: 10px !important;
    height: 10px !important;
    background: var(--bg) !important;
}
::-webkit-scrollbar-thumb
{
    background: var(--accent) !important;
    border: none !important;
    box-shadow: inset 0 0 2px 0 rgba(0,0,0,0.8) !important;
}
::-webkit-scrollbar-thumb:hover 
{
    box-shadow: inset 0 0 100px 0 rgba(0,0,0,0.5) !important;
}
::-webkit-scrollbar-track
{
    background: var(--bg) !important;
    box-shadow: inset 0 0 2px 0 rgba(0,0,0,0.8) !important;
}
:focus
{
    outline-color: var(--accent) !important;
}
::selection
{
    color: black !important;
    background-color: var(--accent) !important;
}
input::selection
{
    color: black !important;
    background-color: var(--accent) !important;
}
textarea::selection
{
    color: black !important;
    background-color: var(--accent) !important;
}

/* button - base */
.yt-ui-button
{
    box-shadow: none !important;
}
/* button - default */
.yt-uix-button-default
{	
	background-color: rgba(255,255,255,0.1) !important;
    border-color: var(--borders) !important;
    box-shadow: none !important;
}
.yt-uix-button-default:hover
{	
	background-color: rgba(255,255,255,0.15) !important;
    border-color: var(--borders) !important;
}
/* button - disabled */
.yt-uix-button[disabled]
{
	opacity: 0.8 !important;
}
.yt-uix-button-primary
{	
	background-color: rgba(255,255,255,0.1) !important;
    border-color: var(--borders) !important;
    box-shadow: none !important;
}
.yt-uix-button-primary:hover
{	
	background-color: rgba(255,255,255,0.15) !important;
    border-color: var(--borders) !important;
}
/* button - subscribed */
.yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-branded
{
	background-color: var(--accent) !important;
    border-color: var(--borders) !important;  
    opacity: 0.7 !important;
}
/* button - subscribe */
.yt-uix-button-subscribe-branded, .yt-uix-button-subscribe-unbranded
{
	background-color: var(--accent) !important;
    border-color: var(--borders) !important;    
}
/* button - subscribe - text */
.subscribe-label, .subscribed-label, .unsubscribe-label
{
	color: black !important;
}
/* button - subscribe - counter label */
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal
{
	background-color: transparent !important;
    border-color: var(--borders) !important;    
}
/* button - subscribe - icons - triangle */
.yt-uix-button-subscribe-branded:before
{
    filter: invert(100%) grayscale(100%) brightness(80%) !important;
    opacity: 0.75 !important;
}
/* button - subscribe - icons - checkmark */
.yt-uix-button-subscribed-branded:before
{
	filter: invert(100%) brightness(50%) !important;
}
/* button - subscribe - icons - cross */
.yt-uix-button-subscribed-branded.hover-enabled:hover:before
{
    filter: brightness(50%) !important;
}
/* alert bar */
.yt-alert-info
{
    background-color: var(--bgdark) !important;
    border-color: var(--borders) !important;
    border-style: solid !important;
    border-width: 1px !important;
    box-shadow: inset 0px 0px 4px 1px var(--accent) !important;
}
/* alert bar - inner containers */
.yt-alert-content, #alerts .yt-alert-icon, #alerts .yt-alert-buttons
{
    background-color: transparent !important;
}
/* panel below the bar */
.yt-alert-panel 
{
     background-color: var(--bg) !important;
}
/* alert bar - sides */
.alerts-wrapper
{
    background-color: transparent !important;
}
/* alert bar - icon (asterisk) */
.yt-alert-default.yt-alert-info .icon
{
    filter: invert(50%) brightness(70%) !important;
}
/* alert bar - button - close */
.yt-alert .close
{
    filter: invert(50%) !important;    
}
.yt-alert .close:hover
{
    background-color: transparent !important;
    filter: brightness(70%) !important;
}
/*thumbnail container */
.yt-thumb
{
    background-color: transparent !important;
}
/* thumbnail - playprogress */
.resume-playback-background
{
	background-color: rgba(255,255,255,0.5) !important;
}
.resume-playback-progress-bar
{	
	background-color: var(--accent) !important;
}
/* thumbnail - playlist : hover - overlay that says "play all" */
.yt-pl-thumb-overlay, .pl-header-play-all-overlay
{
	background: rgba(0,0,0,0.5) !important;
}
/* thumbnail - playlist : hover - overlay that says "play all" , at "watch later"-page */
.pl-header-play-all-overlay
{
    background: rgba(0,0,0,0.5) !important;
    color: var(--txtlight) !important;
}
/* thumbnail - playlist - sidepanel */
.sidebar
{
	background-color: rgba(0,0,0,0.7) !important;
}
/* thumbnail - playlist - sidepanel - text */
.sidebar > span > span
{
	color: white !important;
}
.yt-pl-thumb-overlay .play-icon
{
    filter: invert(50%) brightness(100%) !important;    
}
/* thumnail - button - watch later */
.addto-watch-later-button
{
    background-color: rgba(0,0,0,0.5) !important;
}
.addto-watch-later-button:hover
{
    background-color: rgba(0,0,0,0.25) !important;
}
.addto-watch-later-button:before
{
    filter: invert(100%) !important;
}
/* thumbnail - dutation label */
.video-time
{
    background-color: rgba(0,0,0,0.5) !important;    
}
/* authorcard - when hovering over a channel name there is a card that shows up */
.nbc
{
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
}
/* authorcard - badges */
.tpd
{
    border-color: var(--accent) !important;
    color: var(--accent) !important;
}
/* appbar - small bar at topleft shows up when you like or dislike a video */
.appbar-guide-notification 
{
    background-color: var(--accent) !important;
}
.appbar-guide-notification .appbar-guide-notification-text-content
{
    color: black !important;
}
.appbar-guide-notification-icon
{
    filter: invert(100%) !important;
}



/***********************/
/*****  -000-Mast  *****/
/***********************/



/* containers */
#yt-masthead-container
{
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
}
/* buttons - options (this first neutralizes a hover effect and then adds my own */
#masthead-positioner:hover .yt-uix-button-icon-appbar-guide
{
	background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-hitchhiker-vflXBOoAR.webp) -322px -168px !important;
}
#yt-masthead #appbar-guide-button .yt-uix-button-icon-appbar-guide
{
    opacity: 0.7 !important;
}
#yt-masthead #appbar-guide-button:hover .yt-uix-button-icon-appbar-guide
{
    opacity: 1 !important;
}
/* buttons - home (utube logo) */
#yt-masthead #logo-container .logo
{
	filter: grayscale(100%) invert(100%)!important;
}
/* buttons - search */
#yt-masthead #masthead-search .search-btn-component .yt-uix-button-content
{
	filter: invert(100%) brightness(200%) !important;
    opacity: 0.5 !important;
}
#yt-masthead #masthead-search .search-btn-component:hover .yt-uix-button-content
{
	opacity: 0.7 !important;
}
/* buttons - upload */
#yt-masthead .yt-uix-button-icon-material-upload
{
	filter: invert(100%) !important;
    opacity: 0.9 !important;
}
#yt-masthead .yt-uix-button-opacity:hover
{
	opacity: 0.8 !important;
}
/* buttons - notifications */
#yt-masthead #yt-masthead-notifications-button
{
	background: none !important;
}
#yt-masthead #yt-masthead-user .yt-uix-button-icon-bell
{
	filter: invert(100%) !important;
    opacity: 0.5 !important;
}
#yt-masthead #yt-masthead-notifications-button:hover .yt-uix-button-icon-bell
{
	opacity: 0.7 !important;
}
/* buttons - accountlogo */
#yt-masthead #yt-masthead-account-picker > button:hover
{
    box-shadow: 0 0 1px 2px rgba(177,177,177,1) !important;
}
/* searchbar */
#yt-masthead #masthead-search-terms
{
    background-color: var(--bginput) !important;
    border: 1px solid rgba(0,0,0,0.9) !important;
    box-shadow: inset 0 1px 2px rgba(0,0,0,0.9) !important;
}
#yt-masthead  #masthead-search-term
{
    color: black !important;
}
/* searchbar - button */
#yt-masthead  #search-btn
{
	background-color: transparent !important;
    border-color: var(--borders) !important;
}
/* searchbar - icons - keyboard */
#yt-masthead .gsst_b
{
    background-color: transparent !important;
}



/*****************************/
/*****  -000-Navigation  *****/
/*****************************/



/* containers */
#appbar-guide-menu
{
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
}
/* text */
#appbar-guide-menu .yt-valign-container
{
    color: var(--txtdark) !important;
}
#guide-container a
{
	color: var(--accent) !important;
}
/* buttons - current page */
#appbar-guide-menu .guide-item.guide-item-selected
{
    background-color: var(--accent) !important;
}
#appbar-guide-menu .guide-item.guide-item-selected:hover
{
    background-color: var(--accent) !important;
}
#appbar-guide-menu .guide-item.guide-item-selected .display-name span
{
    color: black !important;    
    text-shadow: none; 
}
/* buttons - current page - icon */
#appbar-guide-menu .guide-item-selected .guide-what-to-watch-icon
{
    filter: invert(50%) brightness(70%) !important;
}
/* buttons - utube red */
#appbar-guide-menu .guide-item .guide-video-youtube-red-icon, .guide-item .guide-unplugged-icon
{
	filter: grayscale(100%) brightness(200%) !important;
}
/* buttons - subscriptions options (looks like 3 dots) */
#appbar-guide-menu .yt-uix-button-icon-hover-action-menu
{
	filter: invert(100%) !important;
}
#appbar-guide-menu .yt-uix-button-icon-hover-action-menu:hover
{
	filter: brightness(200%) !important;
}
/* items in the list */
#appbar-guide-menu .guide-item:hover
{
	background-color: rgba(255,255,255,0.1) !important;
}



/*************************/
/*****  -000-Footer  *****/
/*************************/



/* containers */
#footer-container, #footer-container div
{
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
}
/* borders */
#footer .safety-submit
{
    border-color: var(--borders) !important;
}
/* text */
#footer .yt
{
    color: var(--txt) !important;
}
#footer .yt-notes
{
    color: var(--txtdark) !important;
}
#footer .yt-picker-item, #footer .yt-picker-content strong
{
    color: var(--accent) !important;
}
/* buttons - home (utube logo) */
#footer #footer-logo .footer-logo-icon
{
	filter: grayscale(100%) invert(100%)!important;
}
/* buttons - language */
#footer .yt-uix-button-icon-footer-language
{
    filter: invert(100%) brightness(60%) !important;
}
/* buttons - history */
#footer .yt-uix-button-icon-footer-history
{
	filter: invert(100%) brightness(60%) !important;
}
/* buttons - help */
#footer .yt-uix-button-icon-questionmark
{
	filter: invert(100%) brightness(60%) !important;
}



/**************************************************/
/*****  -000-Home - Trending - Subscriptions  *****/
/**************************************************/



/* containers */
#masthead-appbar, #content .yt-ui-ellipsis, #content .yt-ui-ellipsis-nowrap-single-line
{
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
}
/* buttons - effect not interested (cross shape)(removes preset effect) */
#feed .feed-item-container:hover .yt-uix-button-icon-dismissal
{
	opacity: 0.5 !important;
}
/* thumbnail : hover - color the channel name */
#feed .yt-lockup:hover .g-hovercard
{
	color: var(--accent) !important;
}
/* buttons - grid and list (found in subscriptions) */
#appbar-nav .yt-uix-button-icon-view-module, #appbar-nav .yt-uix-button-icon-view-list
{
    filter: invert(100%) brightness(50%) !important;
}
/* top navigation */
#appbar-nav .yt-uix-button-epic-nav-item:hover
{
    border-color: var(--accent) !important;
}
#appbar-nav .epic-nav-item-heading
{
    color: var(--accent) !important;
    border-color: var(--accent) !important;
}



/*****************************/
/*****  -000-My Channel  *****/
/*****************************/



/* containers */
.secondary-header-contents
{
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;
}
/* borders */
#c4-header-bg-container
{
    border-color: var(--borders) !important;
}
/* buttons - channeldescription (plus sign) */
.c4-editor-plus:before
{
    filter: invert(100%) brightness(60%) !important;
}
/* buttons - options (gear icon) */
.casual-settings-button
{
    background-color: transparent !important;
    box-shadow: inset 0 0 0 1px var(--borders) !important;
}
.yt-uix-button-icon-settings-material
{
     filter: invert(100%) brightness(70%) !important;
}
/* buttons - video management (located on top) */
.header-action-icon.vm-icon
{
    filter: brightness(200%) !important;
}
/* channelsettings - headers */
.channel-settings-dialog-fg h3
{
    color: var(--txtlight) !important;
}
/* channelsettings - background */
.channel-settings-dialog-fg
{    
    background-color: var(--bg) !important;
    border-color: var(--borders) !important;    
}
/* channelsettings - background - footer */
.yt-uix-overlay-actions
{
    background-color: var(--bgdark) !important;
    border-color: white !important;
}
/* channelsettings - buttons - slider background */
.yt-uix-form-input-paper-toggle-bar
{
    background-color: rgba(255,255,255,0.5) !important;
}
/* channelsettings - buttons - slider ball */
.yt-uix-form-input-paper-toggle-button
{
    background-color: var(--accent) !important;
}



/**************************/
/*****  -000-Library  *****/
/**************************/



/* buttons - back */
.yt-uix-button-icon-channel-back
{
    filter: invert(100%) !important;
}
/* buttons - add new playlist */
.add-new-pl-btn .create-playlist-widget-button, .add-new-pl-btn:before
{
    filter: invert(100%) !important;
}
/* buttons - latest video added - rollout */
.yt-uix-button-menu
{
    background-color: var(--bg) !important;
	border-color: var(--borders) !important;
}
/* icons - private */
.video-private-icon
{
    filter: invert(100%) grayscale(100%) !important;
}
/* icons - unlisted */
.video-unlisted-icon
{
    filter: invert(100%) grayscale(100%) brightness(130%) !important;
}
/* buttons - create playlist*/
.create-playlist-widget-button .add-new-pl-btn:before
{
    filter: invert(100%) brightness(60%) !important;
}
/* buttons - create playlist - rollout - containers */
.create-playlist-widget-form .create-playlist-bottom-section, .yt-uix-clickcard-card-border
{
    background-color: var(--bg) !important;    
}
/* buttons - create playlist - rollout - borders */
.yt-uix-clickcard-card-border
{
    border-color: var(--borders) !important;
}
/* buttons - create playlist - rollout - buttons - privacy settings */
.create-playlist-widget-form .privacy-button
{
    background-color: rgba(255,255,255,0.1) !important;
    border-color: var(--borders) !important;
    box-shadow: none !important;
}
.create-playlist-widget-form .privacy-button:hover
{
	background-color: rgba(255,255,255,0.15) !important;
}
/* buttons - create playlist - rollout - buttons - create[disaabled] */
.create-playlist-widget-form .create-button[disabled]
{
    opacity: 0.5 !important;
}
/* buttons - create playlist - rollout - icons - all icons in privacy settings rollout */
.create-playlist-widget-form .yt-uix-button-has-icon:before
{
    filter: invert(100%) brightness(60%) !important;
}


/**************************/
/*****  -000-History  *****/
/**************************/



/* TODO: after clicking a button at top navigation there is a blue outline visible */
/* borders */
.feed-header, .yt-lockup, .branded-page-v2-subnav-container
{
	border-color: var(--borders) !important;
}
/* text */
.comment-history-entry-renderer-content, .content-expander-content
{
    color: var(--txtlight) !important;
}
/* searchbar */
.history-search-field
{
    background-color: var(--bginput) !important;
    border: 1px solid rgba(0,0,0,0.9) !important;
    box-shadow: inset 0 1px 2px rgba(0,0,0,0.9) !important;
}
/* searchbar - buttons - search */
.history-search-button, .history-search-button:hover
{
    background-color: transparent !important;
}
.yt-uix-button-icon-search
{
    filter: invert(100%) brightness(120%) !important;
}



/***********************************************/
/*****  -000-Watch Later / favorites / etc *****/
/***********************************************/


/*TODO: A lot! */
/* borders */
#pl-header
{
    border-color: var(--borders) !important;
}
/* buttons - settings for playlists */
#pl-header .yt-uix-button-icon-gear
{
     filter: invert(100%) !important;
}
.yt-uix-dragdrop-draggable-item
{
    border-color: var(--borders) !important;
}
.pl-video:hover .yt-uix-dragdrop-drag-handle
{
    background-color: var(--accent) !important;
    border-color: var(--borders) !important;
}
.yt-uix-dragdrop-dragged-item 
{
    background: blue !important;
    box-shadow: 10px 10px 10px 10px orange !important;
}
/* icons - pricay settings (globe / locked / unlocked) */
