css:
@-moz-document domain("animefillerlist.com") {
/*! AnimeFillerList Dark - XHS v1.0.4 (2019-05-20)
/*! Repository: https://github.com/Soitora/xhs-styles/
/*! License:	https://creativecommons.org/licenses/by-sa/4.0/
*/

/* Variables */

:root {
	--xhs-primary: #1A1A1D;
	--xhs-secondary: #1E1E21;
	--xhs-border: #17171A;
	--xhs-highlight: #232326;
	--xhs-title: #C8C8C8;
	--xhs-body: #AAAAAA;
	--xhs-orange: #F47447;
	--xhs-blue: #3779A1;
	--xhs-green: #648E76;
	--xhs-purple: #8E78A2;
	--xhs-brown: #7A6A5A;
	--xhs-red: #EF4A4B;
}

/*! Main Code !*/

body {
	background: #2c2f33;
	color: #7289da;
}

h1,
h2,
h3,
h4 {
	color: #99aab5;
}

table.EpisodeList th.header {
	background: #23272a url(http://www.animefillerlist.com/sites/all/themes/afl/i/arrows.png) center right no-repeat;
}

table.EpisodeList tr.filler {
	background: #23272a;
	color: #a14a40;
}

.Bubbles {
	display: none;
}

.Filler {
	display: none;
}

#Footer {
	margin: 0 auto;
	padding: 0 0 30px 0;
	width: 960px;
}

table.EpisodeList td,
table.EpisodeList th {
	border-right: 0px solid #CFCFCF;
	border-bottom: 0px solid #CFCFCF;
	text-align: center;
	padding: 5px 10px;
}

table.EpisodeList th {
	background-color: #dedede;
	text-shadow: 0 0;
	text-align: left;
	padding: 5px 25px 5px 10px;
}

#Condensed .Episodes>a {
	color: #7289da;
	white-space: nowrap;
}

#Condensed .filler .Episodes {
	color: #a14a40;
}

#Condensed .filler {
	background: #23272a;
}

#Condensed .canon {
	background: #23272a;
}

#Condensed {
	color: #7289da;
	border: 0px solid #CFCFCF;
}

#Condensed .filler .Label {
	color: #a14a40;
	border-top: 0px solid #CFCFCF;
}

#Condensed .Label {
	border-bottom: 0px solid #CFCFCF;
	background-color: #23272a;
	display: block;
	font-weight: bold;
	text-shadow: 0 0;
	overflow: hidden;
	padding: 5px 10px;
}

table.EpisodeList {
	border-top: 0px solid #CFCFCF;
}

table.EpisodeList {
	border-left: 0px solid #CFCFCF;
}

tbody {
	border-top: 0px solid #ccc;
}

#TitleToggle {
	color: #a14a40;
}

#Content>.Right h2 {
	background: #23272a;
	border-bottom: #dedede 0px solid;
	color: #99aab5;
	font-family: "HelveticaNeue", "Helvetica Neue", Helvetica, Arial, sans-serif;
	font-size: 12px;
	margin-bottom: 10px;
	padding: 10px 15px;
	text-shadow: 0 0;
	text-transform: uppercase;
}

#Content>.Right .block {
	border: #dedede 0px solid;
}

#Filters a {
	background: #23272a;
	border: #efeae7 0px solid;
	color: #727272;
}

#Filters a.Active {
	background: #7289da;
	color: #fff;
	border: #efeae7 0px solid;
}

#Filters {
	border-bottom: #23272a 1px solid;
}

select {
	color: #7289da;
	background-color: #23272a;
	border-color: #23272a;
}

#Content>.Right .block .MoreLink {
	background: #23272a;
	border: #eee 0px solid;
	border-radius: 4px;
	display: block;
	margin-bottom: 18px;
	padding: 5px 0;
	text-align: center;
}

#Content>.Right .block .item-list li {
	border-bottom: 0px solid #eee;
}

#search-block-form .form-item .form-text {
	background: #2c2f33;
	color: #99aab5;
}
}
