  	.menu-tiles .menu-tile.mtile.menu-option-500000689 .face
  	{
		background: rgb(10, 10, 10) none repeat scroll 0% 0% !important;
		border: 1px solid rgb(0, 255, 157);
		color:rgb(0, 255, 157);
    }
  
 /*orange tiles*/
  	.menu-tiles .menu-tile.mtile.menu-option-500000124 .face,
  	.menu-tiles .menu-tile.mtile.menu-option-500000465 .face,
  	.menu-tiles .menu-tile.mtile.menu-option-500000379 .face,
  	.menu-tiles .menu-tile.mtile.menu-option-500000733 .face
  	{
		background: rgb(10, 10, 10) none repeat scroll 0% 0% !important;
		border: 1px solid rgb(255, 50, 0);
		color:rgb(255, 50, 0);
    }
  
	.menu-tile .face img.icon 
  	{
      opacity:100;
      max-width:100% !important;
    }
  
  .menu-tiles .menu-tile .board .face
  	{
    background: rgb(10, 10, 10) none repeat scroll 0% 0% !important;
  	}
  
  	.menu-tiles .menu-tile .board .face .tile-text 
  	{
    padding-bottom: 0px !important;
    background: transparent none repeat scroll 0% 0%;
    bottom: auto;
    text-align: center;
    font-family: Roboto,Arial,sans-serif;
    top: 0%;
	font-weight:900;
	}
  
	.menu-tiles .menu-tile .board .face {
    color: rgb(230,230,240);
	}
	.menu-tiles .menu-tile .board .face:hover{
    color: rgb(240,240,250) !important;
	}
	.menu-tiles .menu-tile.flipping .board.flipped,
  .menu-tiles .menu-tile.mtile.menu-option-500002094 a.face
  	{
		border: 1px solid rgb(28, 156, 255);
	}
  
  	.menu-tiles .menu-tile.mtile.menu-option-500001558 .timetable-header,
	.menu-tiles .menu-tile.mtile.menu-option-500000835 .imp-dates-header
  	{
    	background-color: rgba(10,10,10,0) !important;
     	border-bottom: 1px solid rgb(28, 156, 255);
    }
	.menu-tiles .menu-tile.mtile.menu-option-500001558 .timetable-header:hover
  	{
	border-bottom: 2px solid rgb(28, 156, 255) !important;
	}
  .menu-tiles .menu-tile.mtile.menu-option-500000835 .imp-dates-header:hover
  {
 	border-bottom: 2px solid rgb(28, 156, 255) !important;
  }
  
  .menu-tiles .menu-tile .board .face .tile-badge .badge-text
  {
  box-shadow:0 0 10px 0 rgba(255, 0, 0, 0.3) !important;
    border: 1px solid rgba(255, 50, 0, 0.7);
  }
  
  .header div.dropdown-toggle img 
  	{
    margin: 0px 0px 0px 10px !important;
    filter: invert(100%);
	}
  .header
  {
  	background:rgb(10,10,10)!important ;
  }
  .dropdown
  {
  	background:rgb(10,10,10) !important;
  }
.menu-tiles .menu-tile.mtile.menu-option-500001558 .description, .dateInfo, .timeInfo
{
        color:rgb(28, 156, 255);
}
.menu-tiles .menu-tile.mtile.menu-option-500001889 .dashboard-card 
{
        background:rgb(10,10,10) !important;
}

/*update 1 - 24/07/17*/

.header .icons ul li a ,
.icon
{
