css:
@-moz-document domain("www.omegle.com") {
/* add in sliding messages */
html {
    overflow: scroll;
    overflow-x: hidden;
    overflow:hidden;
}
::-webkit-scrollbar {
    width: 0px;  /* remove scrollbar space */
    background: transparent;  /* optional: just make scrollbar invisible */
}
body{background-color:#f1f1f1;}
html{font-family: 'Helvetica';}
.strangermsg,.statuslog,.logtopicsettings{
    font-size: 17px;
    font-weight: normal;
    float:left;
    display:inline-block;
    color: black;
    /* padding-right: 12px; */
    padding: 6px 8px;
    padding-right: 12px;
    background-color:#dedede;
    clear: both;
    height: auto;
    border-radius: 15px;
    margin: 1px;
    margin-right: 10px;

    
}

.disconnectbtn {
    color:black;
    background:white;
}

.statuslog{padding-left:14px;}
.youmsg{
    font-size: 17px;
    color: white;
    padding-right: 12px;
    padding: 6px 8px;
    padding-right: 12px;
    
background: #3abaff; /* Old browsers */
background: -moz-linear-gradient(top, #3abaff 0%, #1e9dff 100%); /* FF3.6-15 */
background: -webkit-linear-gradient(top, #3abaff 0%,#1e9dff 100%); /* Chrome10-25,Safari5.1-6 */
background: linear-gradient(to bottom, #3abaff 0%,#1e9dff 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */

    clear: both;
    height: auto;
    margin: auto;
    border-radius: 15px;
    margin: 1px;
    margin-right:10px;
    float: right;
    display: inline-block;
}

.chatmsgwrapper{
	border-radius:100px;
    height:20px;
    padding-left:10px;
}
.chatmsg{
	border-radius:100px;
    height:20px;
    padding-left:2px;
    background-color: rgba(0, 0, 0, 0);
    resize: none;
    margin-right: -20px;
}
.sendbtnwrapper{
    height:30px;
    border-radius: 100px;
}
#adwrapper{display:none;}
#header{display:none;}
.logwrapper{margin-bottom:-48px; margin-top:9px;}
.logbox,.logwrapper{border-radius:10px;}
.logbox{margin-bottom:10px;}
.controlwrapper{height:32px;}
.sendbtn{
    font-weight: bold;
    height:30px;
    color: white;
    border-radius: 100px;
    background: #3abaff; /* Old browsers */
background: -moz-linear-gradient(top, #3abaff 0%, #1e9dff 100%); /* FF3.6-15 */
background: -webkit-linear-gradient(top, #3abaff 0%,#1e9dff 100%); /* Chrome10-25,Safari5.1-6 */
background: linear-gradient(to bottom, #3abaff 0%,#1e9dff 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}
.sendbtn:hover{
    background: rgba(39,170,240,1);
background: -moz-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(39,170,240,1)), color-stop(100%, rgba(25,145,230,1)));
background: -webkit-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: -o-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: -ms-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: linear-gradient(to bottom, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
}
.sendbtn:active{
	background: rgba(23,157,230,1);
background: -moz-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23,157,230,1)), color-stop(100%, rgba(12,124,209,1)));
background: -webkit-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: -o-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: -ms-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: linear-gradient(to bottom, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
}
.disconnectbtnwrapper{
    height:30px;
    border-radius: 100px;
}
.disconnectbtn{
    height:30px;
    border-radius: 100px;
    color: black;
    
}
#feedback,#introtext,#mobilesitenote{display:none;}
.newbtn .disconnectbtn {
    background: #3abaff; /* Old browsers */
background: -moz-linear-gradient(top, #3abaff 0%, #1e9dff 100%); /* FF3.6-15 */
background: -webkit-linear-gradient(top, #3abaff 0%,#1e9dff 100%); /* Chrome10-25,Safari5.1-6 */
background: linear-gradient(to bottom, #3abaff 0%,#1e9dff 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    color: white;
    font-weight: bold;
}
.newbtn .disconnectbtn:hover{background: rgba(39,170,240,1);
background: -moz-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(39,170,240,1)), color-stop(100%, rgba(25,145,230,1)));
background: -webkit-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: -o-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: -ms-linear-gradient(top, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%);
background: linear-gradient(to bottom, rgba(39,170,240,1) 0%, rgba(25,145,230,1) 100%)}
.newbtn .disconnectbtn:active{background: rgba(23,157,230,1);
background: -moz-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23,157,230,1)), color-stop(100%, rgba(12,124,209,1)));
background: -webkit-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: -o-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: -ms-linear-gradient(top, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);
background: linear-gradient(to bottom, rgba(23,157,230,1) 0%, rgba(12,124,209,1) 100%);}
.disconnectbtn:hover{background-color:#dedede}
.disconnectbtn:active{background-color:#c4c4c4;}
.msgsource,.btnkbshortcut{font-size:.1px;color:black;}
}
