css:
@-moz-document domain("www.ozbargain.com.au") {
/*backgroudn and foreground colors*/

body,
p,
h1,
h2,
h3,
h4 {
    font-family: sans-serif !important;
    line-height: 1.4em !important
}
ul.ng2-deals li div.ng2-dealimg > a div.n-vote,
ul.ng2-deals li,
div.menupopup {
    background-color: black !important;
    border: none !important;
    box-shadow: 0 .3em 3em rgba(0, 0, 0, 0.5)
}
.tabs + .node-ozbdeal .content {
    color: #fff !important
}
.site-logo {
    background-size: auto 100%;
    height: 41px;
    width: 43px;
    margin-right: 5px;
    overflow: hidden;
}
#title {
    font-size: 2em;
}
h1#title,
div.node h2.title {
    font-weight: 200;
}
div.wrap {
    min-width: 0 !important;
    background-color: rgba(0, 0, 0, 0.2) !important;
}
#menu {
    float: none;
}
.dollar,
.title em.dollar {
    color: #cd6702 !important
}
.shade1,
.shade1h:hover,
span.marker,
ul.tabs.secondary,
ul.tabs.primary li:hover,
ul.tabs.primary li.active {
    background-color: #789;
    color: #123
}
ul.ozblist div.item {
    margin-left: 0;
    padding: 1em 0
}
body,
#main,
.btn,
form.search,
div.foxshot-container a,
div.couponcode,
form.search input[type="text"],
div.menupopup,
div.menupopup div.group,
.btn:active,
div.storeinfo > h3 > strong,
.btn.active,
.shade3,
.shade3h:hover,
#forum > table th,
table.data,
table.data th,
ul.tabs.secondary li:hover,
span.nodeexpiry span.marker,
textarea,
fieldset.collapsible legend,
ul.tabs.secondary li.active {
    background-color: #161616;
    color: #83898c;
    font-family: sans-serif;
    font-weight: 300;
}
/*divider color*/

form.search input[type="text"],
div.foxshot-container a,
div.couponcode,
div.sidebar ul.ozblist > li,
div.node-teaser,
div.menupopup,
div.menupopup div.group,
#forum td,
#forum th,
#forum table,
table.data,
table.data td,
table.data th,
fieldset.collapsible legend,
table tr > td,
div.nodegrid div.node,
div.menupopup h3,
div.noti-item,
div.nodegrid,
div.couponcode strong,
fieldset,
div.ac-result > ul > li.searchac,
dl.search-results dt.title,
#s-control,
div.ac-result,
div.ac-result,
div.userprofile div.activities > div,
div.forumnew form.search,
.btn,
h2.section {
    border-radius: 0;
    border-color: #404449 !important
}
ul.ng2-deals li,
ul.ng2-deals,
div.ac-result > ul > li.section,
div.productlist div.img {
    background-color: transparent
}
div.productlist div.img a {
    border: none;
}
.productlist {
    background-color: #456
}
dl.search-results strong {
    background-color: #567;
    color: #83898c
}
input {
    background-color: transparent;
    color: currentcolor;
    appearance: none;
    border: 0;
    border-bottom: 1px solid
}
div.referral {
    background-color: black
}
div.couponcode {
    display: inline-block;
}
div.couponcode strong {
    color: #83898c;
    font-family: monaco, dejavu-mono, monospace;
    letter-spacing: .1em;
    padding: .5em 1em;
}
div.nodegrid {
    border-top: 1px solid
}
.btn:hover,
a:hover,
div.comment,
li:hover,
.btn:hover,
.shade0,
.shade0h:hover,
ul.pager a:hover,
ul.tabs.primary,
.node:hover,
#live div.type-forum,
#livetable tr.type-forum td,
span.nodeexpiry span.marker,
div.taxonomy span a,
tr:hover td,
#menu > li:hover > a,
#menu > li.hovered > a {
    background-color: rgba(0, 0, 0, 0.2) !important
}
div.couponcode {
    padding: .2em
}
/*stuff I dont want / need . */

#footer .sidebar,
.unpublished,
div.couponcode i,
ul.ozblist div.votes,
#announcement-nl,
.blockcontent .wikipage,
.node-social,
.announcement-nl,
.ads {
    display: none !important
}
#footer {
    color: #656a72;
    background-color: #11151a;
    border-top: none;
    padding: 2em;
}
#footer h3,
#footer a {
    color: #757a82 !important
}
a {
    color: #894400 !important
}
.comment .links {
    opacity: .65;
}
#c-control,
div.storeinfo,
div.storeinfo > h3,
div.block,
div.block h3.blocktitle,
span.nvb.voteup,
div.c-vote span.voteup,
div.c-vote span.votedown,
span.nvb.votedown {
    background-color: transparent;
    border: none
}
@media only screen and (min-width: 1600px) {
    #main > div.wrap {
        background-color: transparent;
    }
}
* {
    box-sizing: border-box;
}
@media only screen and (max-width: 70em) {
    div.wrapsb1 div.maincontent {
        margin-right: 1em;
        margin-left: 0;
    }
    #sidebar {
        display: block;
        float: none;
        width: 90%;
        clear: both;
        margin: 2em auto;
    }
    form.search {
        display: none
    }
}
h1,
h2 {
    border: none !important
}
div.dokuwiki a.wikilink1,
div.dokuwiki h1 a,
div.dokuwiki h2 a,
div.dokuwiki h3 a,
div.dokuwiki h4 a,
div.dokuwiki h5 a,
div.dokuwiki a.nolink {
    color: #83898c !important
}
div.dokuwiki div.toc,
div.dokuwiki div.tocheader,
#forum > table td.container {
    background-color: transparent !important
}
form.search {
    background-color: transparent
}
form.search input[type="text"] {
    background-color: transparent !important;
    border-bottom: 1px solid !important;
    border-right: none !important;
    padding: .5em
}
div.form-item label,
form.search input[type="text"]:focus {
    color: #83898c;
}
h1#title {
    color: #83898c;
    padding-bottom: .5em
}
div.menupopup h3,
div.comment-hint,
form.search input[type="text"],
form.search button {
    background-color: transparent
}
textarea {
    color: #83898c;
    padding: 1em;
    border-color: #656a73;
    margin: 1em 0;
    outline: 0;
}
textarea:focus {
    border-color: white
}
div.comment-hint {
    border: none
}
button,
input[type="submit"]:focus,
input[type="submit"]:active,
button,
input[type="submit"] {
    font-size: 1.1rem;
    appearance: none;
    border: 1px solid;
    background-color: transparent;
    color: #83898c;
    padding: .5em 1em;
    cursor: pointer;
    outline: 0;
}
button,
input[type="submit"]:focus,
input[type="submit"]:hover,
input[type="submit"]:active {
    color: black;
    background-color: white;
    border-color: white;
}
#header {
    background-color: #000;
}
#menu > li.active > a {
    background-color: #151515 !important;
    color: #cd6702 !important
}
div.comment-owner {
    border: 2px solid #678;
    background-image: none
}
body.a div.nodemeta {
    margin-left: 0;
}
body.a div.nodemeta > ul {
    display: inline
}
body.a div.nodemeta > div,
body.a div.nodemeta > ul > li {
    display: inline-block;
    vertical-align: top;
}
.node-forum {
    color: #c0c4c9
}
.comment {
    color: #d2d4d8
}
div.comment-op {
    background-image: none;
    border: 1px solid orangered
}
.showmore {
    background-color: #000 !important
}
div.submitted,
div.nodegrid div.nodetop span.marker {
    background-color: transparent;
    color: #567
}
div.ac-result,
.noti-snippet {
    background-color: black !important;
    border-color: white !important;
    box-shadow: 0 .2em 1em rgba(0, 0, 0, 0.5) !important
}
div.ac-result > ul > li {
    color: white
}
div.noti-item.noti-unread,
#noti-snippet > h3 {
    background-color: #456
}
div.c-vote {
    color: #83898c !important
}
span.nvb.voteup,
span.nvb.votedown,
div.c-vote span.voteup,
div.c-vote span.votedown {
    color: #567;
    border-radius: 0 !important
}
.marker {
    border: none;
    border-radius: 0 !important
}
div.nodegrid h2.title {
    line-height: 1.4em;
    font-family: sans-serif;
    font-weight: 300
}
ul.competition-list-meta li,
[style="color:#0c0;"] {
    color: #567 !important
}
div.foxshot-container a {
    overflow: hidden;
    border: none;
    background-color: transparent !important;
}
#c-control {
    background-color: #000
}
span.c-toggle {
    background-image: none;
    background-color: #567
}
.closed span.c-toggle {
    background-color: #789
}
div.n-stub > img.gravatar,
.n-left img,
img.usericon {
    border: none;
}
#live div.type-comp,
#livetable tr.type-comp td {
    background-color: #456 !important
}
.gotodeal .btn {
    display: block;
    border-radius: 0;
    font-size: 1.5em !important;
    width: 100%;
    border-color: #c32 !important;
    background-color: #c32 !important
}
.maincontent div.node {
    padding: 1.5em 0 !important
}
div.couponcode i + strong {
    border-left: none
}
::-moz-selection {
    /* Code for Firefox */
    
    color: #83898c;
    background-color: orangered;
}
::selection {
    color: #83898c;
    background-color: orangered;
}
.ac-result .section,
div.ac-result {
    border: none !important
}
.searchac:hover {
    background-color: #456 !important
}
.ac-result .section {
    color: #678
}
div.comment {
    padding: 1em .5em !important;
}
div.storeinfo > h3,
.nodeinfo,
.nodeinfo a {
    color: #83898c !important
}
.sidebar > div:nth-child(2) {
    display: none
}
div.messages {
    background-color: #fff;
    border-radius: 0
}
div.userprofile div.activities div.content {
    color: #83898c
}
div.node h2.title a {
    ;
    margin-bottom: .5em;
    border-bottom: 2px solid transparent;
}
div.node h2.title a:focus,
div.node h2.title a:hover {
    text-decoration: none;
    border-bottom: 2px solid #376da3;
}
.node.node-ozbdeal a {
    color: #4183C4 !important
}
.node.node-ozbdeal.expired h1,
.node.node-ozbdeal.expired h2,
.links a,
.submitted a,
.node.node-ozbdeal.expired a {
    color: #356ba0 !important
}
.expired .foxshot-container {
    box-shadow: none !important
}
.expired .foxshot-container img {
    filter: grayscale(1)
}
.n-vote {
    background-color: rgba(11, 22, 33, 0.8);
}
.tagimg,
div.tooltip img.gravatar,
.maincontent .foxshot-container {
    overflow: hidden;
    /*    background-color: #434a55; */
    
    background-color: white;
    box-shadow: 0 .3em 1em rgba(0, 0, 0, 0.5) !important
}
div.foxshot-container {
    width: 120px;
    height: 120px;
}
.nodegrid .foxshot-container {
    border-radius: 0
}
.comment > .n-right > .links {
    opacity: 0 !important
}
.comment:hover > .n-right > .links {
    opacity: 1 !important
}
div.nodeinfo {
    margin: .5em 0;
}
div.nodeinfo ul {
    padding: 0;
}
.foxshot-container,
.gotodeal {
    display: inline-block;
    vertical-align: middle;
}
.foxshot-container + .gotodeal {
    margin-left: 1em;
}
#tagcontrol div.tagdesc div.description {
    color: #83898c
}
.competition-list-meta li {
    display: inline-block;
    vertical-align: top
}
#tooltip {
    background-color: #444c55;
    box-shadow: 0 .2em 1em rgba(0, 0, 0, 0.5);
    border: none;
}
#tooltip-copy {
    color: #848c95;
}
#tooltip-head {
    background-color: #83898c;
    color: black;
    position: relative
}
#tooltip-head:before {
    content: "";
    display: block;
    position: absolute;
    top: -2em;
    left: 50%;
    margin-left: -1em;
    border-width: 0 1em 1em 1em;
    border-style: solid;
    width: 0;
    height: 0;
    border-width: 1em;
    border-color: transparent transparent white transparent
}
#tooltip-close {
    color: #567;
    font-size: 15px;
    height: 1.2rem;
    width: 1.2rem;
    text-align: center;
}
#tooltip-arrow {
    display: none;
}
[value="Post comment"] {
    float: right
}
div.tooltip img.gravatar {
    border: 0;
}
#header2nd a {
    color: #678 !important
}
h1.tags > span,
#header2nd ul > li > a:hover,
#header2nd ul > li > a.active {
    background-color: #cd6702 !important;
    color: black !important;
    border-radius: 0;
}
div.comment-hint > div.hint {
    background-color: transparent;
    border: 0;
    border-radius: 0;
}
button,
input[type="submit"]:focus,
input[type="submit"]:hover,
input[type="submit"]:active {
    color: #83898c;
    background-color: black;
    border-color: transparent
}

div.menupopup li.userinfo {
    background-color: #161616;
    color: #83898c;
    border-bottom: #ccc solid 1px;
    overflow: hidden;
    padding: 5px 10px 3px 10px;
}
#conversation_table div.conversation:hover {
    background-color: #161616;
}
div.privatemsg_teaser {
    background-color: #161616;
}

span.horizontal-participants {
    color: #356ba0
}
}
