css:
@-moz-document domain("naruto-kun.hu") {
/* menüsor */

.sub-header {
    background: url("https://s5.postimg.org/8lj2fk087/menu_bg_sajat.png") repeat-x scroll left top rgba(0, 0, 0, 0);
    margin-top: 80px;
}
#menu {
    margin-left: 12px;
}
#menu a {
    padding: 10px 15px;
    color: #6C6C6C;
    font-size: 13px;
    font-family: inherit;
    text-transform: none;
}
/* menüsor lenyíló elemek */

#menu ul {
    background: linear-gradient(#f5f8f8, #DCD2C5);
    width: 200px;
}
/* header */

div[style] {
    border-radius: 6% 6% 8px 8px;
}
/* háttér */

body {
    background: url(https://s5.postimg.org/inxuiaf51/bgloop.png) center;
}
/* hír fejlécek */

.cap-main {
    background: linear-gradient(to bottom, rgba(125, 48, 48, 0.55), rgba(125, 48, 48, 0.55) 60%, #905757 40%);
    color: #202020;
    text-shadow: 0 1px 0 #717171;
    border-left: 10px solid rgba(81, 21, 21, 0.2);
    border-top: 1px solid rgba(81, 21, 21, 0.2);
    border-right: 1px solid rgba(81, 21, 21, 0.2);
    border-radius: 4px 5px 3px 0px;
}
/* hír dobozok */

.main-border {
    color: #414445;
    background: rgba(221, 221, 221, 0.58);
    border-left: 1px solid #CBCBCB;
    border-bottom: 5px ridge RGBA(167, 167, 167, 0.2);
    border-right: 5px ridge RGBA(167, 167, 167, 0.2);
    border-radius: 0px 0px 8px 8px;
}
.news-body {
    font: 12px/1.3em Georgia;
}
.news-info {
    border-bottom: 1px dashed rgba(0, 0, 0, 0.3);
}
a {
    text-decoration: none;
    color: #5B2D0F;
}
.news-category {
    box-shadow: 3px 5px 10px #888888;
}
/* oldalválasztó sor */

.pagenav span {
    background: none repeat scroll 0 0 RGBA(195, 193, 191, 0.3);
    color: #4E4E4E;
    border-style: double;
    border-radius: 8px;
    border-color: rgba(188, 134, 100, 0.4);
}
.pagenav a {
    background: none repeat scroll 0 0 rgba(255, 255, 255, 0.5);
    color: #212121;
    border-style: double;
    border-radius: 8px;
    border-color: rgba(188, 134, 100, 0.4);
}
/* oldalsó doboz */

.side-border-left {
    color: #414445;
    background: rgba(221, 221, 221, 0.58);
    border-left: 1px solid #CBCBCB;
    border-bottom: 5px ridge RGBA(167, 167, 167, 0.2);
    border-right: 5px ridge RGBA(167, 167, 167, 0.2);
    border-radius: 4px 8px 8px 8px;
    font: 10px Verdana;
}
.scap-main {
    border-bottom: 5px double rgba(0, 0, 0, 0.1);
}
/* avatarok */

.lstsn-users-online {
    border: none;
    border-top: 4px solid #20C100;
    box-shadow: 3px 5px 10px #888888;
    width: 48px;
    height: 48px;
    border-radius: 15px 15px 15px 15px;
}
.lstsn-users-five {
    border: none;
    border-top: 4px solid #FFA70F;
    box-shadow: 3px 5px 10px #888888;
    width: 48px;
    height: 48px;
    border-radius: 15px 15px 15px 15px;
}
.lstsn-users-offline {
    border: none;
    border-top: 4px solid #FF3D3D;
    box-shadow: 3px 5px 10px #888888;
    width: 48px;
    height: 48px;
    border-radius: 15px 15px 15px 15px;
}
/* footer */

#footer {
    background: url(https://www.sketchuptextureclub.com/public/texture_m/0041-ash-light-wood-fine-texture-seamless.jpg);
    margin-top: -5px;
    border-top: 30px ridge rgba(146, 106, 74, 0.2);
    min-height: 250px;
}
.footerb {
    color: #6c3131;
}
.footer_menu_list li a {
    color: #393939;
    text-shadow: 0px 1px 1px #6d0000;
}
.section_w180 {
    margin-top: 0px;
}
/* copyright footer */

#copyright-info {
    height: 48px;
    color: #675656;
    background: none repeat scroll 0 0 #C2AB8E;
}
/* közösségi gombok */

.slider .socials a {
    background: url(https://s5.postimg.org/4uiokq1bb/social.png) no-repeat 0 0;
}
/* gömbvadászat */

.pbuit {
    border-radius: 5px 25px 5px 25px;
    background-color: rgba(255, 255, 255, 0);
}
.tbl2 {
    font: 10.5px/1.5em Georgia;
    background: rgba(255, 255, 255, 0);
    padding: 4px;
    border: 2px double #979797;
    border-radius: 5px 18px 5px 18px;
}
/* kereső */

#search-button {
    background: linear-gradient(#f5f8f8, #DCD2C5);
    border-radius: 0px 3px 3px 0px;
}
#search-form {
    height: 31px;
    border: 2px double rgba(153, 153, 153, 0.6);
    border-radius: 3px;
    background-color: rgba(0, 0, 0, 0.1);
    overflow: hidden;
    padding-left: 5px;
}
/* komment */

.comment_message {
    background-color: RGBA(235, 235, 235, 0.4);
}
/* hír nagykép */

img.forum-img {
    box-shadow: 3px 5px 10px #888888;
}
/* egyéb */

.forum-caption {
    background-color: #c8b8a7;
}
#mask {
    display: none!important;
    ;
}
/* táblázat szín  */

tr:nth-child(even) {
    background-color: rgba(255, 255, 255, 0.3);
}
}

@-moz-document url-prefix("http://naruto-kun.hu/forum/") {
.sub-header {
    position: absolute;
}
}

@-moz-document url-prefix("http://naruto-kun.hu/manga/") {
td[style] {
    text-align: center!important;
}
}
