@-moz-document domain(192.168.1.1) {
#btn-help:hover,
#btn-help:focus,
#btn-help:active,
#btn-help.active,
#btn-help.disabled,
#btn-help[disabled] {
    background-color: #000000;
}
#btn-help:hover,
#btn-help:focus,
#btn-help:active,
#btn-help.active,
#btn-help.disabled,
#btn-help[disabled] {
    color: #fff;
    background-color: #000000;
}
#logo {
    overflow: hidden;
    text-indent: -1999px;
    width: 640px;
    height: 54px;
    display: block;
    background-image: url(http://zupimages.net/up/17/27/r7ao.png);
    background-repeat: no-repeat;
    background-color: transparent;
}
#header {
    height: 54px;
    background-color: #000000;
    background-image: -moz-linear-gradient(top, #000000, #000000);
    background-image: -webkit-linear-gradient(top, #000000, #000000);
    background-image: -o-linear-gradient(top, #000000, #000000);
    background-image: linear-gradient(to bottom, #000000, #000000);
    background-repeat: repeat-x;
  border-bottom: 0px!important;}
#btn-help {
    position: absolute;
    top: 5px;
    right: 185px;
    display: none;
    width: 18px;
    height: 32px;
    font-size: 30px;
    font-weight: bold;
    line-height: 35px;
    color: #FFFFFF;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
    background-color: #878787;
    background-repeat: repeat-x;
}
.navbar-inner {
    min-height: 40px;
    padding-right: 0px!important;
    padding-left: 0px!important;
    background-color: #000000;
    background-image: none!important;
    /* background-repeat: repeat-x; */
    
    border: none!important;
    border-radius: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
}
caption img,
.text-center img {
    display: none;
}
.navbar {
    margin-bottom: 0!important;
}
.breadcrumb {
    padding: 8px 15px;
    margin: 0 0 20px;
    list-style: none;
    background-color: #bababa;
    -webkit-border-radius: 0!important;
    border-radius: 0!important;
}
a {
    color: #FFFFFF;
    text-decoration: none;
    text-shadow: none!important;
}
a:hover,
a:focus {
    color: #FFFFFF;
    text-decoration: none;
}
#main .homefooter {
    min-height: 300px;
    padding: 20px 0 0 0;
    color: #ffffff;
    background-image: linear-gradient(to top, #000000, #3d3d3d);
}
td.disabled {
    padding-left: 45px;
    color: rgb(241, 191, 77);
    background-image: url(http://zupimages.net/up/17/27/173i.png);
}
td.enabled {
    padding-left: 45px;
    color: rgb(76, 157, 91);
    background-image: url(http://zupimages.net/up/17/27/h33s.png);
}
td.unused {
    padding-left: 45px;
    color: rgb(37, 127, 211);
    background-image: url(http://www.zupimages.net/up/17/27/linr.png);
}
.navbar .nav li.dropdown.open>.dropdown-toggle,
.navbar .nav li.dropdown.active>.dropdown-toggle,
.navbar .nav li.dropdown.open.active>.dropdown-toggle {
    color: #FFFFFF;
    background-color: #1079D6;
}
.dropdown-menu>li>a:hover,
.dropdown-menu>li>a:focus,
.dropdown-submenu:hover>a,
.dropdown-submenu:focus>a {
    color: #fff;
    text-decoration: none;
    background-color: #1079D6;
    background-image: none!important;
}
.dropdown-menu {
    position: absolute;
    font-size: 1.2em;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 0 0 0;
    list-style: none;
    background-color: #fff;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, 0.2);
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 0!important;
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -webkit-background-clip: padding-box;
    -moz-background-clip: padding;
    background-clip: padding-box;
}
.modal h3 {
    font-size: 1.7em;
    color: #18A55B;
    text-align: center;
    font-weight: 300;
}
.btn {
    color: #fff;
    background-color: #000000;
    background-image: none!important;
    outline: 0!important;
}
.btn:hover,
.btn:focus,
.btn:active,
.btn.active,
.btn.disabled,
.btn[disabled] {
    color: #fff;
    background-color: #000000;
}
.btn.active,
.btn:active {
    background-image: none;
    outline: 0!important;
}
.title h1 {
    padding: 12px 10px 0;
    font-size: 1.2em;
    color: #1079D6;
}
.breadcrumb>li>.divider {
    padding: 0 5px;
    color: #534b2d;
}
.has-switch span.switch-primary,
.has-switch span.switch-left {
    color: #fff;
    background-color: #000000;
    background-image: none!important;
    border: 0!important;
}
.has-switch span.switch-primary:hover,
.has-switch span.switch-left:hover,
.has-switch span.switch-primary:focus,
.has-switch span.switch-left:focus,
.has-switch span.switch-primary:active,
.has-switch span.switch-left:active,
.has-switch span.switch-primary.active,
.has-switch span.switch-left.active,
.has-switch span.switch-primary.disabled,
.has-switch span.switch-left.disabled,
.has-switch span.switch-primary[disabled],
.has-switch span.switch-left[disabled] {
    color: #fff;
    background-color: #000000;
}
.has-switch span.switch-primary:hover,
.has-switch span.switch-left:hover,
.has-switch span.switch-primary:focus,
.has-switch span.switch-left:focus,
.has-switch span.switch-primary:active,
.has-switch span.switch-left:active,
.has-switch span.switch-primary.active,
.has-switch span.switch-left.active,
.has-switch span.switch-primary.disabled,
.has-switch span.switch-left.disabled,
.has-switch span.switch-primary[disabled],
.has-switch span.switch-left[disabled] {
    color: #fff;
    background-color: #000000;
}
.has-switch:focus {
    border: none!important;
    outline: 0;
}
select,
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    display: inline-block;
    height: 20px;
    padding: 4px 6px;
    margin-bottom: 10px;
    font-size: 18px;
    line-height: 20px;
    width: 250px;
    color: #000000;
    vertical-align: middle;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}
textarea:focus,
input[type="text"]:focus,
input[type="password"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="time"]:focus,
input[type="week"]:focus,
input[type="number"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="search"]:focus,
input[type="tel"]:focus,
input[type="color"]:focus,
.uneditable-input:focus {
    border-color: #1079D6;
}}
