/* Copyright Alexandre 2017 */

/* i really want this to be global */

#btn-help:hover,
#btn-help:focus,
#btn-help:active,
#btn-help.active,
#btn-help.disabled,
#btn-help[disabled] {
    color: #fff;
    background-color: #00b0ba;
}
#btn-help:hover,
#btn-help:focus,
#btn-help:active,
#btn-help.active,
#btn-help.disabled,
#btn-help[disabled] {
    color: #fff;
    background-color: #00b0ba;
}
#logo a {
    display: block;
    width: 132px;
    height: 50px;
}
#logo {
    overflow: hidden;
    text-indent: -1999px;
    width: 132px;
    height: 50px;
    display: block;
    background-image: url(http://c.woopic.com/libs/58f2b14cd51a7a0cc47683133828cb7d/modules/sosh/icons/sprite_elco_sosh.png?1497517065);
    background-repeat: no-repeat;
    background-color: transparent;
}
#header {
    height: 54px;
    background-color: #00a1ab;
    background-image: -moz-linear-gradient(top, #d40119, #e01727);
    background-image: -webkit-linear-gradient(top, #00a1ab, #00b4bf);
    background-image: -o-linear-gradient(top, #d40119, #e01727);
    background-image: linear-gradient(to bottom, #00a1ab, #00b4bf);
    background-repeat: repeat-x;
    border-bottom: 0px!important;
}
#btn-help {
    position: absolute;
    top: 5px;
    right: 185px;
    display: none;
    width: 18px;
    height: 32px;
    font-size: 30px;
    font-weight: bold;
    line-height: 35px;
    color: #fff;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
    background-color: #00b0ba;
    background-image: -moz-linear-gradient(top, #e2001a, #bd1220);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#00a6b0), to(#00b3be));
    background-image: -o-linear-gradient(top, #e2001a, #bd1220);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#00a6b0), to(#00b3be));
    background-repeat: repeat-x;
    border-color: #1296bd #12adbd #0b6d77;
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
}
.navbar-inner {
    min-height: 40px;
    padding-right: 0px!important;
    padding-left: 0px!important;
    background-color: #e3295e;
    background-image: none!important;
    /* background-repeat: repeat-x; */
    
    border: none!important;
    border-radius: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
}
caption img,
.text-center img {
    display: none;
}
@font-face {
    font-family: 'Abel';
    font-style: normal;
    font-weight: 400;
    src: local('Abel Regular'), local('Abel-Regular'), url(https://fonts.gstatic.com/s/abel/v7/brdGGFwqYJxjg2CD1E9o7g.woff2) format('woff2');
}
#main h1 {
    font-family: Abel!important;
}
.navbar {
    margin-bottom: 0!important;
}
.breadcrumb {
    padding: 8px 15px;
    margin: 0 0 20px;
    list-style: none;
    background-color: #fbcd00;
    -webkit-border-radius: 0!important;
    border-radius: 0!important;
}
a {
    color: #534b2d;
    text-decoration: none;
    text-shadow: none!important;
}
a:hover,
a:focus {
    color: #534b2d;
    text-decoration: none;
}
#main .homefooter {
    min-height: 300px;
    padding: 20px 0 0 0;
    color: #fff;
    background-color: #00a9b4;
}
td.disabled {
    padding-left: 45px;
    color: #bd1220;
    background-image: url(http://zupimages.net/up/17/27/ua94.png);
}
td.enabled {
    padding-left: 45px;
    color: rgb(0, 95, 101);
    background-image: url(http://zupimages.net/up/17/27/ywt6.png);
}
.navbar .nav li.dropdown.open>.dropdown-toggle,
.navbar .nav li.dropdown.active>.dropdown-toggle,
.navbar .nav li.dropdown.open.active>.dropdown-toggle {
    color: #ffffff;
    background-color: #e3295e;
}
.dropdown-menu>li>a:hover,
.dropdown-menu>li>a:focus,
.dropdown-submenu:hover>a,
.dropdown-submenu:focus>a {
    color: #fff;
    text-decoration: none;
    background-color: #e3295e;
    background-image: none!important;
}
.dropdown-menu {
    font-family: Abel;
    position: absolute;
    font-size: 1.2em;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 0 0 0;
    list-style: none;
    background-color: #fff;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, 0.2);
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 0!important;
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -webkit-background-clip: padding-box;
    -moz-background-clip: padding;
    background-clip: padding-box;
}
.modal h3 {
    font-size: 1.7em;
    color: #007177;
    font-family: Abel;
    text-align: center;
    font-weight: 300;
}
.btn {
    color: #fff;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.2);
    background-color: #e3295e;
    background-image: none!important;
    outline: 0!important;
}
.btn:hover,
.btn:focus,
.btn:active,
.btn.active,
.btn.disabled,
.btn[disabled] {
    color: #fff;
    background-color: #e3295e;
}
.btn.active,
.btn:active {
    background-image: none;
    outline: 0!important;
    -webkit-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
}
.title h1 {
    padding: 12px 10px 0;
    font-size: 1.2em;
    color: #00adb7;
}
.breadcrumb>li>.divider {
    padding: 0 5px;
    color: #534b2d;
}
.has-switch span.switch-primary,
.has-switch span.switch-left {
    color: #fff;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.2);
    background-color: #E3295B;
    background-image: none!important;
    border: 0!important;
}
.has-switch span.switch-primary:hover,
.has-switch span.switch-left:hover,
.has-switch span.switch-primary:focus,
.has-switch span.switch-left:focus,
.has-switch span.switch-primary:active,
.has-switch span.switch-left:active,
.has-switch span.switch-primary.active,
.has-switch span.switch-left.active,
.has-switch span.switch-primary.disabled,
.has-switch span.switch-left.disabled,
.has-switch span.switch-primary[disabled],
.has-switch span.switch-left[disabled] {
    color: #fff;
    background-color: #e3295e;
}
.has-switch span.switch-primary:hover,
.has-switch span.switch-left:hover,
.has-switch span.switch-primary:focus,
.has-switch span.switch-left:focus,
.has-switch span.switch-primary:active,
.has-switch span.switch-left:active,
.has-switch span.switch-primary.active,
.has-switch span.switch-left.active,
.has-switch span.switch-primary.disabled,
.has-switch span.switch-left.disabled,
.has-switch span.switch-primary[disabled],
.has-switch span.switch-left[disabled] {
    color: #fff;
    background-color: #e3295e;
}
.has-switch:focus {
    border: none!important;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(118, 92, 159, 0.6);
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(118, 92, 159, 0.6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(118, 92, 159, 0.6);
}
select,
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    display: inline-block;
    height: 20px;
    padding: 4px 6px;
    margin-bottom: 10px;
    font-size: 18px;
    line-height: 20px;
    font-family: Abel!important;
    width: 250px;
    color: #000000;
    vertical-align: middle;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}
textarea:focus,
input[type="text"]:focus,
input[type="password"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="time"]:focus,
input[type="week"]:focus,
input[type="number"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="search"]:focus,
input[type="tel"]:focus,
input[type="color"]:focus,
.uneditable-input:focus {
    border-color: #e3295e;
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px;
    box-shadow: inset 0 1px 1px rgba(227, 41, 94, 0), 0 0 8px;
}
