css:
@-moz-document url-prefix("https://www.jianshu.com")
{
html{
   zoom:110%;
}

  /*将简书上的轮播隐藏掉*/
  .carousel-inner {
    overflow: hidden;
    width: 100%;
    display: none;
}

#indexCarousel {
	display:none;
}

.row {
    margin-left: -15px;
    margin-right: -45px;
}

/*宽屏模式*/
.note .post {
    margin: 0 auto;
    padding-top: 20px;
    padding-bottom: 60px;
    width: 85%
}

.note .post .article .show-content .image-package {
    max-width: 100%;
    padding-bottom: 25px;
    margin-left: 13.5%;
    text-align: center
}

.hljs {
    display: block;
    overflow-x: auto;
    padding: 0.5em;
    background: #fff;
    color: black;
}
.hljs,pre {
    background: #f6f6f6;
    color: #657b83;
    -webkit-text-size-adjust: none
}
.diff .hljs-header,
.hljs-comment,
.hljs-doctype,
.hljs-pi,
.lisp .hljs-string {
    color: #006a00;
}
.css .hljs-tag,
.hljs-addition,
.hljs-keyword,
.hljs-request,
.hljs-status,
.hljs-winutils,
.method,
.nginx .hljs-title {
    color: #aa0d91;
}
.hljs-meta {
    color: #aa0d91;
}
.hljs-command,
.hljs-doctag,
.hljs-hexcolor,
.hljs-link_url,
.hljs-number,
.hljs-regexp,
.hljs-rule .hljs-value,
.hljs-string,
.hljs-tag .hljs-value,
.tex .hljs-formula {
    color: #c41a16;
}
.css .hljs-function,
.hljs-built_in,
.hljs-chunk,
.hljs-decorator,
.hljs-id,
.hljs-identifier,
.hljs-localvars,
.hljs-name,
.hljs-title,
.vhdl .hljs-literal {
    color: #008;
}
.hljs-attribute,
.hljs-class .hljs-title,
.hljs-constant,
.hljs-link_reference,
.hljs-parent,
.hljs-type,
.hljs-variable,
.lisp .hljs-body,
.smalltalk .hljs-number {
    color: #9b703f;
}
.css .hljs-pseudo,
.diff .hljs-change,
.hljs-attr_selector,
.hljs-cdata,
.hljs-header,
.hljs-pragma,
.hljs-preprocessor,
.hljs-preprocessor .hljs-keyword,
.hljs-shebang,
.hljs-special,
.hljs-subst,
.hljs-symbol,
.hljs-symbol .hljs-string {
    color: #cb4b16
}
.hljs-deletion,
.hljs-important {
    color: #0000ff;
}
.hljs-link_label {
    color: #6c71c4
}
.tex .hljs-formula {
    background-color: #eee;
    font-style: italic;
    background: #eee8d5
}
}
