css:
@-moz-document domain("vk.com") {
.audio_w_covers .audio_row .audio_row__cover_icon {
    background-repeat: no-repeat;
    background-position: 50%;
    display: block !important;
}

.audio_w_covers .audio_row .audio_row__cover {
    background-size: cover;
    border-radius: 2px;
    display: none !important;
}

.audio_page_player .audio_page_player__cover {
    display: none !important;
}

.audio_page_player2 .audio_page_player__cover,.audio_w_covers .audio_row .audio_row__cover_back, .audio_w_covers .audio_row .audio_row__sound_bars {
    display: none !important;
}

.audio_w_covers .audio_row.audio_row__playing .audio_row__cover_icon {
    background-image: url(https://image.jimcdn.com/app/cms/image/transf/dimension=30x10000:format=png/path/s42cb6e5cc3a32b98/image/i832f79d92316cb7f/version/1499174238/image.png);
}

.audio_w_covers .audio_row .audio_row__cover_icon {
    background-image: url(https://image.jimcdn.com/app/cms/image/transf/dimension=30x10000:format=png/path/s42cb6e5cc3a32b98/image/i59fe7e3bee87b5f8/version/1499174262/image.png);
}

.audio_row .audio_row_content {
    height: 48px;
}

.audio_w_covers .audio_row {
    height: 44px;
}

.audio_row:hover:not(.audio_row__current) .audio_row_content {
    background-color: #f2f4f7;
    border-radius: 2px;
}

.audio_w_covers .audio_row .audio_row__lyrics {
    background-color: #e9edf1;
}

.audio_w_covers .audio_row .audio_row__title {
    color: #2a5885;
}

.audio_row:hover:not(.audio_row__current) .audio_row_content {
    background-color: transparent;
}
}
