css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myparcels.ru") {

  
  
#main div.rules, #main div.welcome, #main div.news, #main div.message {
display:none;
}  
  #leftColumn {
width:100%;
}
  body .forum .col-stats span {
    display: block;
    color:#fff;
}
  .forum{
    background-position: 0 top;
    top: 1px;
    color: #A2B31C;
    background: #20282D;
    background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30)); 
    box-shadow: 1px 1px 1px rgba(255,255,255,0.1); 
}
  #main .new div.info  {

    background: transparent;
    background: -moz-linear-gradient(top, #A0C658 3%, #8DAF4E 4%, #75923D 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#A0C658), color-stop(4%,#8DAF4E), color-stop(100%,#75923D)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
    color:#fff;
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
}
  #main  .new div.info .shipment-actions-container .shipment-actions a span {
      color:#fff;  
  }  
  
  #main  .new div.info a {
      color:#fff;  

  }    

 .ui-component-dropdown {
    background: #232B30; 
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
   border:0px;
   color:#fff;
} 
  .ui-component-dropdown .ui-component-dropdown-item, .ui-component-dropdown .ui-component-dropdown-item a {
   color:#fff;
}
  .ui-component-dropdown-item:hover{
     background: transparent;
    background: -moz-linear-gradient(top, #A0C658 3%, #8DAF4E 4%, #75923D 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#A0C658), color-stop(4%,#8DAF4E), color-stop(100%,#75923D)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);    
  }
  #header {
    background: #232B30; 
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
}
  #header .hinner {
   background: #232B30;
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
}
  body{
    background: #232B30;
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30));
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
    
  }
  img{
  
  }
a {
    color: #A2B31C;

}
  .tt-container {
    background-position: 0 top;
    top: 1px;
    color: #A2B31C;
    background: #20282D;
    background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30)); 
    box-shadow: 1px 1px 1px rgba(255,255,255,0.1); 
}
  .tt-container .tt-content, .tt-container .tt-content a {
    color: #A2B31C;
}

  #main .details {
    padding-left: 38px;
    border-bottom: 1px solid #21292E;
}
  #main .shipment {
border:0;
}
  .pagerPage {
border:0;
}
  .selectedPage {
    background-position: 0 top;
    top: 1px;
    color: #A2B31C;
    background: #20282D; 
    background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30));
    box-shadow: 1px 1px 1px rgba(255,255,255,0.1); 
}
  .shipment .shipment-actions-container .shipment-actions a:hover, .shipment .shipment-actions-container .shipment-actions a.active {
    background-position: 0 top;
    top: 1px;
    color: #A2B31C;
    background: #20282D;
    background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30)); 
    box-shadow: 1px 1px 1px rgba(255,255,255,0.1); 
}
  #main {
    outline: 0;
    padding: 5px 12px;
    display: block;
    color: #9fa8b0;
    font-weight: bold;
    text-shadow: 1px 1px #1f272b;
    border: 1px solid #1c252b;
    border-radius: 3px;
    background: #232B30; 
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 

	animation-name: pullUp;
	-webkit-animation-name: pullUp;	
	
	animation-duration: 1.1s;	
	-webkit-animation-duration: 1.1s;
	
	animation-timing-function: ease-out;	
	-webkit-animation-timing-function: ease-out;	
	
	transform-origin: 50% 100%;
	-ms-transform-origin: 50% 100%;
	-webkit-transform-origin: 50% 100%; 
}
#main .info {
    outline: 0;
    padding: 5px 12px;
    display: block;
    color: #9fa8b0;
    font-weight: bold;
    text-shadow: 1px 1px #1f272b;
    border: 1px solid #1c252b;
    border-radius: 3px;
    background: #232B30; 
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 

}
	@keyframes pullUp {
	0% {
	transform: scaleY(0.1);
	}
	40% {
	transform: scaleY(1.02);
	}
	60% {
	transform: scaleY(0.98);
	}
	80% {
	transform: scaleY(1.01);
	}
	100% {
	transform: scaleY(0.98);
	}				
	80% {
	transform: scaleY(1.01);
	}
	100% {
	transform: scaleY(1);
	}							
	}
  
  #main .plus, #main .plus2 {
    outline: 0;

    display: block;
    color: #9fa8b0;
    font-weight: bold;
    text-shadow: 1px 1px #1f272b;
    border: 1px solid #1c252b;
    border-radius: 3px;
    background: #232B30; 
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
}
    #main .plus:hover, #main .plus2:hover {
    background-position: 0 top;
    top: 1px;
    color: #fff;
    background: #20282D; 
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); 
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); 
}
  html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {

    font-size: 8pt;

}
}
