css:
@-moz-document url-prefix("https://news.google.com")
{
    	html, body {
        overflow-Y: auto;
    }

    header[role="banner"], div[role="banner"] {
        position: relative !important;
    }

    #yDmH0d {
        height: initial !important;
    }

    .pGxpHc {
        height: 120px;
    }

    .JRcPue main>div:first-child {
        max-width: initial;
    }

	main>div:nth-child(2) {
	  	position: relative;
	  	left: 0;
	  	opacity: 1;
        transition: width .5s, left .3s, opacity .8s;
    }

    .JRcPue main>div:nth-child(2) {
	  	min-width: initial;
        max-width: initial;
	  	width: 0;
	    overflow: hidden;
	  	left: 500px;
	    opacity: 0;
    }
}
