css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("erhoehtesbewusstsein.de") {

body {
	background-color: #000;
}

.main-container {
  background: #000;
}

#header {
	background: #000;
}

a:hover, .menu .current-menu-item > a, .menu .current-menu-item, .current-menu-ancestor > a.sf-with-ul, .current-menu-ancestor, footer .textwidget a, .single_post a, #commentform a, .copyrights a:hover, a, footer .widget li a:hover, .menu > li:hover > a, .single_post .post-info a, .post-info a, .readMore a, .reply a, .fn a, .carousel a:hover, .single_post .related-posts a:hover, .sidebar.c-4-12 .textwidget a, footer .textwidget a, .sidebar.c-4-12 a:hover {
	color: #00ff39;
  background-color: black;
}



.secondary-navigation a {
	color: #ffb400;
}

.secondary-navigation a:hover, .secondary-navigation .sfHover {
	background: #000;
	color: #009cff !important;
}

.frontTitle {
	background: #0000;
	color: #00C9FF;
	font-size: 19px;
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
}

article header {
	margin-bottom: 2px;
	overflow: hidden;
	color: #0042ff;
}

.readMore a:hover {
	color: #ffcf00;
	background-color: black;
  letter-spacing: 2px;
}

.post.excerpt .post-content p {
	margin-bottom: 0;
	color: #d400fe;
}

.title a {
	color: #2AFF00;
}

.title a:hover {
	color: #FFAB00;
}

.secondary-navigation a {
	border-right: 1px solid #C300FF;
	border-left: 1px solid #ff00f3;
  display:none;
}

.secondary-navigation a {
	border-right: 0px solid #E2E2E2;
	border-left: 0px solid #fff;
}


#navigation > ul li:first-child a {
	border-left: 0px solid #E2E2E2;
}

.menu .current-menu-item > a::after {
	content: "";
	position: absolute;
	width: 100%;
	height: 1px;
	background: #000;
	bottom: 0px;
	left: 0;
	z-index: 1;
}

h1 {
	font-size: 28px;
	line-height: 1.4;
	color: #005dff;
}
  
input, textarea, select {
	padding: 8px 12px;
	font-size: 13px;
	color: #54F800;
	border: 1px solid #0027FF;
	background-color: black;
}
  
#s {
	border: 1px solid #00D2FF;
  background: #000;
}

#searchform :hover {
	border: 1px solid #F00;
  background: #000;
}
  
.sbutton {
  color: black;
	background: url('http://www.erhoehtesbewusstsein.de/wp-content/themes/point/images/search.png') center no-repeat;
}

.sbutton:hover {
  color:black;
  background: url('http://fs5.directupload.net/images/170705/noasark3.png') center no-repeat !important;;
}
  
.copyrights {
  background: #000;
  color: #f00;
}

.post-info {
	color: #ffb400;
}  
  
div {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
	color: #0fff00;
}
  
h2 {
	margin-bottom: 12px;
	color: #F00;
	font-weight: bold;
	text-transform: uppercase;
}

}
