css:
@-moz-document url-prefix("https://authserver.szu.edu.cn/authserver/login") {
html {
    background: url('https://szdx.sinaapp.com/wechat/asset/ia_bg.jpg') no-repeat center center fixed; 
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    height: 100%;
    width: 100%;
    overflow: hidden;
}

body {
   font: 14px/18px tahoma,arial,'Hiragino Sans GB','\5b8b\4f53',sans-serif !important;
	width: 100%;
   height: 100%;
   margin: 0;
   padding: 0;
}

/* 屏蔽原来的背景、选择器、版权 */
.auth_bg,
.auth-language,
.auth_login_footer {
	display: none;
}

.auth_page_wrapper {
   float: left;
	width: 100%;
   height: 100%;
}

/* 去掉原来的 logo */
.auth_page_wrapper .auth_logo img {
	display: none;
}

.auth_page_wrapper .auth_logo {
   position: static !important;
   right: 0 !important;
   bottom: 0 !important;
	width: 100%;
}

.auth_page_wrapper .auth_logo:after {
    margin: 80px auto;
    margin-bottom: 0;
	content: '';
    display: block;
    height: 60px;
    width: 435px;
    background-image: url("http://cdn.iszu.cn/auththeme/szuialogo@2x.png");
    background-size: 435px 60px;
}

/* 登录框 */

.auth_login_content .auth_login_left {
	display: none;
}
.auth_login_content .auth_login_right {
	margin: 60px auto;
   width: 360px;
    float: none;
    position: static;
}

.auth_login_content .auth_login_right .auth_tab {
	 width: 100%;
    margin-left: 0;
    padding: 30px 10px 20px 10px;
    background: #FFF;
    border-radius: 6px;
    box-shadow: 0 0 5px rgba(0,0,0,.3);
    margin: 0;
    box-sizing: border-box;
}

.auth_login_content .auth_login_right .auth_tab:after {
    position: absolute;
    left: 0;
    margin-top: 20px;
    width: 100%;
    content: 'Design By Jason.';
    font-size: 12px;
    line-height: 30px;
    text-align: center;
    font-weight: normal;
    color: rgba(255,255,255,.8);
    text-shadow: 0 0 5px rgba(0,0,0,.6);
    display: block;
}

.auth_login_content .auth_login_right .auth_tab .auth_tab_links:before {
    color: #3464AE;
    font-size: 20px;
    line-height: 40px;
    font-weight: bold;
    user-select: none;
    content: '深圳大学统一身份登录';
    text-align: center;
	display: block;
}

.auth_login_content .auth_login_right .auth_tab .auth_tab_links ul {
	display: none;
}

.auth_login_content .auth_login_right .auth_tab .auth_tab_content {
	padding: 20px 35px;
}

.auth_login_content .auth_login_right .auth_tab .auth_tab_content .auth_login_btn {
	 position: relative;
    width: 100%;
    height: 40px;
    padding: 0;
    color: #FFF;
    text-align: center;
    line-height: 40px;
    font-size: 16px;
    font-weight: bold;
    display: block;
    background: #3464AE;
    border-radius: 3px;
    border: none;
}
}
