css:
@-moz-document domain("club.kdslife.com") {
.kds_main{
    width: 879px !important;
    margin: 0 auto;
    background: #F5F6F8;
    overflow: hidden;
}
.kds_search{
    width: 879px !important;
    height: 42px;
    background: #f4f2f2;
    overflow: hidden;
}
.w1200{
	width: 879px !important;
}
body {
	background-color: #d8d8d8 !important;
   background-image:url(https://i.loli.net/2019/04/07/5ca9796e3878f.gif) !important;
}
.main_List li.i2{
    background: #FEFEFE !important;
    color: #000;
    height:4;
    line-height: 44px;
    font-size: 12px;
}

.topic_title{
    height: 40px;
    background: #5e666c !important;
    line-height: 40px;
    color: #fff;
}
.wrap-form-search .form-send {
    width: 44px;
    height: 26px;
    outline-style: none;
    background-color: #5e666c !important;
    font-size: 12px;
    color: #fff;
    line-height: 25px;
}
.main_List li.example{
    background: #5e666c !important;
    color: #fff;
    height:40px;
    line-height: 40px;
    border-bottom:0;
    font-size: 12px;
}
.main_List li.i3{
    background: #5e666c !important;
    color: #fff;
    height:40px;
    line-height: 40px;
    font-size: 12px;
}
.i2 span a{
    display: inline-block;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;

}
select,input{
    font: 13px/1.5em  'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}
.mc{
	position: relative;
    padding:13px 8px 0 24px;
    font-family:'Helvetica Neue',Helvetica,Arial,sans-serif !important;
    font-size:14px;
    color:#333;
}
.kds_floorL a{
    font-size: 13px;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
    padding-left: 5px
}
.kds_floorR span,.kds_floorR i{
     font-size: 13px;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}
.topic_title h1{
    font-size:15px;
    font-family:'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}
.main_position div.main_positionR{
	height: 30px;
	line-height: 30px;
	width: 370px;
	text-align: right;
	font-family:'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}
.main_position div.main_positionL{
	height: 30px;
	line-height: 30px;
	width: 370px;
	font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}
.fn {
	position: relative;
	overflow: hidden;
	border-bottom: 1px #E6E6E6 solid;
	padding: 0 0 0 20px;
	height: 31px;
	line-height: 31px;
	font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}

.detail-content{
    font:14px/180% 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
    border-bottom: 1px solid #e6e6e6;
}

.reply tr .reply_th{
    position: relative;
    background: #45A4D8;
    padding: 0 19px 0 14px;
    width: auto;
    font-weight: 700;
    color: #FFF;
    text-align: left;
    word-break: break-all;
    font-family:'Helvetica Neue',Helvetica,Arial,sans-serif !important;
    font-size: 14px;
    height: 40px;
    line-height: 40px;
}


.reply th {
    padding: 16px 0px 7px 19px;
    border: 1px #E6E6E6 solid;
    text-align: left;
    word-break: break-all;
    color: #000;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
    font-weight: normal;
    width: 124px;
}

.reply td {
    padding: 16px 0px 7px 12px;
    border: 1px #E6E6E6 solid;
    text-align: left;
    word-break: break-all;
    color: #000;
    font-family:'Helvetica Neue',Helvetica,Arial,sans-serif !important;
}
body, button, input, select, textarea{ font:14px/1.5em 'Helvetica Neue',Helvetica,Arial,sans-serif !important;}
.detail_master a{
    font-size: 13px;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif !important;
    color: #333;
    padding-left: 5px;
}
.reply tr .reply_th{
    position: relative;
    background: #5e666c !important;
    padding: 0 19px 0 14px;
    width: auto;
    font-weight: 700;
    color: #FFF;
    text-align: left;
    word-break: break-all;
    font-family: 'simsun';
    font-size: 14px;
    height: 40px;
    line-height: 40px;
}
#main{
    width: 879px;
    min-height: 300px;
    background: #f4f7f7 !important;

}
.kds_search{
    width: 1200px;
    height: 42px;
    background: #f4f7f7 !important;
    overflow: hidden;
}
.main_tabPoL a{
   float: left;
   width: 96px;
   height: 40px;
   background: #A2A9AF !important;
   color: #E7E5DF !important;
   line-height: 40px;
   text-align: center;
   margin-right: 2px;
   font-weight: 700;
}
.main_tabPoR a.tob_grey{
    background: #4C6085 !important;
    border:0px solid #4C6085 !important;
    border-radius: 2px;
    color: #ffffff !important;
    margin-right: 0;
}
.blue{
    color: #4C6085 !important;
}
.item {
    overflow: hidden;
    width: 838px;
    border: 1px #E6E6E6 solid !important;
    border-bottom: none;
    background: #FEFEFE !important;
}
.main_tabPoR a.tob_Blue{
    background: #4C6085 !important;
    border:0px solid #cac5d8 !important;
    border-radius: 2px;
    color: #ffffff !important;

}
.main_info{
    padding-left: 107px;
    *padding-left: 75px;
    margin-top: 10px;
    margin-bottom: 10px;
    height: 35px;
    line-height: 35px;
    background: #FEFEFE !important;
    font-size: 12px;
    margin-right:19px;
    margin-left: 20px;
}
.kdsModerator{
    padding-left: 20px;
    background: url(https://i.loli.net/2019/04/07/5ca97ac307091.png) no-repeat 0 50% !important;
    margin-right: 8px;
}
.author strong {
	display: block;
	font-size: 15px;
	color: #4C6085 !important;
}
.louzhu i{
  display:block;
  width:40px;
  height:40px;
  position:absolute;
  top:0;
  right:0;
  background:url(https://i.loli.net/2019/04/07/5ca979bfefab9.png) no-repeat !important;
  z-index:20;
}
.replyBtn {
    background: #4C6085  !important;
    border: none;
    border-radius: 2px;
    font: 400 14px/30px 'microsoft yahei';
    color: #FFF;
    width: 100px;
    cursor: pointer;
}
.wrap-form-search .form-txt {
	width: 212px;
	height: 24px;
	border: 1px solid #5e666c !important;
	outline-style: none;
	background-color: #F4F7F7 !important;
	padding-left: 8px;
	line-height: 25px;
	margin-right: 10px;
}

.wrap-form-search select {
	height: 26px;
	border: 1px solid #5e666c !important;
	outline-style: none;
	background-color: #F4F7F7 !important;
	font-size: 12px;
	margin-right: 10px;
	position: relative;
}
.toRed{
    color: #5e666c !important;
    font-weight: 700;
}
.reply td a.re_red{
    color:  #5e666c !important;
    font-weight: 700;
}
}
