css:
@-moz-document domain("portal-dev.stcshared.com")
  {

/* Lighter background */
.tools, .table, .table thead th, .table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
    background-color: #39475B !important;  
}

/*  */
pre, code, .input-block-level {
   background-color: #131B26 !important;   
}

.table tbody tr:first-child {
    border-top: 6px solid #ddd;   
}

body {
    background-image:none !important;
    background-color: #131B26 !important;
}

.navbar-inner {
	background-color: #0F1621 !important;  
    background-image: none !important;
}

.header .bar {
    border-bottom: 1px solid #77869D !important;  
}

/* Text Color */
.profiler-number, .profiler-unit,
dl dt, .table th, .table td, .heading {
    color: #77869D !important;
}

/* Lighter text color */
pre, code, .input-block-level {
	color: #aab9d0 !important;   
}

.profiler-button  {
    background-color: #39475B !important;
}

.main-container h3 {
	color: #77869D !important;
    border-bottom: 1px solid #77869D;
}

hr {
    border-top: 1px solid #77869D;
    border-bottom: 1px solid #77869D;   
}

a {
    color: #8ca5c7 !important;  
}

.btn-info {
    background-color: #39475B !important;
    background-image: none !important;
}

.btn-danger {
	background-color: #964d4a !important;
    background-image: none !important;
}

.well {
	background-color: #39475B !important;  
}

.footer-container {
	display: none !important;   
}
}
