css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("steemit.com") {
  
  body
  {
    background-color: #1D1F21;
    color: #C4C4C4;
  }
  
  div.columns
  {
    background-color: #1D1F21;
  }
  
  .Header .menu
  {
    background-color: #1D1F21;
  }
  
  .Header__sub-nav li > a
  {
    background-color: #1D1F21;
  }
  
  .TagList__horizontal a
  {
    background: #39393E none repeat scroll 0% 0%;
  }
  
  .TagList__horizontal a:hover
  {
    background: #505056 none repeat scroll 0% 0%;
  }
  
  .Comment__header-user 
  {
    color: white;
  }
  
  .PostSummary 
  {
    background-color: #1D1F21;
  }
  
  .PostSummary__header > h3 > a
  {
    color: #C4C4C4;
  }
  
  .PostsList__post_container .PostFull
  {
    background-color: #1D1F21;
  }
  
  .PostsList__post_container, .PostsList__post_top_bar
  {
    background-color: #1D1F21;
  }
  
  .Author__dropdown
  {
    background-color: #28282E;
  }
  
  .Author__dropdown .Author__name  
  {
    color: #8A8A8A;
  }    
  
  .VerticalMenu > li > a
  {
    background-color: #28282E;
    color: #8A8A8A;
  }
  
  .VerticalMenu > li > a:hover
  {
    background-color: #39393E;
  }
  
  .menu.vertical > li
  {
    background-color: #28282E;
  }
  
  textarea[rows]
  {
    background-color: #28282E;
    color: #8A8A8A;
  } 
  
}
