css:
@-moz-document domain("www.tumblr.com") {
.messaging-conversation-popovers .messaging-conversations-container.messaging-minimized-conversations {
    right: 0;
    top: 116px;
}

.messaging-conversation-popovers.has-open .messaging-conversations-container.messaging-open-conversations {
    position: fixed;
    right: 0;
    height: calc(100% - 54px);
    min-height: 0;
    max-height: 100%;
    width: calc((100% - 990px) / 2 + 336px);
    min-width: 297px;
}

.messaging-conversations-container.messaging-open-conversations .popover--conversation-popover {
    width: 100%;
}

.messaging-conversation-popovers.has-open .messaging-conversations-container.messaging-open-conversations .popover--conversation-popover .messaging-conversation-wrapper .messaging-conversation .conversation-main .tx-scroll .conversation-messages .message-list {
    width: 100%;
    overflow-x: hidden;
}

.messaging-conversation--open .conversation-message-text .message-bubble, .messaging-conversation--open .conversation-message-text .full-width-bubble,
.messaging-conversation--open .message-container.has-thumbnail .message-bubble{
    flex: 0 1 auto;
    -ms-flex: 0 1 auto;
}

.thumbnail img, .thumbnail a img {
    width: 100%;
    height: auto;
}

.thumbnail {
    width: 250px;
}

.message-container.has-thumbnail .message-bubble {
    width: 230px;
}

.messaging-conversation.messaging-conversation--open {
    border-radius: 0;
}

.conversation-header .conversation-header-main .title {
    white-space: normal;
    line-height: normal;
}

.conversation-message .conversation-message-timestamp .inline-activity.timestamp {
    text-align: left;
    padding-left: 50px;
}

.conversation-messages.tx-scroll-container--vertical.tx-scroll-container .conversation-pill {
    width: 100%;
}

.conversation-messages.tx-scroll-container--vertical.tx-scroll-container--horizontal.tx-scroll-container div .conversation-icebreakers {
    width: 100%;
}
}
