css:
@-moz-document domain(meta.eveonline.com){
span.subcategory {
    display: table-row;
}
.topic-avatar img.avatar {
    height: 75px;
    width: 75px;
    border-radius: 0;
}
.topic-avatar {
    width: 75px !important;
}
section.embedded-posts.bottom .topic-avatar img.avatar {
    height: 45px;
    width: 45px;
    border-radius: 50%;
}
section.embedded-posts.bottom .topic-avatar {
    width: 45px !important;
}
section.embedded-posts.top .topic-avatar img.avatar {
    height: 45px;
    width: 45px;
    border-radius: 50%;
}
section.embedded-posts.top .topic-avatar {
    width: 45px !important;
}
}
