css:
@-moz-document domain(www.automarket.ro) {

#header_billboard,
#ad_spacer,
#search_home,
#banda_footer,
#footer .top,
.hp_blog_box,
.adlink-banner,
.article_drive_test .drive_test_tag,
.adsbygoogle,
.box_stiri_similare,
.list_box_top_stiri a .poza,
.box_news ul li .poza,
.drive_test_home li .title,
.hp_top_stories .top_storie .subtitle,
.article_drive_test .gallery .nav,
.article_drive_test .article_utils { display:none!important}
body { background:#f3f3f3; line-height:1.5!important; font-size:14px; font-family:Roboto}
h1, h2, h3, h4, h5, h6 { line-height:1.3!important}
#main_container,
#header_v5 .top_menu {width:1140px;}
#footer {width:1080px; padding:30px; background: #26272B;}
.hp_top_news .top_news_item { width:530px;}
.hp_top_news .top_news_image a img,
.hp_top_stories .top_storie .image img,
.hp_news .news_image a img,
.article_drive_test .main_image img,
.article_drive_test .right_image img,
.article_layout_2 .article_gallery_slide img,
.listare_stiri .stire .poza img,
.drive_test_index .drive_test_list .poza img {width:100%; height:auto;}
.hp_top_stories .top_storie { width: 346px; }
.hp_top_stories .top_storie .image a { width:auto; height:auto;}
#footer .bottom {font-size:12px;}
.hp_left_col {  width: 730px; }
.hp_news .news_image { width: 235px; height: auto;  margin-right: 27px;}
#container {  background: #fff; padding: 30px; }
#left {  width: 740px; }
.article_drive_test .main_image { width: auto;  height: auto; }
.article_drive_test .overlayer {width:100%; height: 130px; background: -moz-linear-gradient(top, rgba(0,0,0,0.65) 0%, rgba(0,0,0,0) 100%); background: -webkit-linear-gradient(top, rgba(0,0,0,0.65) 0%,rgba(0,0,0,0) 100%);background: linear-gradient(to bottom, rgba(0,0,0,0.65) 0%,rgba(0,0,0,0) 100%); }
.article_drive_test .top_description .versiune {color:#fff}
.article_drive_test .article_content { padding:20px 0}
.article_drive_test p {font-size:16px; line-height:1.6}
.article_drive_test .description_title h2 {font-size:20px;}
.article_drive_test .description_title { margin:30px 0 15px 0}
.article_drive_test .right_image { width:300px;}
.article_layout_2 {padding:0}
.article_layout_2 .image_slider,
.article_layout_2 .article_slide_images,
.article_layout_2 .article_gallery_slide{width:100%; height:auto}
.article_layout_2 .entry p {font-size:16px; line-height:1.6}
.article_layout_2 .image_slider, .article_layout_2 .article_slide_images, .article_layout_2 .article_gallery_slide { height:513px;}
.article_layout_2 .nav { width: 700px;}
.content_container {padding:0;}
.title_grad {background:none; padding:0; margin:0 0 20px 0}
.listare_stiri .stire .poza {width:250px; margin-right:25px;}
.listare_stiri .stire h3 {font-size:20px;}
.article_drive_test .general_rating { top: 4px; background: none }
.box_comment_list ul li .right {width:500px;}
.box_post_comment {  border:none; background: #fff; padding: 30px 0; }
.hp_news_header .news_channels li {font-size:12px;}
.hp_top_stories .top_storie h2,
.hp_news .news_item h3{font-size:20px;}
.hp_video .video_item {width:232px;}
.hp_video .video_image a img {width:266px; height:auto; margin-top:-10px }
.hp_video .video_image a {width:232px; height:147px; overflow:hidden}
.article_layout_2 h1 {font-size:50px; line-height:1.1!important}
.article_layout_2 .widget_newsletter,
.article_layout_2 .widget_esential{  background: #fff; border-radius:0; box-shadow:none; padding-right:10px; border-right:2px solid #ccc  }
.article_layout_2 .widget_newsletter .inner {padding:0;}
.heading,
.box_top_stiri,
.box_tags{background-image:none!important;}
.box_news ul li .body {width:auto; float:none}
.small {font-size:12px;}
.drive_test_index .drive_test_list li { width: 240px;}
.drive_test_index .drive_test_list .item_rating {background:#333; top:0;}
.drive_test_home ul li .data {padding:0;}
.drive_test_home ul li {margin-bottom:25px;}
.item_rating,
.hp_top_stories .top_storie .rating.hp_drivetest {background:#333; }
.drive_test_home .item_rating,
.hp_top_stories .top_storie .rating.hp_drivetest,
.dt_segment_categ .item_rating {top:0}
.hp_top_news .top_news_info .channel_url,
.hp_news .news_info .channel_url { text-transform: uppercase;    background: #007BC6;    color: #fff;    display: inline-block;    padding: 2px 8px 0px;    border-radius: 2px;}
.btn_more_news a { padding: 15px;  border: 1px solid #007BC6;  background: #fff;}
.listare_stiri .stire { border-top:none; margin-bottom: 20px;}
.box_border .content,
.list_box_top_stiri a{padding:5px 0;}
.box_similar_models .inner_scrollbox { height: 433px; }
#right .content_container { padding-bottom:0;}
.article_drive_test .view_all,
.article_drive_test .gallery{padding:10px 0}
.article_drive_test .carousel-clip-region {width:auto;}
.article_drive_test .autor {font-size:12px}
.article_drive_test p {margin-bottom:25px;}
.article_drive_test .box_costs {border-radius:2px}
.article_drive_test .cat_rating {  margin: 7px 0px 0px 7px;}
.article_drive_test .description_title {  margin: 30px 0 25px 0;}
.breadcrumbs { margin-bottom: 25px;}
.drive_test_index .header {font-size:32px; background:none; margin:0 0 25px 0}
.drive_test_index .header h1 {font-size:32px; background:none ;padding:0; margin:0;}
.content_container .search {border:1px solid #ccc; text-align:center; border-radius:2px; padding:15px; margin:0 0 25px 0 }
.content_container .search select {font-size:16px; padding:10px 15px; border:1px solid #ccc; border-radius:2px;}
.content_container .search *,
.content_container .search span{ float: none; display:inline-block; font-size:16px;}
.article_layout_2 .meta {border:none}
.dt_segment_categ ul li .data {padding-top:0;}

}
