@-moz-document domain("youtube.com"), domain("apis.google.com") {
/* Created by TheSxW */
/*Date: 07.07.2017*/
/*Updated: 24.10.2018*/
/*#00ffff -> aqua*/

  body, div, span, b, table, #content, .content.ytd-video-secondary-info-renderer, yt-formatted-string,ytcp-ve div
  {
    color: aqua!important;
  }

  a,[class*="C2KYWWB"],h1, h2, h3, h4, h5, h6, h7, h8, h9
  {
    color: #00ffff!important;
  }

  a:hover
  {
    color: #00b9b9!important;
  }

  img
  {
    background-color: transparent!important;
    border: 1px solid #003333;
  }

  input
  {
    border-radius: 8px;
    padding-left: 15px!important;
    width: 99%!important;
    max-width: 480px!important;
    color: #00ffff!important;
  }
/*Tylko Kolor czarny background*/

  #container, #page-manager, ytd-watch, #sections, #header, paper-menu, #footer, #content, #creator-page-sidebar, #creator-page-content, .ytg-fl.account-container, #body-container, #footer-container, a.yt-uix-button, #yt-masthead-container, #yt-alert-panel, #yt-lang-alert-content, #yt-lang-alert-container, .yt-alert-panel, #ticker-content, .yt-consent.yt-consent-banner.clearfix, #guide-container,#appbar-nav,.yt-ui-ellipsis,#feed-main-what_to_watch,.yt-card,#yt-masthead-notifications-clickcard,.yt-dialog-fg, .yt-uix-overlay-fg,.yt-uix-overlay-actions,.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal,.channel-header .secondary-header-contents,#contentTable,#appbar-content,#appbar-guide-menu,.yt-uix-clickcard-card,.yt-uix-clickcard-card-body,#yt-masthead-multilogin,.yt-masthead-picker-footer,ytd-active-account-header-renderer,paper-item,ytd-background-promo-renderer,ytd-multi-page-menu-renderer,#primary,#secondary,ytd-rich-metadata-renderer,ytcd-channel-dashboard,ytcd-ideas-card,ytcd-card,ytcd-carousel-card,ytcd-video-card,ytcp-ve
  {
    background: black!important;
  }

  #search-icon-legacy.ytd-searchbox, ytd-button-renderer #button.ytd-button-renderer, ytd-browse, #header, app-header-layout, ytd-playlist-sidebar-renderer, #contents, ytd-alert-renderer, .header, ytd-playlist-panel-video-renderer, iron-list, ytd-simple-menu-header-renderer, .sbdd_b, .sbsb_a, .sbsb_b, #tabs-inner-container, #channel-container, #channel-header, #watch7-sidebar-contents, #watch-header, #action-panel-details, #watch-discussion, table, li, ul, .yt-uix-menu-content.yt-ui-menu-content.yt-uix-kbd-nav, .yt-subscription-button-subscriber-count-branded-horizontal.yt-subscriber-count,#yt-masthead-notifications-content,li,#yt-masthead-notifications-content .item-section>li>.yt-lockup-tile, #yt-masthead-notifications-content .yt-ui-ellipsis,.yt-masthead-account-picker-option,.nbc,.ytcp-navigation-drawer-0 .thumbnail-wrapper.ytcp-navigation-drawer,ytcp-animatable,[id*="metric"],.ytcd-video-card
  {
    background-color: #000!important;
    background: #000!important;
  }

/*Szary kolor background*/

  .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus,.sbsb_c.gsfs.sbsb_d, .style-scope.yt-next-continuation, button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-primary, #masthead-search-terms,.related-channels-editor:hover,[class*="C2KYWWB"],.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header, .yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header,.ytcp-navigation-drawer-0 #main-menu.ytcp-navigation-drawer paper-icon-item.iron-selected.ytcp-navigation-drawer:not(.bottom-section-entry), .ytcp-navigation-drawer-0 #main-menu.ytcp-navigation-drawer paper-icon-item.iron-selected.ytcp-navigation-drawer:not(.bottom-section-entry) iron-icon.ytcp-navigation-drawer
  {
    background-color: #222!important;
    background: #222!important;
  }

/*radius i border 1ps aqua*/

  ytd-button-renderer #button.ytd-button-renderer, button[is='paper-icon-button-light'].ytd-masthead
  {
    border-radius: 50px!important;
    border: 1px solid aqua;
  }

  #alerts
  {
    border-radius: 15px!important;
    border: 1px solid aqua;
  }

  ytd-button-renderer #button.ytd-button-renderer:hover,.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header, .yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header
  {
    color: #00b9b9!important;
    border: 1px solid #00b9b9;
  }

  #search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox, button
  {
    color: aqua!important;
  }

  paper-menu, #search-icon-legacy.ytd-searchbox, ytd-searchbox[mode=legacy] #container.ytd-searchbox, .ytg-fl.account-container,ytd-multi-page-menu-renderer
  {
    border: 1px solid #00ffff;
  }

  .sbib_c[dir=ltr],.yt-uix-button
  {
    background: #00b9b9!important;
    border-radius: 15px!important;
    border: 2px solid #222!important;
  }

  button[is='paper-icon-button-light'].ytd-masthead, ytd-topbar-logo-renderer, #guide-button.ytd-app, #logo-container
  {
    background-color: hsla(185, 60%, 50%, .6)!important;
  }

  .yt-dialog-base
  {
    background-color: hsla(0, 0%, 0%, .4)!important;
  }

  .yt-uix-button-icon,.yt-uix-button-empty .yt-uix-button-icon-wrapper
  {
    background-color: hsla(185, 60%, 50%, .8)!important;
    border-radius: 5px;
  }

  ytd-topbar-logo-renderer, #logo-container
  {
    padding: 8px 25px 8px 15px!important;
    border-radius: 50px!important;
  }
/*subscribe*/

  paper-button[subscribed].ytd-subscribe-button-renderer, paper-button.ytd-subscribe-button-renderer, .yt-uix-button.yt-uix-button-size-default.yt-uix-button-subscribe-branded.yt-uix-button-has-icon.no-icon-markup.yt-uix-subscription-button.yt-can-buffer .yt-uix-button-subscribed-branded, .yt-uix-subscription-button, .yt-uix-button-size-default.yt-uix-button-default.yt-uix-button-empty.yt-uix-button-has-icon.yt-uix-subscription-preferences-button.yt-uix-subscription-notifications-highlights,.guide-item.guide-item-selected
  {
    background: darkred!important;
  }
/*reszta nie otagowana*/
  .appbar-guide-menu-layout
  {
    margin-top: 65px!important;
  }

  #masthead-positioner
  {
    position: fixed!important;
  }

  #logo.ytd-masthead
  {
    width: auto!important;
  }

  #tabs-inner-container
  {
    border-bottom: 2px solid #00b9b9 !important;
    border-top: 2px solid #00b9b9 !important;
  }

  #spinner
  {
    padding-left: 20px!important;
    padding-right: 20px!important;
    border-radius: 15px!important;
    background: #002222!important;
  }

  .channel-header .secondary-header-contents
  {
    margin-top: 10px!important;
  }
/*fix some yt shet designers*/
  #yt-masthead-notifications .yt-uix-button-icon-bell
  {
    left: 0px!important;
    width: 25px!important;
  }

  #yt-masthead-user #sb-button-notify, #yt-masthead-user #yt-masthead-notifications-button
  {
    width: 25px!important;
  }

  .yt-uix-button-empty .yt-uix-button-icon-wrapper
  {
    padding: 5px!important;
  }
/*Cinematic wide video screen*/
/*Cinematic wide video screen*/
/*Cinematic wide video screen*/
/*Cinematic wide video screen*/

  .watch-stage-mode .player-width, .watch-stage-mode #player
  {
    width: 100% !important;
    width: calc(100vw - 17px) !important;
    /* Compensating for the right scrollbar */
    margin-left: 0 !important;
    left: 0 !important;
  }

  .watch-stage-mode #placeholder-player .player-width
  {
    width: 100% !important;
  }

  .watch-stage-mode .ad-container, .watch-stage-mode .html5-video-container, .watch-stage-mode video.video-stream.html5-main-video
  {
    left: 0 !important;
    top: 0 !important;
    width: 100% !important;
    height: 100% !important;
  }

  .watch-stage-mode .html5-video-container > .video-ads > .ad-container
  {
    /* Fix for ads bottom bar at the old player. */
    bottom: 30px !important;
    height: auto !important;
  }

  .watch-stage-mode .ad-container.ad-overlay
  {
    /* Fix for banner-style ads. */
    top: auto !important;
    height: auto !important;
  }

  .watch-stage-mode #player-playlist .watch-playlist
  {
    /* Fixing the playlist sidebar (when watching a video from a playlist). */
    top: calc(100vh - 52px - 18px - 360px) !important;
    height: 480px !important;
  }

  .watch-stage-mode .ytp-chrome-bottom
  {
    /* Video control bar for the new player. */
    width: 95% !important;
    width: calc(100% - 24px) !important;
  }
/*body::-webkit-scrollbar {
    width: 12px !important;
  }
  body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
  }
  body::-webkit-scrollbar-thumb {
    background-color: darkgrey;
    outline: 1px solid slategrey;
  }*/

@media (max-aspect-ratio: 15/9) and (max-width: 1015px){
    .watch-stage-mode .player-height
{
        /* (9 / 16 = 0.5625) 0.5565 for the aspect ratio */
        /* -12px for the bottom scrollbar */
        /* -50px for the top bar */
        
        height: calc(100vw * 0.5565 - 17px - 50px) !important;
  }
}

@media (max-aspect-ratio: 15/9) and (min-width: 1015px)
{
  .watch-stage-mode .player-height
  {
        /* (9 / 16 = 0.5625) 0.5565 for the aspect ratio */
        /* -50px for the top bar */
    height: calc(100vw * 0.5565 - 50px) !important;
  }
}

@media (min-aspect-ratio: 15/9) and (max-width: 1015px)
{
  .watch-stage-mode .player-height
  {
        /* -50px for the top bar */
        /* -12px for the bottom scrollbar */
    height: calc(100vh - 17px - 50px) !important;
  }
}

@media (min-aspect-ratio: 15/9) and (min-width: 1015px)
{
  .watch-stage-mode .player-height
  {
        /* -50px for the top bar */
    height: calc(100vh - 50px) !important;
  }
}
/* Handle top bar */
/* Fix the top bar to the top of the page */
}
