css:
@-moz-document domain("nobelium.xyz") {
.navbar-light.bg-faded {
	background: linear-gradient(135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    border-bottom: none;
    border-bottom-left-radius: 10px;
	border-right: none;
    border-left: none;
  	border-top: none;
    border-bottom-right-radius: 10px;
    box-shadow: 0px 2px 3px rgba(105, 105, 105, 0.72)!important;
}

.alert {
	display: none;
}

body {
	background: none;
}

.card-block {
	border: none!important;
}

.footer>.container {
	background: linear-gradient(135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    color: white;
}

.footer>.container a {
	color: white;
}

.btn-danger {
    background: white;
    border: none;
    border-radius: 10px;
    color: black!important;
}

.custom-control-input:checked~.custom-control-indicator {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    border: 1px solid white;
}

.page-item {
	color: black!important
}

.text-muted {
	color: white!important;
}

.page-item.active .page-link {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%)!important;
    border: none;
}

.game-card-vote .vote-bar {
	color: white!important;
}

.game-card-name-secondary {
	color: white!important;
}

.btn-danger:hover {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    color: white!important;
    border: none;
}

.btn-secondary {
	color: black!important;
}

.btn-secondary:hover {
	color: white!important;
}

.card-header {
	background-color: white;
    border-bottom: none;
    color: black!important;
}

.btn-success {
	background: white;
    border: none;
    border-radius: 10px;
    color: black!important;
    
}

.btn-success:hover {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    color: white!important;
    border: none;
}

.btn-primary {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    color: black;
    border: none;
    color: white;
    border-radius: 10px;
}

.btn-info {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    border: none;
    border-radius: 10px;
}

.btn-secondary {
	border: none;
    border-radius: 10px;
}

.btn-secondary:hover {
	background: linear-gradient(-135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    border: none;
    color: white;
}

.card-block a {
	color: white;
}

.trustedText {
	color: white;
}

.text-success {
	color: white!important;
}

.card-block {
	background: linear-gradient(135deg, rgba(221,0,255,1) 0%, rgba(162,0,255,1) 100%);
    border-bottom-left-radius: 10px;
    color: white!important;
}

.navbar-light a {
	color: white!important;
    font-weight: 700;
    border-bottom: none;
}

.navbar-light a:hover {
    text-shadow: 0px 2px 3px rgba(0, 0, 0, 0.72)!important;
    transition: linear 0.3s;
}
}
