css:
@-moz-document url-prefix("https://www.youtube.com/watch"), url-prefix("http://www.youtube.com/watch") {
ytd-watch-flexy[theater] #player-theater-container.ytd-watch-flexy, ytd-watch-flexy[fullscreen] #player-theater-container.ytd-watch-flexy{
    height:100vh;
    max-height: 100vh;
}

ytd-watch:not([fullscreen])[theater] #player.ytd-watch{
    max-height: 100vh;
}

ytd-watch[theater] #player.ytd-watch {
    width: 100%;
    max-height: 100vh;
}

ytd-watch-flexy[flexy_][flexy-v2_][is-two-columns_] #columns.ytd-watch-flexy{
    margin-top:50px;
}

#masthead-container.ytd-app {
    position: relative;
}

#page-manager.ytd-app {
   margin-top:0;
}

#container.ytd-masthead {
    height: auto;
}

#page-manager.ytd-app {
    margin-top: -40px;
}
}
