css:
@-moz-document domain(tweakers.net) {

/* Universal */

body {
    background-color: #FAFAFA;
}
div.wrap {
    background-color: #FAFAFA;
}
#contentArea {
    background-color: #FAFAFA;
    margin-bottom: 0;
    padding-bottom: 0px;
    padding-top: 20px;
}
#layout {
    height: 100%;
}
.mainColumn.frontpage {
    border-right: 0px solid #e6e6e6;
    margin-right: 0;
    padding-right: 0px;
    padding-top: 15px;
    width: 659px;
}
h1,
h2,
h1 a,
h2 a {
    color: #2D2D00;
}

/* Menu */

#menubar {
    background: #2D2D2D;
    background-image: none;
    border-bottom: 0px solid black;
    box-shadow: none;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
#menu > ul > li.active a {
    color: #fff;
    background-color: #a61e39;
}
#searchbar {
    background: #333333;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    margin-bottom: -1px;
}

/* Unordered */
.fpaTitle h2 {
    font-size: 19px;
    line-height: 28px;
    color: #2d2d2d;
}
.fpaItem a {
    color: #2d2d00;
}
#tracker .fakeTop > div {
    width: 22px;
    height: 38px;
    position: absolute;
    bottom: -50px;
    right: -16px;
    cursor: pointer;
    display: none;
}
body.hasTracker #tracker {
    display: none
}
body.hasTracker {
    margin-left: 0;
    position: relative;
}
.frontpageAdItem,
.textadContainer,
.leaderBoard,
.relatedJobs,
.socialcount,
#menubottombar,
#trackerStretch { /* DELETED */ }
#ticker,
.social,
.wvhj,
#true {
    display: none !important;
}
#search .fancyButton.abo {
    display: none;
}
.fpaItem+.fpaItem {
    width: 300px;
}
.fpaItem {
    border-right: 0px solid #e6e6e6;
    float: left;
    overflow: hidden;
    padding: 9px 2px 19px 0;
    position: relative;
    width: 300px;
}
.fpaTitle h2 {
    color: inherit;
    font-size: 19px;
    line-height: 28px;
    text-align: center;
}
.fpaTitle {
    height: 59px;
    overflow: hidden;
}
.fpaTitle p {
    font-size: 16px;
    line-height: 22px;
    text-align: center;
}
#categoryBar {
    height: 42px;
    margin-bottom: 17px;
    margin-top: 4px;
    background-color: #ffffff;
    border-radius: 2px;
    padding: 10px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
.fpaItemsWrapper {
    border-bottom: none;
    overflow: hidden;
}
.fpaItemsWrapper.greyTopBorderBlock {
    border-top: none;
    background-color: #ffffff;
    border-radius: 2px;
    padding: 10px;
    padding-right: 2%;
    padding-left: 2%;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
table.highlights {
    border-top: 1px solid #e6e6e6;
    margin-bottom: 14px;
    table-layout: fixed;
    width: 100%;
    background-color: #ffffff;
    border-radius: 2px;
    padding: 10px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}

/* Little Fixes */

#poll p, #poll dl {
    line-height: 19px;
    padding-bottom: 10px;
}
  
.greyTopBorderBlock {
   border-top: none;
}

#menu>ul>li.more.dropdown {
    background: #fafafa !important;
    border-left: 1px solid #ccc;
}

/* Menu Items */

#logo:hover, #menu>ul>li:not(.selected):hover {
    background: #881133;
    border-bottom: 1px solid #881133;
    height: 43px;
}
#menu > ul > li.active a {
    color: #fff;
    background-color: #881133;
}

/* Buttons */

.fancyButton {
    background: #a61e39;
    border: 0px solid black;
    background-image: none;
}
.fancyButton:hover {
    background: #2d2d2d;
}

/* Cards */

.darkFpBlock {
    background-color: #ffffff;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    background-image: none;;
    border: 0px solid #d9d9d9;
}
  
#recentlyTested {
    background-color: #ffffff;
    border-radius: 2px;
    padding: 10px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
  
.highlightedProductsWrapper {
    background-color: #ffffff;
    border-radius: 2px;
    padding: 10px;
    padding-left: 7%;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
  
#community {
    background-color: #ffffff;
    border-radius: 2px;
    padding: 10px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}


/* Options */

#bestBuyGuides{
 	display: none;   
}
#news .more {
 	display: none;   
}

#poll {
    display: block;
}

#categoryBar {
   display: block;
}


  

  



  

  

  








/* Responsive Homepage */

@media screen and (max-width: 1000px) {
    .mainColumn.frontpage {
        width: auto;
        margin-right: 10px;
	}
    
    .mainColumn.frontpage #groupedContent, .mainColumn.frontpage #newsletter, .mainColumn.frontpage #community, .mainColumn.frontpage #b_ad { /* DELETED */ }
    
    #categoryBar {
    	display: none;
    }
    
    .secondColumn.frontpage {
		float: none;
        width: 100%;
        margin-right: 10px;
	}
    
    .secondColumn .fpItem, .secondColumn #b_re { /* DELETED */ }
}

#menubar {        background: #252525 !important;    background-color: #252525 !important;}#searchbar {    background: #444444;}
}
