@-moz-document domain("selly.gg") {
/**

Thanks for downloading! Feel free to modify anything you want below :)
Last update: version 1.1.1 on September 13th, 2017.

- More bright things that Selly added now un-brightened

/* Text Color */
body, text, a, li, h3, p {
	color: #eee !important;
	fill: #eee !important;
}

body {
	background: #3c3c3c;
}

/* Main Background */
.main-content {
    background: #3c3c3c;
}

/* Containers */
.panel {
    background-color: #5d5d5d;
}

/* Tables */
.order-row {
	background-color: #666;
}

.table-striped>tbody>tr:nth-child(odd)>td,.table-striped>tbody>tr:nth-child(odd)>th { 
	background-color:transparent
}

.table-striped>tbody>tr:nth-child(even)>td,.table-striped>tbody>tr:nth-child(even)>th {
	background-color:rgba(243,247,249,0.1)
}

/* Notifications */
.panel>.list-group:last-child .list-group-item:last-child, .panel>.table-responsive:last-child>.table:last-child, .panel>.table:last-child {
    background-color: #777;
}

.panel-heading {
	border-color: #757575;
}

.header .notifications .panel-heading {
    background-color: #848484;
}

/* Item Overview */
.panel.main {
    background-color: #444;
}

.theme-two>.user-info {
    background: #555;
}

/* Modify Product */
.md-header.btn-toolbar {
    border: 0;
}

.bottom-about {
    background: #505050;
}

.product-tile {
    color: #ffffff;
    background: #4e4e4e;
}

/* Markdown */
.btn-markdown {
    background: #696969;
    border-radius: 0px;
    color: #a7a7a7;
}

.btn-markdown:hover {
	background: #777;
}

.form-control[disabled], .form-control[readonly] {
	background-color: rgba(72, 72, 72, 0.48);
}

/* Input Boxes */
.form-control {
    background-color: #444;
    border: 0;
	color: white;
}

.header .notifications .panel-heading {
	border-bottom: 0;
}


input::-webkit-input-placeholder {
	color: grey;
}
/* Selective */
.selectivity-multiple-input-container {
	background-color: #444;
	border: 0;
}

.selectivity-dropdown {
    background-color: #4e4e4e;
}

.selectivity-result-item {
    border-bottom: 0
}
.selectivity-result-item.highlight {
	color: white;
	background-color: #666;
}
.selectivity-dropdown:before {
    border-color: transparent transparent #4e4e4e;
	-webkit-filter: drop-shadow(0 -1px 0 #222);
    filter: drop-shadow(0 -1px 0 #222);
}
.selectivity-single-select {
    background-color: #444;
    border: 0;
}
.selectivity-single-selected-item {
    color: #ffffff;
}
.selectivity-dropdown {
    background-color: #4e4e4e;
    border: 0;
}
/* Income Chart */
.highcharts-background {
    fill: grey;
}

/* Profile Dropdown Menu */
.dropdown-menu {
    background-color: #8a8a8a;
    border: 1px solid #797979;
    box-shadow: 0px 0px 5px rgba(0,0,0,0.5);
}
.dropdown-menu li>a:hover {
	background-color: grey !important;
}

.dropdown-menu:before {
    border-color: transparent transparent #8a8a8a;
    border-style: solid;
    border-width: 0 8px 10px;
    -webkit-filter: drop-shadow(0 -1px 0 #797979);
    filter: drop-shadow(0 -1px 0 #797979);
}

.table-responsive {
    border: 0;
}

/* Status Currency */
.status-currency {
    color: #ffffff;
}

/* File Upload */
.dropzone {
    background: #4c4c4c;
}

/* Other Stuff */
.bg-white {
	background-color: #444;
	color: #bbb;
}

.bg-grey {
    background: #656565;
}

.landing-feature__gateway {
	border: 1px solid #6b6b6b;
}
.pro-only {
    padding: 20px;
    background: #7b714b;
    border-radius: 3px;
}

.border-offset-large {
    border-top: 1px solid #4e4e4e;
    padding-top: 40px;
    margin-top: 40px;
}

.landing-extra {
    border-top: 0;
    background: #383838;
}

.gateway-row {
    background-color: #797979;
}

.switcher-dash-action li.active a {
    background: #444;
}

.switcher-dash-action li a {
    background: #333;
}

/* Profile */
.user-info {
    color: #ffffff;
    background: #565656;
}

.user-background {
    background-color: #737373;
}

/* Chat Bubbles */

.chat-box .right .message .panel {
    background-color: #777;
	color: #efefef;
}

.chat-box .left .message:before {
    border-right: 6px solid #5c74a0;
}
.chat-box .right .message:before {
    border-left: 6px solid #777;
}

.chat-box .left .message .panel {
    background-color: #5c74a0;
}
/* Docs */
.doc-content {
    background: #444;
    padding-bottom: 0;
}

.parameters {
    border-top: 1px solid #6d6d6d;
	border-bottom: 1px solid #6d6d6d;
}

.sidebar {
	border-right: 0 !important;
}

h6 {
color: #ddd;
}

table {
    border: 1px solid #545454;
}

thead {
	background: #444;
}

th {
    background: #696868;
}

td, th {
    border-bottom: 1px solid #6b6b6b;
}
}
