css:
@-moz-document domain("play.typeracer.com") {
.main {
    background: white;
    white-space: normal;
}
body {
    background: gray;
}
.container {
    padding-left: 10px;
    background: gray;
    white-space: nowrap;
}
.title {
    padding: 0;
    display: inline-block;
    vertical-align: top !important;
}
.navigation {
    display: inline;
    font-size: 10px;
    padding: 0;
    vertical-align: top;
}
.navigation > div {
    margin-bottom: 15px !important;
}
.navigation a {
    padding: 2px;
    margin: 0;
}
#tstats a {
    /* The sign out and WPM links */
    color: white;
    text-decoration: none;
    font-size: 90%;
    font-weight: bold;
}
body > div.container > table > tbody > tr > td:nth-child(3) {
    display: block;
}
#tstats {
    float: none;
    display: block;
}
.MainUserInfoEditor .ImageButton {
    /* http://www.iconsdb.com/black-icons/edit-icon.html */
    background: url(http://i.imgur.com/RtREzsC.png) !important;
    background-size: 12px 12px !important;
    background-repeat: no-repeat !important;
    height: 12px !important;
    width: 12px !important;
}
.lblSectionHeading {
    display: none;
}
.TextDisplay, .TypingEditDisplay {
    margin-top: 10px;
    padding: 5px 10px;
    margin-bottom: 5px;
}
table.textInfoView > tbody > tr:nth-child(3) {
    display: none;
}
.TypingLogReplayPlayer .ImageButton {
    border: none;
    margin-right: 5px;
    margin-left: 5px;
}
.TypingLogReplayPlayer {
    max-width: none;
}
.themeContent {
    min-height: 0;
}
.mainViewportHolder, .mainViewport {
    width: 550px;
}
.themeContent, #dUI, table#dUI, .mainViewportHolder, .mainViewport {
    transition: all 2s ease;
}
table.tblOwnStats img {
    display: none;
}
#dUI > table > tbody > tr:nth-child(2) > td:nth-child(2) > div > div.mainViewport > table > tbody > tr:nth-child(2) > td > div {
    display: none;
}
table.mainMenuItem {
    margin-top: -5px;
    padding-top: 0;
}
table.mainMenuItem > tbody > tr > td:first-child {
    display: none;
}
table.tblOwnStats > tbody > tr {
    display: inline-block;
    margin-right: 50px;
}
table.tblOwnStats > tbody > tr > td:first-child {
    display: none;
}

.textInfoHeader {
    max-height: 5px;
    visibility: hidden;
}
.themeHeader {
    width:  230px !important;
    min-width: 0;
    height: 600px;
    float: left;
}
.main {
    display: inline-block;
}

/* Hide the headings for the logged in user stats. */
#tstats > table > tbody > tr:nth-child(1) > td:nth-child(2), #tstats > table > tbody > tr:nth-child(1) > td:nth-child(3), #tstats > table > tbody > tr:nth-child(1) > td:nth-child(4) {
    display: none;
}
#tstats-datarow > td {
    display: block;
}

td#tstats-skill {
    display: none;
}
.mainUserInfoBoxWpm:before {
    content: "Last 10 Average:";
    margin-right: 14px;
    font-weight: normal;
}
#tstats-edit > table {
    border-spacing: 2px;
    border-collapse: separate;
    margin-bottom: 15px;
}
#tstats-edit > table > tbody > tr > td:nth-child(1) > a {
    /* The "sign out" link */
    font-size: 80%;
}
.mainUserInfoBoxWpm {
    font-weight: bold;
}
#tstats-cg > div:before {
    content: "Races Completed: ";
    font-weight: normal;
    margin-right: 3px;
}
#tstats-cg > div {
    font-weight: bold;
}
#tstats-edit > table > tbody > tr > td:nth-child(2) > img:before {
    content: "what";
}
.main {
    margin-top: 20px;
    box-shadow: 0 6px 10px 0 rgba(0,0,0,0.4);
    padding: 0;
}
.main a {
    color: #047AC6;
    text-decoration: none;
}

div.mainMenuAdUnit {
    display: none;
}

/* Leaderboard on sidebar */
.statsView {
    position: absolute;
    left: 0;
    top: 180px;
    width: 220px !important;
}
.statsView > table > tbody > tr:nth-child(1) {
    display: none;
}

#gwt-uid-1 > table > tbody > tr:nth-child(1) > td > table > tbody > tr > td.gwt-TabBarFirst-wrapper {
    display: none;
}

#gwt-uid-1 td.gwt-TabBarItem-wrapper {
    font-size: 75%;
}

#gwt-uid-1 {
    background: none;
}
#gwt-uid-1 .gwt-TabBarItem {
    color: white !important;
}
.friendsTable .headerRow {
    display: none;
}
.friendsTable {
    color: #eee;
}
#dUI > table > tbody > tr:nth-child(2) > td:nth-child(1), #dUI > table > tbody > tr:nth-child(2) > td:nth-child(3) {
    /* Borders around main view */
    display: none;
}


#gwt-uid-1 div.gwt-TabPanelBottom a {
    color: #eee;
    font-weight: bold;
}
#gwt-uid-1 div.gwt-TabPanelBottom td {
    color: #eee;
}

/* Leaderboards (last hour) */
#gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(1) table.friendsTable > tbody > tr > td:first-child, #gwt-uid-1 table.friendsTable > tbody > tr > td:nth-child(4) {
	/* Hide high score numbers and time */
    display: none;
}

#gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(1) table.friendsTable > tbody > tr > td:nth-child(2) {
    /* Leaderboard username */
	max-width: 140px;
    width: 140px;
    white-space: nowrap;
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    padding-left: 4px;
}
#gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(1) table.friendsTable > tbody > tr > td:nth-child(3) {
    /* Leaderboard speed */
    padding-left: 20px;
}

/* My Scores */
#gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(2) table.friendsTable > tbody > tr > td:nth-child(3) {
    display: none;
}

/* Move graph down */
#gwt-uid-1 > table > tbody > tr:nth-child(2) > td > div > div:nth-child(2) > div > table > tbody > tr > td:nth-child(2), #gwt-uid-1 > table > tbody > tr:nth-child(2) > td > div > div:nth-child(2) > div > table > tbody > tr > td:nth-child(1) {
    display: block;
}
#gwt-uid-1 > table > tbody > tr:nth-child(2) > td > div > div:nth-child(2) > div > table > tbody > tr > td:nth-child(2) img {
    background: #eee;
    padding: 5px;
    border-radius: 2px;
    max-width: 210px;
}

/* Fastest Typists */
#gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(3) table.friendsTable > tbody > tr > td:nth-child(1), #gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(3) table.friendsTable > tbody > tr > td:nth-child(5) {
    display: none;
}
#gwt-uid-1 div.gwt-TabPanelBottom > div:nth-child(3) table.friendsTable > tbody > tr > td:nth-child(2) {
    /* Leaderboard username */
	max-width: 140px;
    width: 140px;
    white-space: nowrap;
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    padding-left: 4px;
}

  .chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br
  {
    background: 0;
  }
.footer, .highScoresFooter {
    display: none;
}

.scoreboard .row .progressBar {
    border: none;
    margin: 0;
    margin-left: 0;
    margin-right: -400px;
    height: 4px;
    border-radius: 1px;
    transition: all 2s linear;
    background-clip: content-box, padding-box;
    position: relative;
}
.scoreboard tr:nth-child(5n+1) .row .progressBar {
    background-image: linear-gradient(to bottom, white 0%, white 100%), linear-gradient(to bottom, #7ff8a4 0%, #7ff8a4 100%);
}
.scoreboard tr:nth-child(5n+2) .row .progressBar {
    background-image: linear-gradient(to bottom, white 0%, white 100%), linear-gradient(to bottom, #eded82 0%, #eded82 100%);
}
.scoreboard tr:nth-child(5n+3) .row .progressBar {
    background-image: linear-gradient(to bottom, white 0%, white 100%), linear-gradient(to bottom, #84d0f5 0%, #84d0f5 100%);
}
.scoreboard tr:nth-child(5n+4) .row .progressBar {
    background-image: linear-gradient(to bottom, white 0%, white 100%), linear-gradient(to bottom, #fa80c5 0%, #fa80c5 100%);
}
.scoreboard tr:nth-child(5n+5) .row .progressBar {
    background-image: linear-gradient(to bottom, white 0%, white 100%), linear-gradient(to bottom, #ce85f6 0%, #ce85f6 100%);
}

.avatar {
    position: absolute;
    left: 0;
    top: -20px;
}

.scoreboard .row .progressBar .avatar .nameContainer {
    width: 400px;
    text-align: left;
}
.nameContainer + img {
    display: none;
}
.lblName {
    white-space: normal !important;
    display: inline;
}
.scoreboard .row .rankPanel {
    z-index: 1;
}
.scoreboard > tbody > tr > td > table > tbody > tr > td {
    overflow: hidden;
}
.scoreboard .row .rankPanel .rankPanelWpm-self {
    font-size: 120%;
}

table.gameView > tbody > tr:nth-child(4) > td > div {
    max-height: 600px;
    overflow-y: hidden;
    opacity: 1;
    transition: max-height 1s ease, opacity 0.3s ease 0.3s;
}
table.gameView > tbody > tr:nth-child(4) > td > div:empty {
    display: block !important;
    opacity: 0;
    max-height: 170px;
    transition: max-height 1s ease, opacity 0.3s ease 0.3s;
}
.mainViewport .room .gameView {
    transition: all 2s linear;
}
table.gameView > tbody > tr:nth-child(4) > td > div > table > tbody > tr:nth-child(5), table.gameView > tbody > tr:nth-child(4) > td > div > table > tbody > tr:nth-child(6) {
    position: absolute;
    top: 20px;
    left: 820px;
}
input:focus { outline: none; }
  .room-title
  {
    background: white;
    font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;
    color: #047AC6;
    font-size: 18px;
    padding: 0;
    font-weight: lighter !important;
    font-style: italic;
    text-transform: uppercase;
  }
  .nonHideableWords, .hideableWords, .txtInput, table.inputPanel div
  {
    font-family: "Anonymous Pro", "Lucida Console", Monaco, monospace !important;
  }
table.inputPanel div > span:nth-child(1), table.inputPanel div > span:nth-child(2) {
    color: #047AC6;
}

  .gameStatusLabel, .arrowPopupText
  {
    color: #047AC6 !important;
    opacity: 1;
    transition: opacity .4s ease-in-out;
    -moz-transition: opacity .4s ease-in-out;
    -webkit-transition: opacity .4s ease-in-out;
  }

  .gameStatusLabel-flashOn, .arrowPopupText-flashOn
  {
    opacity: 0.66;
    transition: opacity .4s ease-in-out;
    -moz-transition: opacity .4s ease-in-out;
    -webkit-transition: opacity .4s ease-in-out;
  }

  .txtInput
  {
    background: none !important;
    border: none !important;
    border-bottom: 2px solid #047AC6 !important;
    transition: all 0.2s ease;
  }

  .txtInput-error
  {
    background: none !important;
    border-bottom: 2px solid red !important;
    transition: all 0.2s ease;
  }

  [style="text-decoration: underline; color: rgb(153, 204, 0);"], [style="color: rgb(153, 204, 0);"]
  {
    color: #047AC6 !important;
    text-decoration: none !important;
  }
.TextStatsView img {
    display: none;
}
.TextStatsView img.gwt-Image {
    background: #ddd;
    border-radius: 2px;
    padding: 5px;
    display: block;
}
.practiceViewScoresPopup {
    display: none;
}

.TextStatsView div.gwt-TabPanelBottom[role="tabpanel"] a, .TextStatsView .gwt-TabBarItem {
    color: white;
    font-weight: bold;
}
.TextStatsView div.gwt-TabPanelBottom[role="tabpanel"] > div > table > tbody > tr > td,
  .TextStatsView table.abstractStatsList > tbody > tr > td > table > tbody > tr {
    display: block;
}
div.gwt-TabPanelBottom[role="tabpanel"] > div > table > tbody > tr > td,
  table.abstractStatsList > tbody > tr > td > table > tbody > tr > td {
    padding-right: 8px;
}
TextStatsViewtable.abstractStatsList > tbody > tr > td > table > tbody > tr > td:nth-child(1), table.abstractStatsList {
    white-space: nowrap;
}
.dialogContent .TextStatsView .friendsTable a.gwt-Anchor, .dialogContent .TextStatsView .friendsTable, .dialogContent .TextStatsView .gwt-TabBarItem {
    color: #333;
}
.TextStatsView table.abstractStatsList > tbody > tr > td > table > tbody > tr.headerRow {
    display: none;
}

.TextStatsView div[aria-hidden="false"] > table.abstractStatsList > tbody > tr > td > table > tbody > tr > td:nth-child(1), .TextStatsView table.abstractStatsList > tbody > tr > td > table > tbody > tr > td:nth-child(3) {
    display: none;
}

#dUI > table > tbody > tr:nth-child(3) {
    display: none;
}

/* Popup code from Jon&Kathy */
/* Player Info */
  .playerInfoPopup
  {
    background: #292b2c;
    color: #FFF;
    border-color: #222;
  }

  .playerInfoPopup a
  {
    color: #EEE;
  }

  .playerInfoPopup a:hover,
.playerInfoPopup a:focus
  {
    color: #FFF;
  }
.popupContent
  {
    background: #EEE;
    color: #292b2c;
  }

  .PopupWithIcon
  {
    background-color: #292b2c;
    border-color: #292b2c;
  }
.dialogContent a {
    color: #047AC6;
}
  .trPopupDialog .xButton
  {
    background-color: #292b2c;
  }

  .trPopupDialog
  {
    box-shadow: none;
  }
}
