css:
@-moz-document domain("ldjam.com") {
body {
    background: #445;
    font-family: 'Lucida Grande', Verdana, Arial, sans-serif !important;
    top: 0px;
}

#content {
    padding-bottom: 1em;
}

#footer {
    display: none;
}

.view-bar {
    background: none;
    position: static !important;
    margin-bottom: 20px;
}

.view-bar>.-content>.-right>a div, .view-bar>.-content>.-right>a:visited div, .view-bar>.-content>.-right>div div {
    color: #CCD !important;
}

.content-common {
    background: linear-gradient(to bottom, #DDD 0%, #EEE 25%) !important;
    max-width: 640px;
    margin: 0px auto;
}

.-body {
	display: block;
    max-width: 640px;
    margin: 0px auto;
}

.content-nav>.-button, .filter-game>.-bodies>.content-common-body>.button-base {
    border: none !important;
    color: #BBC !important;
    background: transparent !important;
}

.content-nav>.-button:hover, .filter-game>.-bodies>.content-common-body>.button-base:hover {
    color: #FFF !important;
}

.filter-game {
    background: transparent !important;
    box-shadow: none !important;
    border: solid #3d4752 1px;
}

.content-boxes {
    max-width: 640px;
    margin: 0px auto !important;
}

.content-nav {
    margin: 0px auto !important;
    display: table;
}

.content-common-body, .-markup {
	font-size: 12px !important;
}

#layout {
    max-width: 960px !important;
    margin: 0px auto;
    background: #889;
    padding: 0px 10px;
}

.view-bar>.-content {
    max-width: 980px !important;
}

.content-common-header.-col-a, .content-common-header.-col-b, .content-common-header.-col-c, .-headline {
    color: rgba(0, 0, 0, 0.3) !important;
    overflow: visible !important;
    font-size: 24px !important;
    height: 1em !important;
    text-align: right;
    padding-right: 10px;
    font-style: normal !important;
}

.content-common-header.-col-c {
    background-color: #DBE !important;
}

.-headline {
    color: rgba(255, 255, 255, 0.3) !important;
    margin-bottom: 20px;
}

.content-common-header.-col-a:after, .content-common-header.-col-b:after, .content-common-header.-col-c:after, .-headline:after {
	content: "";
    position: absolute;
    left: 0px;
    top: 1em;
    right: 0px;
    height: 20px;
}

.content-common-header.-col-a:after {
    background: linear-gradient(to bottom, #e53, rgba(0, 0, 0, 0));
}

.content-common-header.-col-b:after {
    background: linear-gradient(to bottom, #fc1, rgba(0, 0, 0, 0));
}

.content-common-header.-col-c:after {
    background: linear-gradient(to bottom, rgb(221, 187, 238), rgba(0, 0, 0, 0));
}

.-headline:after {
    background: linear-gradient(to bottom, #445, rgba(0, 0, 0, 0));
}

.content-common-footer.-has-items {
    visibility: hidden;
}

.-count {
    visibility: visible;
    font-size: 12px !important;
    color: #04C;
}

.icon-heart, .icon-heart-plus, .icon-heart-minus, .icon-edit {
    visibility: visible;
}

.-at-name, .-atname {
    color: #04C !important;
}

.-comments>.-count:hover, .-at-name:hover, .-atname:hover {
    color: #FA0 !important;
}

.-has-items>.-right>.-edit {
    position: absolute !important;
    right: 0px;
}

.-has-items>.-right>.-love, .-has-items>.-right>.-comments {
    padding: 0px !important;
    margin: 0px;
}

.content-more {
    max-width: 640px;
    margin-left: auto !important;
    margin-right: auto !important;
}

.-domain, .-markup a, .-the-rest {
    color: #04C !important;
}

.-domain:hover, .-markup a:hover, .-the-rest:hover {
    color: #FA0 !important;
}

.-icon-domain>svg {
    display: none;
}

.-has-items>.-right {
    width: 100%;
    position: relative !important;
}

.-has-items>.-left {
    display: none;
}

.-love>.-count {
    font-weight: bold;
}

.-comments>.-count:after {
    content: " Comments »";
}

.-logo {
    background: url("http://ludumdare.com/compo/wp-content/themes/ludum/povimg/LDLogo2015.png") no-repeat;
    width: 386px;
    height: 64px;
}

.-logo>svg {
    visibility: hidden;
}

.-logo:hover, .-logo:active, .-logo:focus {
    background: url("http://ludumdare.com/compo/wp-content/themes/ludum/povimg/LDLogo2015.png") no-repeat !important;
}

#header {
    display: none;
}

a:hover, a:active {
    background-color: transparent !important;
}

#content .content-common-body.-title a, #content .content-common-body.-title a:visited {
    color: #026;
    font-family: 'Roboto Condensed', 'Trebuchet MS', 'Lucida Grande', Verdana, Arial, sans-serif !important;
    font-size: 20.4px !important;
}

.-comment {
    font-size: 12px !important;
}

.-comment>.-body>.-text {
    margin-left: 30px !important;
}

#content .content-item .-rating .-items .-grade, #content .content-item .-rating .-items .-metric, .-rating>.-footer, #content .-footer {
    font-size: 12px !important;
}

#content .content-common-footer-button:focus, #content .content-common-footer-button:hover>svg {
    color: #777 !important;
}

.content-common-draft {
    display: none;
}

#content .content-more>.-button {
    visibility: hidden;
    color: #BBC;
    position: absolute;
    left: 0px;
    top: 0px;
}

#content .content-more>.-button:after {
    content: "« Older Entries";
    visibility: visible;
    font-size: 12px;
    font-weight: normal;
    position: absolute;
    left: 0px;
    top: -1em;
    display: block;
}

#content .content-more>.-button:hover {
    color: #FFF !important;
}
}

@-moz-document url-prefix("https://ldjam.com/events/"), url-prefix("https://ldjam.com/users/") {
#sidebar {
    display: none !important;
}
}

@-moz-document regexp("https://ldjam.com/events/ludum-dare/\\d+/[^/]+(/?(comments)?)?") {
.content-common, .-body {
    max-width: 960px !important;
}
}
