css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("steemit.com") {

  /*Colours:
    #f0f0f0 light grey grey top header
    #fafafa light grey article backgrund
    #cee3f8 light blue header
    #e0e0e0 light grey borders
    #EFF7FF pale blue tabs
    #369 blue header
    #306092 blue text
    white text
    orangered active tab text
  */
     
     
  /*Header*/
  .Header .Header__top {
    background-color: #f0f0f0;
    border-bottom: 1px solid gray;
  }
  
  .Header .menu {
    background-color: #f0f0f0;
  }
  
  .Header .Header__sub-nav {
    background-color: #cee3f8;
    border-bottom: 1px solid #e0e0e0;
    padding: 10px 10px 0px 10px;
  }
  
  .Header .HorizontalMenu {
    background-color: #cee3f8;
    margin: 0px;
    padding: 0px;
  }
  
  .Header .HorizontalMenu > li {
    color: #306092;
    background-color: #EFF7FF;
    margin: 2px 10px -1px 10px;
    padding: 10px 10px 10px 10px;
    border: 1px solid #e0e0e0 !important;
    border-radius: 10px 10px 0px 0px;
    text-transform: capitalize;
  }
  
  .Header .HorizontalMenu > li > a {
    color: #306092;
  }
  
  .Header .HorizontalMenu > li > a:hover {
    background-color: #EFF7FF;
    color: orangered;
  }
  
  .Header .HorizontalMenu .active {
    background-color: white;
    border-bottom: 1px solid white !important;
  }
  
  .Header .HorizontalMenu .active > a {
    color: orangered !important;
    outline: 0;
  }
  
  .UserProfile__banner {
    background-color: #369;
    border-bottom: 1px solid #e0e0e0;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:first-of-type {
    background-color: #369;
    border: 1px solid #EFF7FF;
    color: #EFF7FF;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:first-of-type:hover {
    border: 1px solid #e0e0e0;
    background-color: #EFF7FF;
    color: #369;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:last-of-type {
    background-color: #369;
    border: 1px solid #EFF7FF;
    color: #EFF7FF;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:last-of-type:hover {
    border: 1px solid #e0e0e0;
    background-color: #EFF7FF;
    color: #369;
  }
  
  .UserProfile__banner > .column {
    background: inherit !important;
  }
  
  .UserProfile__top-nav {
    background-color: #cee3f8;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    border-bottom: 1px solid #e0e0e0;
  }
  
  .UserProfile__top-nav .row .columns {
    overflow-x: unset !important;
  }
  
  .UserProfile__top-menu {
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    max-width: none !important;
  }
  
  .UserProfile__top-menu .columns {
    max-width: none !important;
  }

  .UserProfile__top-menu .columns .menu a {
    color: #306092;
    background-color: #EFF7FF;
    margin: 10px 10px -1px 10px;
    padding: 0px 10px 0px 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px 10px 0px 0px;
  }


  .UserProfile__top-nav .menu > li > a.active {
    color: orangered;    
    border-bottom: 1px solid white;
    outline: 0;
  }
  
  .UserProfile__top-nav .menu > li > a:hover {
    background-color: #EFF7FF;
    color: orangered;
  }
  
  .VerticalMenu {
    background-color: #EFF7FF;
  }
  
  .VerticalMenu > li > a:hover {
    background-color: #cee3f8;
  }
  
  /* Followers */
  
  .UserList {
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    padding: 10px;
    background-color: #EFF7FF;
    margin-bottom: 10px;
  }
  
  .pager .button {
    border: 1px solid #e0e0e0;
    background-color: #369;
    color: white;
  }
  
  .pager .button:hover {
    background-color: #f0f0f0;
    color: #306092;
  }
  
  .UserList > div > div > table {
    border: 1px solid #e0e0e0;
  }
  
  .UserList > div > div > table > tbody > tr:nth-child(odd) {
    background-color: white;
  }
  
  .UserList > div > div > table > tbody > tr:nth-child(even) {
    background-color: #f0f0f0;
  }
  
  .UserList > div > div > table > tbody > tr .button:first-of-type {
    border: 1px solid #e0e0e0;
    background-color: #EFF7FF;
    color: #306092;
  }
  
  .UserList > div > div > table > tbody > tr .button:first-of-type:hover {
    background-color: #369;
    color: white;
  }
  
  .UserList > div > div > table > tbody > tr .button:last-of-type {
    border: 1px solid #e0e0e0;
    background-color: #EFF7FF;
    color: #306092;
  }
  
  .UserList > div > div > table > tbody > tr .button:last-of-type:hover {
    background-color: #369;
    color: white;
  }
  
  /* Rewards Page */
  
  .UserProfile__tab_content .UserWallet > div:first-child {
    border: 1px solid #e0e0e0;
    border-radius: 10px 10px 0px 0px;
    background-color: #EFF7FF;
  }
  
  .UserProfile__tab_content .UserWallet > div:first-child > div {
    background-color: #EFF7FF !important;
    border: none !important;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row {
    border: 1px solid #e0e0e0;
    border-top: none;
    border-radius: 0px 0px 10px 10px;
    margin-bottom: 10px;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div {
    display: none;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div {
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: #EFF7FF;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table {
    border: 1px solid #e0e0e0;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table > tbody > tr:nth-child(even) {
    background-color: #f0f0f0;
  }
    
  /* Wallet - Balances */
  
  .UserWallet__claimbox {
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: #EFF7FF;
    padding-left: 10px;
    padding-right: 10px;
  }
  
  .UserWallet__claimbox .button {
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: #cee3f8;
    color: #306092;
    margin-right: 0px !important;
  }
  
  .UserWallet > div:nth-last-child(1) {
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: #EFF7FF;
    margin-bottom: 10px;
  }
  
  .UserWallet > div:nth-last-child(1) > div > table {
    border: 1px solid #e0e0e0;
  }
  
  .UserWallet > div:nth-last-child(1) > div > table > tbody > tr:nth-child(even) {
    background-color: #f0f0f0;
  }
  
  .UserWallet > div:nth-last-child(2) {
    display: none;
  }
  
  .UserWallet > div:nth-last-child(4) {
    border: 1px solid #e0e0e0;
    border-top: none;
    border-radius: 0px 0px 10px 10px;
    padding: 5px 10px 10px 10px;
    background-color: #EFF7FF;
  }
  
  .UserWallet > div:nth-last-child(4) > div:first-of-type {
    border: 1px solid #e0e0e0;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(4) > div:last-of-type {
    border: 1px solid #e0e0e0;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(5) {
    border-left: 1px solid #e0e0e0;
    border-right: 1px solid #e0e0e0;
    padding: 5px 10px 5px 10px;
    background-color: #EFF7FF !important;
  }
  
  .UserWallet > div:nth-last-child(5) > div:first-of-type {
    border: 1px solid #e0e0e0;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(5) > div:last-of-type {
    border: 1px solid #e0e0e0;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(6) {
    border-left: 1px solid #e0e0e0;
    border-right: 1px solid #e0e0e0;
    padding: 5px 10px 5px 10px;
    background-color: #EFF7FF;
  }
  
  .UserWallet > div:nth-last-child(6) > div:first-of-type {
    border: 1px solid #e0e0e0;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(6) > div:last-of-type {
    border: 1px solid #e0e0e0;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(7) {
    border-left: 1px solid #e0e0e0;
    border-right: 1px solid #e0e0e0;
    padding: 5px 10px 5px 10px;
    background-color: #EFF7FF !important;
  }
  
  .UserWallet > div:nth-last-child(7) > div:first-of-type {
    border: 1px solid #e0e0e0;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(7) > div:last-of-type {
    border: 1px solid #e0e0e0;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(8) {
    border: 1px solid #e0e0e0;
    border-bottom: none;
    border-radius: 10px 10px 0px 0px;
    padding: 10px 10px 5px 10px;
    background-color: #EFF7FF;
  }
  
  .UserWallet > div:nth-last-child(8) > div:first-of-type {
    border: 1px solid #e0e0e0;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: white;
  }
  
  .UserWallet > div:nth-last-child(8) > div:last-of-type {
    border: 1px solid #e0e0e0;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: white;
  }
  
  .WalletSubMenu {
    border-bottom: none;
    margin: 10px 0px 0px 0px;
  }
  
  .WalletSubMenu > li > a {
    border: 1px solid #e0e0e0;
    border-radius: 10px 10px 0px 0px;
    background-color: white;
    color: #306092;
    margin: 10px 10px -1px 10px;
    padding: 0px 10px 0px 10px;
  }
  
  .WalletSubMenu > li > a.active {
    border-bottom: 1px solid #EFF7FF;
    background-color: #EFF7FF;
    color: orangered;
    outline: 0;
  }
  
  .UserWallet__buysp.button.hollow {
    border: 1px solid #e0e0e0;
    border-radius: 10px 10px 0px 0px;
    margin: 19px 10px -17px 10px;
    padding: 5px 10px 5px 10px;
    
  }
  
  /* Wallet - Permissions & Password */
  
  .UserProfile__top-nav + div > div > a.float-right {
    float: none !important;
    width: 100px;
    margin-left: auto;
    margin-right: auto;
  }
  
  .UserProfile > div > div > br {
    display: none;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) {
    background-color: #EFF7FF;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    padding: 10px 10px 0px 10px;
    margin-bottom: 10px;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div {
    background-color: white;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    padding: 10px 10px 0px 10px;
    margin-bottom: 10px;
    flex: 0 0 100% !important;
    max-width: 100% !important;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div .button {
    border: 1px solid #e0e0e0;
    background-color: #369;
    width: 9rem;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div .button a {
    color: white;
    font-size: 0.9rem;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div .button a:hover {
    color: gold;
  }
  
  .ChangePassword__rules > hr {
    border: 1px solid #e0e0e0;
  }
  
  .ChangePassword .float-right a {
    margin: 0px 0px 10px 0px;
    padding: 10px;
    border: 1px solid #e0e0e0 !important;
    background-color: #369;
    color: white;
    font-size: 0.9rem;
    font-family: sans-serif;
    line-height: 1;
    display: inline-block;
    border-radius: 3px;
    vertical-align: middle;
    text-align: center;
    width: 20rem;
  }
  
  .ChangePassword .float-right a:hover {
    color: gold;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form > center {
    width: 20rem;
    margin-left: auto;
    margin-right: 10px
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form .button {
    border: 1px solid #e0e0e0;
    background-color: #369;
    margin: 10px;
    padding: 10px;
    color: white;
    width: 20rem;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form .button:hover {
    color: gold;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form > div:last-of-type {
    width: 20rem;
    margin-left: auto;
    margin-right: 10px;
  }
  
  /* Settings */
  
  .UserProfile__tab_content .Settings > div > form {
    background-color: #EFF7FF;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    padding: 10px;
    max-width: 100% !important;
    flex: 100% !important;
  }
  
  .UserProfile__tab_content .Settings > div > form > label {
    background-color: white;
    border: 1px solid #e0e0e0;
    border-bottom: none;
    border-radius: 10px 10px 0px 0px;
    padding: 10px;
  }
  
  .UserProfile__tab_content .Settings > div > form > div {
    background-color: white;
    border: 1px solid #e0e0e0;
    border-top: none;
    border-radius: 0px 0px 10px 10px;
    padding: 10px;
  }
  
  .UserProfile__tab_content .Settings > div > form .button {
    border: 1px solid #e0e0e0;
    background-color: #369;    
    padding: 10px;
    color: white;
  }
  
  .UserProfile__tab_content .Settings > div > form .button:hover {
    color: gold;
  }
  
  .UserProfile__tab_content .Settings > div + div {
    background-color: #EFF7FF;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    margin: 10px -15px 10px -15px;
    padding: 10px 0px 10px 0px;
  }
  
  .UserProfile__tab_content .Settings > div + div .button {
    border: 1px solid #e0e0e0;
    background-color: #369;
    margin-top: 10px;
    padding: 10px;
    color: white;
  }
  
  .UserProfile__tab_content .Settings > div + div .button:hover {
    color: gold;
  }
  
  .UserProfile__tab_content .Settings div:last-of-type > br {
    display: none;
  }
  
  /* Posts */
  
  .PostsList__summaries {
    background-color: #EFF7FF;
    padding: 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
  }
  
  .PostSummary.hentry  {
    margin: 0px 0px 10px 0px;
    padding: 10px 10px 10px 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
  }
  
  .PostSummary__reblogged_by {
    padding-left: 20px;
  }
  

  /*Article*/
  
  div .Post {
    padding: 10px;
  }
  
  article {
    background-color: #fafafa;
    color: #222222;
  }
  
  div .PostFull__header > span {
    margin: 0px !important;
    padding: 20px;
    border-top: 1px solid #e0e0e0 !important;
    border-bottom: 1px solid #e0e0e0 !important;
  }
  
  .PostFull.hentry {
    padding: 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
  }
  
  .PostFull.hentry .Promote__button {
    margin-top: 10px;
    margin-right: 0px !important;
    background-color: #369;
    color: white;
    border: 1px solid #e0e0e0;
  }
  
  .PostFull.hentry .Promote__button:hover {
    color: gold;
  }
  
  div .TagList__horizontal {
    border-top: 1px solid #e0e0e0;
  }
  
  div .TagList__horizontal > a {
    border: 1px solid #e0e0e0;
    margin: 10px 10px 0px 0px;
    padding: 0px 10px 0px 10px;
    background-color: #EFF7FF;
  }
  
  div .PostFull__footer {
    border-top: 1px solid #e0e0e0;
    border-bottom: 1px solid #e0e0e0;
    margin: 0px !important;
  }
  
  /* Article Replies */
  
  div.PostFull__footer + div {
    margin: 0px !important;
  }
  
  div.PostFull__footer + div > div.column {
    padding: 0px;
    flex: 0 0 100%;
    max-width: 100%;
  }
  
  .PostFull .ReplyEditor {
    margin: 0px;
  }
  
  .PostFull .ReplyEditor > div.column {
    padding: 0px;
  }
  
  .PostFull .ReplyEditor .Preview {
    background-color: white;
    border: 1px solid #e0e0e0 !important;
    border-radius: 10px;
    margin-top: 10px;
    padding: 10px;
    max-width: 100%;
  }
  
  .PostFull  .ReplyEditor .Preview .MarkdownViewer {
    background-color: #fafafa;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
  }
  
  /*Comments*/
  
  div .hentry.Comment.root {
    padding: 10px 0px 10px 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: white;
  }
  
  div .hentry.Comment.reply .depth {
    display: none;
  }
  
  div .Comment__replies.hfeed {
    margin: 10px 0px 0px 10px;
    padding: 10px 0px 0px 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px 0px 0px 10px;
    border-right: none;
  }
  
  .hentry.Comment.reply {
    margin-bottom: 10px;
  }
  
  div .Comment__replies.hfeed:nth-child(odd) {
    background-color: #EFF7FF;
  }
  
  div .Comment__replies.hfeed:nth-child(even) {
    background-color: white;
  }
  
  /* Comment Reply */
  
  .Comment__replies .ReplyEditor {
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: #cee3f8;
    margin: 0px 10px 10px 0px !important;
    padding: 10px !important;
  } 
  
  .Comment__replies .ReplyEditor > div {
    padding: 0px;
  }
  
  .Comment__replies .ReplyEditor > div .ReplyEditor__body {
    margin: 0px;
  }
  
  .Comment__replies .ReplyEditor .Preview {
    background-color: white;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    margin-top: 10px;
    padding: 10px;
  }
  
  .Comment__replies .ReplyEditor .Preview .MarkdownViewer {
    background-color: #fafafa;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
  }
  
  /* Sunmissions */
  
  .Comment__replies .ReplyEditor {
    margin-top: 0px;
    margin-bottom: 0px;
    padding: 0px;
  }
  
  .SubmitPost .ReplyEditor > div {
    margin: 0px;
    padding: 0px;
  }
  
  .SubmitPost .ReplyEditor > div > form {
    margin: 10px;
    padding: 10px;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    background-color: #EFF7FF;
  }

  .ReplyEditor > div > form > div > button:first-of-type {
    background-color: #369;
    color: white;
    border: 1px solid #e0e0e0;
    margin: 0px;
    padding: 10px;
  }
  
  .ReplyEditor > div > form > div > button:first-of-type:hover {
    color: gold;
  }
  
  .ReplyEditor > div > form > div > button:last-of-type {
    background-color: #EFF7FF;
    color: #306092;
    border: 1px solid #e0e0e0 !important;
    margin: 0px;
    padding: 10px;
    text-decoration: none !important;
  }
  
  .ReplyEditor > div > form > div > button:last-of-type:hover {
    background-color: orangered;
    color: white !important;
  }
  
  .SubmitPost .ReplyEditor__options {
    margin-top: -10px;
    padding: 0px;
  }
  
  .SubmitPost .ReplyEditor__options > select {
    padding-right: 1.5rem;
  }
  
  .SubmitPost .ReplyEditor .Preview {
    background-color: white;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    margin-top: 10px;
    padding: 10px;
  }
  
  .SubmitPost .ReplyEditor .Preview .MarkdownViewer {
    background-color: #fafafa;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
  }
  
  .SidePanel > div {
    background-color: #369;
  }
  
  .SidePanel > div .menu > li > a {
    border-top: 1px solid #e0e0e0;
  }
  
  .SidePanel > div .menu > li > a:hover {
    background-color: #EFF7FF;
    color: #306092;
  }
  
  .SidePanel .menu > li.last {
    border-bottom: 1px solid #e0e0e0;
  }
  
  /* Tags and Topics */
  
  .PostsIndex__topics {
    background-color: #EFF7FF;
    color: #306092;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    margin: 0px;
    padding: 10px;
  }
  
  ul.Topics {
    background-color: white;
    color: #306092;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    margin: 0px;
    padding: 10px;
  }
  
  .Topics__title {
    background-color: #cee3f8;
    color: #306092;
    padding: 10px;
  }
  
  ul.Topics > hr {
    display: none;
  }
  
  ul.Topics > li {
    padding-left: 10px;
  }
  
  ul.Topics > li > a:hover {
    color: orangered;
  }
  
  ul.Topics > li > a.active {
    color: orangered;
  }
  
  div.TagsIndex {
    background-color: #EFF7FF;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    padding: 10px;
  }
  
  div.TagsIndex > div.column {
    padding: 0px;
  }
  
  div.TagsIndex > div.column > br {
    display: none;
  }
  
  div.TagsIndex > div.column > table {
    border: 1px solid #e0e0e0;
  }
  
  div.TagsIndex > div.column > table > thead {
    background-color: #cee3f8;
  }
  
  div.TagsIndex > div.column > table > tbody {
    background-color: white;
  }
  
  div.TagsIndex > div.column > table > tbody > tr:nth-child(even) {
    background-color: #f0f0f0;
  }
  
  div.TagsIndex > div.column > table > tbody > tr > td > a:hover {
    color: orangered;
  }
}
