css:
@-moz-document domain("www.animetoon.org"), domain("animetoon.org") {
body, #sitepicker ul{
    background: #000;
    color: #aaa;
}

body, #body > .s_left_col, #body > .s_left_col, #top_block, .vmargin, #eps_blocks, .bar, #elinks a.right, #elinks a.left, span.playlist, .menu-bar, #sitepicker ul li, #search-box .form-text-box, #content_block, .hup-item{
    background-color: #000;
}

span.playlist, #search-box .form-text-box{
    color: #aaa;
}

#streams, #downloads, #comments-series, .bar-container, #search-box .form-text-box {
 	border: 1px solid #555;   
}

.vmargin, #top_block {
    border: 5px solid #555;
}

.menu-bar-left, #sitepicker ul li, .menu-bar-wrapper-inner {
    border-right: 1px solid #555;
}

#sitepicker ul{
    border-bottom: 3px solid #555;
}

.menu-bar-wrapper-inner{
    border-left: 1px solid #555;
}
.bar-container{
    border: none;
}

body > div:first-child {
    display: block !important;
}

#ads-banner-inner, .info_block, #sidebar, #eps_blocks, body > div:last-child, #home_sidebar > iframe, #content > iframe, #Mad, #M_AD {
    display: none;
}
}
