css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('meneame.net') {

::-webkit-scrollbar,
 ::-webkit-scrollbar-corner {
    background: #040406;
    width: 14px;
    height: 14px;
}

::-webkit-scrollbar-button {
    display: none;
}

::-webkit-scrollbar-thumb {
    background: #333;
}

::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838;
}

::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c;
}

html * {
    margin: 0;
    padding: 0;
}

body {
    background-image: none;
    font-family: 'Open Sans', 'Helvetica Neue', Helvetica, Arial, sans-serif;
    color: #aaa;
    background-color: #22232b !important;
    margin: 0px;
    padding: 0px;
}

.header-top-wrapper.fixed {
    position: fixed;
    top: 0;
    left: 0;
    z-index: 10;
}

.header-sub-wrapper.fixed {
    position: fixed;
    top: 47px;
    left: 0;
    z-index: 9;
}

.header-top-wrapper.pinned {
    position: fixed;
    top: 0px;
    left: 0px;
    right: 0px;
    -webkit-transform: translateY(0px);
    -moz-transform: translateY(0px);
    -ms-transform: translateY(0px);
    -o-transform: translateY(0px);
    transform: translateY(0px);
    transition: all 1.0s ease-in-out;
    z-index: 1001;
}

.mnm-center-in-wrap {
    margin: 0 auto;
    min-width: 805px;
    max-width: 1300px;
    width: 99%;
    text-align: left;
}

#ajaxinfo {
    display: none;
}

label {
    color: #e97c2a;
}

 :focus {
    outline: 0;
}

select {
    background: #22232b;
    color: #e97c2a;
}

legend {
    font-weight: bold;
    padding: 0 5px;
    width: auto;
    font-size: 15px;
    border-bottom: 0px solid #e5e5e5;
}

legend a {
    text-decoration: underline;
}

legend,
legend a,
legend a:visited {
    background: #e97c2a;
    color: #fff;
}

legend a:hover {
    color: #888;
}

input,
textarea {
    margin: 0 0 10px 0;
    padding: 2px 2px;
    background: #22232b;
    border: 1px solid #22232b;
    color: #ddd;
}

.button,
button,
input[type="button"],
input[type="submit"] {
    border: none;
    font-weight: bold;
    margin: 4px 0;
    padding: 2px 4px;
    background-color: #e97c2a;
    color: #fff;
}

.button:hover,
button:hover,
input[type="button"]:hover,
input[type="submit"]:hover {
    background-color: #FFB300;
}

.button:disabled,
button:disabled,
input[type="button"]:disabled,
input[type="submit"]:disabled {
    background: #feefe5;
}

fieldset {
    border: none;
    padding: 10px 10px;
    margin: 10px 0;
    background: #343543;
}

label {
    font-weight: bold;
}

a {
    outline: none;
    vertical-align: baseline;
    color: #ddd;
    text-decoration: none !important;
    transition: color .25s cubic-bezier(.25, .08, .44, 1);
}

a:hover,
a:focus {
    text-decoration: underline;
    color: #e97c2a;
}

a:active {
    color: #000;
}

a:visited {
    color: #c29fe2;
}

input[disabled],
input[readonly],
select[disabled],
select[readonly],
checkbox[disabled],
checkbox[readonly],
textarea[disabled],
textarea[readonly] {
    color: #aaa;
}

#map,
.commentform.warn,
.warn,
.box,
.searchbox,
fieldset,
label,
.button,
button {
    border-radius: 3px;
}

#header-top,
#header-center,
.news-shakeit,
.answers .counter,
#notifier_panel {
    font-family: 'Lato', 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

#variable {
    width: 100%;
    min-width: 790px;
}

#header {
    width: 100%;
    margin-top: 47px;
}

#header-top {
    overflow: hidden;
    white-space: nowrap;
    z-index: 900;
    user-select: none;
    -moz-user-select: none;
    -webkit-user-select: none;
    margin: 0 auto;
    min-width: 805px;
    max-width: 1300px;
    width: 99%;
    text-align: left;
}

.header-top-wrapper {
    height: 47px;
    background-color: #e97c2a;
    width: 100%;
}

#header-top a.sub-name {
    color: #fff;
    font-size: 13px;
    border-left: 1px solid #fff;
    display: inline-block;
    height: 40px;
    padding-left: 10px;
    margin-top: 4px;
    padding-top: 12px;
    margin-left: 10px;
    font-family: 'Open Sans', sans-serif;
    float: left;
    text-transform: uppercase;
}

#header-top .logo-mnm {
    float: left;
    margin: 8px 0 0 9px;
    font: 0/0 serif;
    text-shadow: none;
    color: transparent;
    cursor: pointer;
}

#header-top .logo-mnm img {
    height: 30px;
    width: 163px;
}

#header-top .logo-ed {
    background: url('https://mnmstatic.net/v_128/img/mnm/h9_logo_ed.png') no-repeat left top;
    background-size: 9px 16px;
    height: 16px;
    width: 9px;
    margin-left: 20px;
    margin-top: 7px;
    margin-right: 15px;
    float: left;
}

.header-center-wrapper {
    height: 26px;
    background-color: #101010;
    width: 100%;
}

#header-center {
    margin: 0 auto;
    min-width: 805px;
    max-width: 1300px;
    width: 99%;
    text-align: left;
    padding-top: 3px;
}

#header-center .checkbox {
    margin: 0;
    padding: 0;
}

#header-center span,
#header-center a.discover-subs {
    color: #B7B7B7;
    font-size: 12px;
    font-family: 'Open Sans', sans-serif;
    font-weight: 400;
}

#header-center a.discover-subs {
    margin-left: 15px;
}

.header-menu-wrapper {
    background-color: #343543;
    color: #E9E9E9;
    border-bottom: 1px solid #343543;
}

.header-menu01 ul.dropdown-menu a:hover {
    background: none;
    text-decoration: underline;
}

ul.official-subs {
    margin: 0;
    padding: 0;
    list-style-type: none;
    display: inline;
    padding-left: 5px;
    border-left: 1px solid #B7B7B7;
    margin-left: 15px;
}

ul.official-subs li {
    display: inline;
}

ul.official-subs li a {
    color: #B7B7B7;
    font-size: 12px;
    font-family: 'Open Sans', sans-serif;
    font-weight: 400;
    margin-left: 10px;
}

ul.official-subs li a:hover {
    color: #fff;
}

ul.official-subs li a i {
    margin-right: 4px;
}

.menu-more {
    display: inline-block;
    cursor: pointer;
    margin-left: -10px;
}

.menu-more a {
    color: #e97c2a;
}

a.menu-more-button {
    color: #ddddc6;
    font-weight: 600;
    font-size: 13px;
    display: inline-block;
    padding: 5px 10px;
    position: relative;
}

a.menu-more-button:hover {
    text-decoration: none;
}

a.menu-more-button i:hover {
    background: none;
}

div.dropdown.menu-more.open a.menu-more-button {
    background-color: #343543;
    box-shadow: 0px 2px 3px 0px #D8D8D8;
    border-radius: 3px 3px 0px 0px;
}

div.dropdown.menu-more.open ul.dropdown-menu.menu-subheader {
    padding: 20px 0 0 0;
    background-color: #343543;
    margin: 0;
    border: none;
    border-radius: 0px 3px 3px 3px;
}

div.dropdown.menu-more.open ul.dropdown-menu.menu-subheader a {
    text-transform: uppercase;
    display: block;
    width: 100%;
    padding: 0 0 0 10px;
    line-height: 32px;
}

div.dropdown.menu-more.open ul.dropdown-menu.menu-subheader a:hover {
    text-decoration: none;
    color: #101010;
    background-color: #343543;
}

ul.menu01-itemsr li a {
    font-family: 'Open Sans', sans-serif;
    font-size: 11px;
    text-transform: uppercase;
}

#header-center .checkbox {
    margin: 0 0 0 9px;
}

#header-center .checkbox-slider--b-flat input+span {
    padding-left: 15px;
}

#header-center .checkbox-slider--b-flat input+span:before {
    width: 31px;
    background-color: #A1A1A1;
    height: 18px;
    background: #A1A1A1;
    box-shadow: inset 0px 1px 1px 0px rgba(0, 0, 0, 0.50);
    border-radius: 11px;
    animation: none;
    top: 1px;
}

#header-center .checkbox-slider--b-flat input+span:after {
    width: 20px;
    height: 20px;
    top: 0px;
    left: -1px;
    animation: none;
}

#header-center .checkbox-slider--b-flat input:checked+span:before {
    width: 31px;
    background-color: #e97c2a;
    height: 18px;
    background: #e97c2a;
    box-shadow: inset 0px 1px 1px 0px rgba(0, 0, 0, 0.50);
    border-radius: 11px;
    animation: none;
}

#header-center .checkbox-slider--b-flat input:checked+span:after {
    width: 20px;
    height: 20px;
    top: 0px;
    left: -8px;
    animation: none;
}

.header-sub-wrapper {
    width: 100%;
    height: 26px;
}

.header-sub {
    background: #FBFBFB;
    box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 0.23);
}

.header-sub a {
    text-transform: uppercase;
    color: #000;
    opacity: .7;
    font-family: 'Lato', sans-serif;
    font-weight: bold;
    display: inline-block;
    width: 100%;
    text-align: center;
}

@media (-webkit-min-device-pixel-ratio: 2),
(min-resolution: 192dpi) {
    #header-top .logo-ed {
        background-image: url('https://mnmstatic.net/v_128/img/mnm/h9_logo_ed.svg');
    }
}

#header-top ul#edlist {
    list-style-type: none;
    margin: 2px 0 0 0;
    padding: 0;
    float: left;
}

#header-top ul li {
    font-size: 14px;
    color: #fff;
    position: relative;
    float: left;
    line-height: 140%;
}

#header-top ul#edlist li a {
    padding-top: 1px;
    text-decoration: none;
    height: 27px;
    margin-right: 20px;
    color: #fff;
    display: inline-block;
}

#header-top ul#edlist li.selected a {
    white-space: nowrap;
}

#header-top ul#edlist li a:hover {
    color: #fff;
}

#header-top ul#edlist li.selected a,
#header-top ul#edlist li a:hover {
    background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAADCAIAAADdv/LVAAAAFElEQVQI12P4//8/EwMDAxMDAwMAHgYDArjSWt4AAAAASUVORK5CYII=') bottom left repeat-x;
}

#header-top ul#userinfo {
    list-style-type: none;
    margin: 8px 0 0 0;
    padding: 0;
    float: right;
}

#header-top ul#userinfo li {
    position: relative;
    float: left;
}

#header-top ul#userinfo li a {
    text-decoration: none;
    color: #fff;
}

#userinfo #avatar {
    width: 30px;
    height: 30px;
}

ul#userinfo a.notifications {
    border-left: 1px solid rgba(16, 16, 16, .2);
    font-size: 20px;
    height: 35px;
    margin-left: 10px;
    padding-left: 10px;
    padding-top: 5px;
    display: inline-block;
}

ul#userinfo a.notifications span {
    display: inline-block;
    min-width: 10px;
    padding: 1px 7px;
    font-size: 12px;
    font-weight: 700;
    line-height: 1;
    color: #e97c2a;
    text-align: center;
    white-space: nowrap;
    vertical-align: bottom;
    background-color: white;
    border-radius: 10px;
    margin-left: -12px;
    margin-top: 1px;
    border: 3px solid #e97c2a;
}

ul#userinfo a:hover {
    color: #fff;
}

ul#userinfo li.search {
    border-right: 1px solid rgba(16, 16, 16, .2);
    font-size: 20px;
    height: 35px;
    margin-right: 10px;
    padding-right: 10px;
}

#header-top .usertext {
    font-size: 15px;
    color: #fff;
    margin-right: 10px;
    margin-top: 4px;
}

#header-top .userlogin {
    width: 40px;
    height: 30px;
    margin: 0;
    text-shadow: none;
    cursor: pointer;
    padding-top: 6px;
}

#header-top .userlogin:before {
    font-family: FontAwesome;
    content: "\f011";
    color: #fff;
    font-size: 19px;
    padding-left: 12px;
    text-rendering: optimizeLegibility !important;
    -webkit-font-smoothing: antialiased !important;
    -moz-osx-font-smoothing: grayscale !important;
}

#header-center .logo-eli {
    background: url('https://mnmstatic.net/v_128/img/mnm/h9_eli.png') no-repeat left top;
    background-size: 39px 35px;
    height: 35px;
    width: 55px;
    margin: 5px 0 0 20px;
    float: left;
    font: 0/0 serif;
    text-shadow: none;
    color: transparent;
    cursor: pointer;
}

@media (-webkit-min-device-pixel-ratio: 2),
(min-resolution: 192dpi) {
    #header-center .logo-eli {
        background-image: url('https://mnmstatic.net/v_128/img/mnm/h9_eli.svg');
    }
}

#header-center .edname {
    margin: 6px 0 0 15px;
    padding: 0;
    font-size: 25px;
    position: relative;
    float: left;
}

.edname a:link,
.edname a:visited {
    color: inherit;
}

.subname,
.subname_base {
    font-family: 'Lato', 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

.subname,
.upname a:visited,
.subname a:link {
    color: #fff !important;
    background-color: #ccc;
    padding: 3px 10px;
    border-radius: 20px;
    text-transform: lowercase;
}

.subname.big {
    font-size: 110%;
}

#searchform_button {
    float: left;
    cursor: pointer;
    color: #fff;
    margin-right: 10px;
}

.searchform {
    height: auto;
    position: relative;
    float: right;
}

#nav-panel .searchform .searchbox {
    width: 280px;
}

.searchform .searchbox {
    border: 0px;
    height: 24px;
    font-size: 16px;
    padding-top: 0px;
    padding-left: 10px;
    background-size: 20px 300px;
}

.header-menu01 {
    height: 36px;
    padding-top: 4px;
}

.header-menu01 ul.menu01-itemsl {
    list-style-type: none;
    margin-top: 4px;
    padding: 0;
    float: left;
}

.menu01-itemsl span.button-new {
    display: none;
}

.menu01-itemsl li {
    margin-right: 20px;
}

.header-menu01 a {
    font-size: 12px;
    font-weight: 600;
}

.header-menu01 a.submit_new_post,
.header-menu01 a.submit_new_article {
    text-transform: uppercase;
    padding: 4px 10px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    color: #cbd4dd;
    margin-top: -6px;
    font-size: 12px;
    font-weight: 400;
    background-color: #22232b;
    background-image: linear-gradient(-180deg, #5f6179 0%, #343543 100%);
    border: 1px solid #22232b;
    box-shadow: 0 1px 0 0 #22232b;
}

.header-menu01 a.submit_new_article {
    margin-left: -16px;
}

.header-menu01 .selected a.submit_new_post,
.header-menu01 .selected a.submit_new_article {
    background: none;
}

.menu01-itemsl a.submit_new_post:before {
    font-family: FontAwesome;
    content: "\f067";
    display: inline-block;
    margin-right: 6px;
    font-size: 15px;
    text-rendering: optimizeLegibility !important;
    -webkit-font-smoothing: antialiased !important;
    -moz-osx-font-smoothing: grayscale !important;
    position: relative;
    top: 2px;
}

.header-menu01 a.submit_new_article:before {
    font-family: FontAwesome;
    content: "\f040";
    display: inline-block;
    margin-right: 6px;
    font-size: 15px;
    text-rendering: optimizeLegibility !important;
    -webkit-font-smoothing: antialiased !important;
    -moz-osx-font-smoothing: grayscale !important;
    position: relative;
    top: 0px;
}

.menu01-itemsl a.submit_new_post:hover,
.menu01-itemsl a.submit_new_article:hover {
    background-image: none;
}

.header-menu01 ul.menu-subheader li {
    float: none;
}

.header-menu01 ul.menu-subheader li a {
    background: none;
}

.header-menu01 ul.menu-subheader li.selected a {
    font-weight: bold;
}

ul.menu01-itemsl li a {
    text-transform: uppercase;
    color: #ddddc6;
    font-size: 13px;
}

.menu01-itemsl a.username {
    text-transform: none;
}

.header-menu01 ul li {
    position: relative;
    float: left;
}

.header-menu01 ul li a {
    color: #ddddc6;
    text-decoration: none;
    display: inline-block;
    padding-bottom: 9px;
}

.header-menu01 ul.menu01-itemsr {
    list-style-type: none;
    margin-top: 3px;
    padding: 0;
    float: right;
    display: block;
}

.menu01-itemsr li {
    margin: 0 10px;
}

.header-menu01 .selected a {
    white-space: nowrap;
}

.header-menu01 ul li a:hover {
    color: #e97c2a;
}

.header-menu01 .selected a,
.header-menu01 ul li a:hover {
    background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAADCAIAAADdv/LVAAAAFElEQVQI12P4n8LAxMDAwMTAwAAADwEBaFtNWv8AAAAASUVORK5CYII=') repeat-x 100% 100%;
}

#nav-menu {
    display: inline-block;
    color: #fff;
    margin: 0 5px 0 10px;
    padding: 0;
    float: left;
    cursor: pointer;
}

#nav-menu:before {
    font-family: FontAwesome;
    content: "\f0c9";
    font-size: 24px;
    text-rendering: optimizeLegibility !important;
    -webkit-font-smoothing: antialiased !important;
    -moz-osx-font-smoothing: grayscale !important;
    font-weight: normal;
}

#nav-panel {
    position: fixed;
    display: none;
    top: 47px;
    right: 0px;
    overflow: auto;
    z-index: 901;
    width: auto;
    height: auto;
    white-space: normal;
    margin-bottom: 0px;
    padding: 0 25px 0 25px;
    background-color: #3c3c3c;
    box-shadow: 1px 1px 8px rgba(0, 0, 0, 0.5);
    -moz-box-shadow: 1px 1px 8px rgba(0, 0, 0, 0.5);
    -webkit-box-shadow: 1px 1px 8px rgba(0, 0, 0, 0.5)
}

#nav-panel .header-menu01 {
    height: auto;
    padding: 0;
    margin: 0;
    font-size: 16px;
    background-color: #3c3c3c;
}

#nav-panel ul {
    margin-bottom: 10px;
    display: block;
    float: none;
}

#nav-panel ul li {
    float: none;
    margin: 0;
}

#nav-panel ul li a {
    padding: 2px 0;
    white-space: nowrap;
    color: #fff;
}

#nav-panel ul li.selected a,
#nav-panel ul li a:hover {
    background: none;
    font-weight: bold;
    display: block;
}

#nav-panel .searchform {
    float: none;
    position: relative;
    display: block;
    margin: 10px 0 0 0;
    height: auto;
}

#wrap {
    color: #aaa;
    background-color: #22232b !important;
    padding-top: 2px;
    min-width: 805px;
    max-width: 1300px;
    width: 99%;
    text-align: left;
    margin: auto;
    margin-top: 0;
}

.banner-right {
    width: 100%;
    margin: 5px 0 20px 0;
}

#container {
    clear: both;
    margin-top: 15px;
}

#sidebar {
    display: none;
}

#newswrap {
    margin: 20px 56px 0 4px;
}

#singlewrap {
    margin: 0 10px 0 10px;
}

#map {
    overflow: hidden;
}

#toplink {
    display: none;
    position: relative;
    background: #22232b;
    padding: 15px;
    margin: 20px 10px;
    vertical-align: middle;
    border-radius: 3px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
}

#toplink h1 {
    font-size: 18px;
    font-weight: normal;
    margin: 0 0 10px 0;
    font-family: 'Lato', sans-serif;
}

#toplink h1 a {
    color: #9B3A0C;
}

#toplink .thumbnail {
    float: left;
    margin-right: 8px;
}

#toplink .thumbnail.medium {
    width: 120px;
    height: 120px;
}

#toplink p {
    color: #3C3C3C;
    font-size: 13px;
}

#toplink .footer,
.topbox .footer {
    display: block;
    margin-top: 5px;
    text-align: right;
    color: #fff;
}

#toplink a.footer {
    background-color: #FF6600;
    border-radius: 10px;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    font-size: 13px;
    text-transform: uppercase;
    padding: 1px 15px;
}

.topbox {
    display: none;
}

.topbox .title {
    margin-top: 5px;
    text-transform: capitalize;
    font-variant: small-caps;
    text-align: right;
    color: #aaa;
    font-size: 120%;
}

.topbox .footer {
    text-decoration: none;
}

.topbox.opinion {
    background: #f9f9f9 url(https://mnmstatic.net/v_128/img/common/opinion_01.png) 99% 5px no-repeat;
    padding-right: 40px;
}

.mini-icon-text {
    vertical-align: middle;
}

.mini-icon-text i.fa.fa-edit {
    color: #e97c2a;
}

.subheader {
    width: auto;
    height: 30px;
    padding-left: 25px;
    list-style-type: none;
}

.subheader:after {
    content: ".";
    display: block;
    height: 0;
    clear: left;
    visibility: hidden;
}

