css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leetcode.com") {
  /* Background, darkest */
  body, .dropdown-menu, .dropdown-menu > li > a, .form-signin, .problem-category .problem-category-info a,
  .progress-panel-base ul > li:nth-of-type(2n+1), .list-group-item, .topic .vote-buttons, .navbar-default,
  #status {
    background-color: #000;
    color: #aaa;
  }
  
  /* Odd rows */ .question-list-table tbody tr:nth-of-type(2n+1), .side-bar ul.side-bar-list li:nth-of-type(2n+1),
  /* Modals */   .panel-default > .panel-heading, .panel,
  /* Tags */     .category > ul > li .tag-list .tag,
  /* DIsabled */ .pagination > .disabled > a
  {
    background-color: #222;
    color: #ccc;
  }
  /* Inputs, light */
  pre, input, select, button, button.icon-btn, a.icon-btn, .form-control, .btn-default, .btn-default:hover,
  .problem-category .problem-category-info a:hover, .dropdown-menu > li > a:hover, .popover,
  .topic [component="post/parent"], .pagination > li > a, .pagination > li > a:hover,
  /* Open tabs */ .open > .dropdown-toggle.btn-default, a.tab-item.icon-btn.active, .filter-dropdown-menu-item:hover {
    background-color: #444;
    color: #fff;
  }
  /* "Grey" text */
  .msg .description, .progress-panel-base ul .points-info .info-value, .progress-panel-base, .text-gray,
  .side-bar ul.side-bar-list li, .panel-default > .panel-heading, a.permalink, .topic .threaded-replies .replies-last,
  .title, .category > ul > li:not(.unread) h2 a, a.permalink:hover, .btn:hover {
    color: #aaa;
  }
  
  /* Syntax highlighting */
  pre.markdown-highlight, .hljs.hljs, .ace-tm, .ace-tm .ace_gutter {
    background-color: #272822;
    color: #fff;
  }
  .ace-tm .ace_identifier, .ace-tm .ace_keyword.ace_operator {
    color: #fff;
  }
  .hljs-keyword, .ace-tm .ace_keyword, .ace-tm .ace_constant.ace_language, .ace-tm .ace_constant {
    color: #f44;
  }
  .hljs-title, .ace-tm .ace_support.ace_function {
    color: #A6E22E;
  }
  .hljs-string, .ace-tm .ace_string {
    color: #FD971F;
  }
  .hljs-number, .ace-tm .ace_constant.ace_numeric {
    color: #66D9EF;
  }
  .hljs-comment {
    color: #75715e;
  }
  
  /* Hiding things */
  hr, #storeNav, #bookNav, #subscribe, #announcement, #support-link, .separator-line, .alert {
    display: none;
  }
  
  /* Misc */
  a, a:focus {
    color: #4af;
  }
  a:hover {
    color: #6cf;
  }
  /* Borders */ .panel, .CodeMirror, input, select, .form-control, .btn, button.icon-btn, a.icon-btn, pre,
  .pagination > .disabled > a, .pagination > li > a, .editor, #details-summary {
    border: solid 0.5px #555;
  }
  .side-bar {
    border-left-color: #555;
  }
  .label-Medium, .label-warning {
    color: #000;
  }
}
