css:
@-moz-document domain("appleinsider.ru") {
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td 
{
	font-family: Tahoma;
}

#global-wrapper, body
{
    color: #c0c0c0;
    background: #363e45;
}

aside.menu.menu-dark nav,
aside.menu.menu-light nav
{
  background: #363e45;
}

aside.menu nav
{
    padding: 10px 20px 16px;
}


h1, h2, h3,
.form-table th, .form-wrap label {
    color: #c0c0c0;
}

body header.header-site .sitename a, 
.entry-info a, 
.entry-header .entry-title a,
#respond,
.related-posts .related-also .title,
nav.trends a,
aside.abouts a,
aside.podcast a strong, aside.discount a strong,
aside.popular > h3 strong,
.pagination-number a, .pagination-number span,
aside.telegram a strong,
aside.menu h3,
aside.menu.menu-dark a,
aside.menu.menu-light a,
article.preview .tablet-title a, 
article.preview .second .title a
{
    color: #76c36e;	/* Зеленый */
}

aside.menu a:hover {
    background: #76c36e;
    color: #fff;
}


aside.abouts,
aside.podcast a, aside.discount a,
aside.discount-box,
body header.header-site .trends ul,
.related-posts article.preview .cover,
aside.telegram a,
article + article,
aside.menu.menu-light li + li,
aside.popular article .entry-title,
aside.abouts a
{
    /*border: solid 1px #62717e;*/
    border-color: #62717e;
}

/*article + article,
aside.menu.menu-light li + li
{
    border-top: solid 1px #62717e;
}*/

aside.popular,
.comment .reply,
.header-trand nav.trends,
aside.menu.menu-light nav
{
    border-bottom: 1px solid #62717e;
}

aside.menu.menu-light nav
{
    border-left: 1px solid #62717e;
}

aside.menu.menu-light nav
{
    border-right: 1px solid #62717e;
}



.comment .controls .rating.positive {
    color: #4dc441;
    background-color: #b3ffad;
}

.comment .controls .rating.negative {
    color: #ff1753;
    background-color: #ffd0d6;
}

/* Комментарии */
.comment {
    color: #c0c0c0;
}

textarea {
    background: #4c5863;
}

#respond .field
{
    width: 100%;
}


/* Кнопка ответить в комментариях */
.comment .reply .link-wrapper {
    background: #548c4e;
}
.comment .reply a {
    color: #fff;
}

/* Реклама */
.foreign-posts,
.skyline .wrapper,
.branding-placeholder
{
    visibility: hidden;
    height: 0px;
}

aside.abouts a:first-child
{
    margin-right:0;
}

article.preview .second .description:after
{
    display:none;
}
}
