css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("steemit.com") {

  /*Colours:
    #f0f0f0 top header
    ##1F1F1F main header
    #306093
    #1A1A1A bottom header
    #111111 borders
    #383635 tabs
    #CDCAC1 tab text
    #fafafa article backgrund
    #B0AEAC text
    #7AC533, #AAF563 active tab text
    #547CAA, #8BB9E0 link text
    #EC5840 warnings
  */
     
  body {
	  background-color: black;
	  color: #B0AEAC;
  }
  
  a {
    color: #547CAA;
  }
  
  a:hover {
    color: #8BB9E0;
  }
  
  .help-text {
    color: #B0AEAC;
  }
  
  input[type=text] {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  input[type=text]:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  input[type=password] {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  input[type=password]:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  input[type=url] {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  input[type=url]:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  textarea {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  textarea:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  textarea:active {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  select {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  select:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  /*Header*/
  
  .Header .Header__top {
    background: url("data:image/jpeg;base64,") repeat scroll left top transparent !important;
    border-bottom: 1px solid #111111;
  }
  
  .Header .menu {
    background-color: inherit;
  }
  
  li.Header__userpic .Userpic {
    border: 1px solid #111111;
  }
  
  .Header .menu .hamburger {
    background: black;
    border: 1px solid black;
  }
  
  .VerticalMenu > li.title {
    border-bottom: 1px solid #111111;
    color: #7AC533;
  }
  
  .Header .Header__sub-nav {
    background-color: #1A1A1A;
    border-bottom: 1px solid #111111;
    padding: 10px 10px 0px 10px;
  }
  
  .Header .HorizontalMenu {
    background-color: #1A1A1A;
    margin: 0px;
    padding: 0px;
  }
  
  .Header .HorizontalMenu > li {
    color: #CDCAC1;
    background-color: #383635;
    margin: 2px 10px -1px 10px;
    padding: 10px 10px 10px 10px;
    border: 1px solid #111111 !important;
    border-radius: 10px 10px 0px 0px;
    text-transform: capitalize;
  }
  
  .Header .HorizontalMenu > li > a {
    color: #CDCAC1;
  }
  
  .Header .HorizontalMenu > li > a:hover {
    background-color: #383635;
    color: #7AC533;
  }
  
  .Header .HorizontalMenu .active {
    background-color: #383635;
    border-bottom: 1px solid black;
  }
  
  .Header .HorizontalMenu .active > a {
    border: 1px solid #111111;
    background-color: #383635;
    color: #7AC533 !important;
    outline: 0;
  }
  
  .Header__sub-nav li > a {
    background-color: #383635;  
    border: 1px solid #111111;
  }
  
  .UserProfile__banner {
    background-color: #1F1F1F;
    border-bottom: 1px solid #111111;
  }
  
  .UserProfile__banner > div > h1 {
    color: #7AC533;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:first-of-type {
    border: 1px solid #111111;
    background-color: #383635;
    color: #7AC533;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:first-of-type:hover {
    background-color: black;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:last-of-type {
    border: 1px solid #111111;
    background-color: #383635;
    color: #EC5840;
  }
  
  .UserProfile__banner .UserProfile__buttons .button:last-of-type:hover {
    background-color: black;
  }
  
  .UserProfile__banner > .column {
    background: inherit !important;
  }
  
  .UserProfile__banner .UserProfile__stats a {
    color: #547CAA;
  }
  
  .UserProfile__banner .UserProfile__stats > span {
    border-color: #111111;
  }
  
  .UserProfile__top-nav {
    background-color: #1A1A1A;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    border-bottom: 1px solid #111111;
  }
  
  .UserProfile__top-nav .row .columns {
    overflow-x: unset !important;
  }
  
  .UserProfile__top-menu {
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    max-width: none !important;
  }
  
  .UserProfile__top-menu .columns {
    max-width: none !important;
  }

  .UserProfile__top-menu .columns .menu a {
    color: #CDCAC1;
    background-color: #383635;
    margin: 10px 10px -1px 10px;
    padding: 0px 10px 0px 10px;
    border: 1px solid #111111;
    border-radius: 10px 10px 0px 0px;
  }
  
  .UserProfile__top-nav .menu > li > a:hover {
    background-color: #383635;
    color: #7AC533;
  }
  
  .UserProfile__top-nav .menu > li > a.active {
	  background-color: black;
	  color: #7AC533;
    border-bottom: 1px solid black;
    outline: 0;
  }
  
  .dropdown-pane {
    border: 1px solid #111111;
  }

  .VerticalMenu {
    background-color: #383635;
    border: 1px solid #111111;
  }
  
  .VerticalMenu > li > a {
    border: 1px solid #111111;
	  color: #CDCAC1;
  }
  
  .VerticalMenu > li > a:hover {
    background-color: #1A1A1A;
  }
  
  /* Followers */
  
  .UserList {
    border: 1px solid #111111;
    border-radius: 10px;
    padding: 10px;
    background-color: #383635;
    margin-bottom: 10px;
  }
  
  .pager .button {
    border: 1px solid #111111;
    background-color: #383635;
    color: #7AC533;
  }
  
  .pager .button:hover {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    color: #AAF563;
  }
  
  .UserList > div > div > h3 {
    color: #7AC533;
  }
  
  .UserList > div > div > table {
    border: 1px solid #111111;
  }
  
  .UserList > div > div > table > tbody > tr:nth-child(odd) {
    background-color: #1A1A1A;
  }
  
  .UserList > div > div > table > tbody > tr:nth-child(even) {
    background-color: #1F1F1F;
  }
  
  .UserList > div > div > table > tbody > tr .button:first-of-type {
    border: 1px solid #111111;
    background-color: #383635;
    color: #7AC533;
  }
  
  .UserList > div > div > table > tbody > tr .button:first-of-type:hover {
    background-color: black;
    color: #7AC533;
  }
  
  .UserList > div > div > table > tbody > tr .button:last-of-type {
    border: 1px solid #111111;
    background-color: #383635;
    color: #EC5840;
  }
  
  .UserList > div > div > table > tbody > tr .button:last-of-type:hover {
    background-color: black;
    color: #EC5840;
  }
  
  /* Rewards Page */
  
  .UserProfile__tab_content .UserWallet > div:first-child {
    border: 1px solid #111111;
    border-radius: 10px 10px 0px 0px;
    background-color: #383635;
  }
  
  .UserProfile__tab_content .UserWallet > div:first-child > div {
    background-color: #383635 !important;
    border: none !important;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row {
    border: 1px solid #111111;
    border-top: none;
    border-radius: 0px 0px 10px 10px;
    margin-bottom: 10px;
    background-color: #1F1F1F;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div {
    display: none;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div {
    border: 1px solid #111111;
    border-radius: 10px;
    background-color: #383635;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > h4 {
    margin-top: 20px;
    color: #7AC533;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table {
    border: 1px solid #111111;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table > tbody > tr:nth-child(odd) {
    background-color: #1A1A1A;
  }
  
  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table > tbody > tr:nth-child(even) {
    background-color: #1F1F1F;
  }
  
  .TransferHistoryRow__text > a {
    color: #547CAA;
  }
    
  /* Wallet - Balances */
  
  .reveal {
    background-color: black;
    color: #B0AEAC;
    border: 1px solid #111111;
    border-radius: 10px;
    padding: 10px;
  }
  
  .reveal > form {
    background-color: #383635;
    border: 1px solid #111111;
    border-radius: 10px;
  }
  
  .reveal > form > div+div > div > label {
    color: #7AC533;
  }
  
  .reveal > form > div+div > div > input {
    border: 1px solid #111111;
    background-color: black;
    color: #B0AEAC;
  }
  
  .reveal > div {
    background-color: #383635;
    margin: 0px;
    padding: 10px;
    border: 1px solid #111111;
    border-radius: 10px;
  }
  
  .reveal > div > h3 {
    color: #7AC533;
  }
  
  .reveal > div .input-group-label  {
    background-color: black;
    color: #B0AEAC;
    border: 1px solid #111111;
  }
  
  .reveal .button {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    color: #7AC533;
    text-decoration: none !important;
  }
  
  .reveal .button:focus {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    color: #7AC533;
  }
  
  .reveal .button:hover {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    color: gold;
  }
  
  .ConfirmTransactionForm > h4 {
    color: #7AC533;
  }
  
  .LoginForm > center > h3 {
    color: #7AC533;
  }
  
  .LoginForm label {
    color: #B0AEAC;
  }
  
  .LoginForm .info {
    color: #B0AEAC;
  }
  
  .UserWallet__claimbox {
    border: 1px solid #111111;
    border-radius: 10px;
    background-color: #383635;
    padding-left: 10px;
    padding-right: 10px;
  }
  
  .UserWallet__claimbox .button {
    border: 1px solid #111111;
    border-radius: 10px;
    background-color: #1A1A1A;
    color: #7AC533;
    margin-right: 0px !important;
  }
  
  .UserWallet__claimbox .button:hover {
    background-color: #1A1A1A;
    color: gold;
    border: 1px solid #111111;
  }
  
  .UserWallet__claimbox .button:focus {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    color: #7AC533;
  }
  
  .UserWallet > div:nth-last-child(1) {
    border: 1px solid #111111;
    border-radius: 10px;
    background-color: #383635;
    margin-bottom: 10px;
  }
  
  .UserWallet > div:nth-last-child(1) > div > h4 {
    margin-top: 20px;
    color: #7AC533;
  }
  
  .UserWallet > div:nth-last-child(1) > div > table {
    border: 1px solid #111111;
  }
  
  .UserWallet > div:nth-last-child(1) > div > table > tbody > tr:nth-child(odd) {
    background-color: #1A1A1A;
  }
  
  .UserWallet > div:nth-last-child(1) > div > table > tbody > tr:nth-child(even) {
    background-color: #1F1F1F;
  }
  
  .UserWallet > div:nth-last-child(2) {
    display: none;
  }
  
  .UserWallet > div:nth-last-child(4) {
    border: 1px solid #111111;
    border-top: none;
    border-radius: 0px 0px 10px 10px;
    padding: 5px 10px 10px 10px;
    background-color: #383635;
  }
  
  .UserWallet > div:nth-last-child(4) > div:first-of-type {
    border: 1px solid #111111;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: #1F1F1F;
    color: #7AC533;
  }
  
  .UserWallet > div:nth-last-child(4) > div:first-of-type .secondary {
    color: #B0AEAC;
  }
  
  .UserWallet > div:nth-last-child(4) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  
  .UserWallet > div:nth-last-child(5) {
    border-left: 1px solid #111111;
    border-right: 1px solid #111111;
    padding: 5px 10px 5px 10px;
    background-color: #383635 !important;
  }
  
  .UserWallet > div:nth-last-child(5) > div:first-of-type {
    border: 1px solid #111111;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: #1F1F1F;
    color: #7AC533;
  }
  
  
  .UserWallet > div:nth-last-child(5) > div:first-of-type .secondary {
    color: #B0AEAC;
  }
  
  .UserWallet > div:nth-last-child(5) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  
  .UserWallet > div:nth-last-child(6) {
    border-left: 1px solid #111111;
    border-right: 1px solid #111111;
    padding: 5px 10px 5px 10px;
    background-color: #383635;
  }
  
  .UserWallet > div:nth-last-child(6) > div:first-of-type {
    border: 1px solid #111111;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: #1F1F1F;
    color: #7AC533;
  }
  
  .UserWallet > div:nth-last-child(6) > div:first-of-type .secondary {
    color: #B0AEAC;
  }
  
  .UserWallet > div:nth-last-child(6) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  
  .UserWallet > div:nth-last-child(7) {
    border-left: 1px solid #111111;
    border-right: 1px solid #111111;
    padding: 5px 10px 5px 10px;
    background-color: #383635 !important;
  }
  
  .UserWallet > div:nth-last-child(7) > div:first-of-type {
    border: 1px solid #111111;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: #1F1F1F;
    color: #7AC533;
  }
  
  .UserWallet > div:nth-last-child(7) > div:first-of-type .secondary {
    color: #B0AEAC;
  }
  
  .UserWallet > div:nth-last-child(7) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  
  .UserWallet > div:nth-last-child(8) {
    border: 1px solid #111111;
    border-bottom: none;
    border-radius: 10px 10px 0px 0px;
    padding: 10px 10px 5px 10px;
    background-color: #383635;
  }
  
  .UserWallet > div:nth-last-child(8) > div:first-of-type {
    border: 1px solid #111111;
    border-right: none;
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
    background-color: #1F1F1F;
    color: #7AC533;
  }
  
  .UserWallet > div:nth-last-child(8) > div:first-of-type .secondary {
    color: #B0AEAC;
  }
  
  .UserWallet > div:nth-last-child(8) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  
  .WalletSubMenu {
    border-bottom: none;
    margin: 10px 0px 0px 0px;
  }
  
  .WalletSubMenu > li > a {
    border: 1px solid #111111;
    border-radius: 10px 10px 0px 0px;
    background-color: #1F1F1F;
    color: #CDCAC1;
    margin: 10px 10px -1px 10px;
    padding: 0px 10px 0px 10px;
  }
  
  .WalletSubMenu > li > a:hover {
    color: #7AC533;
  }
  
  .WalletSubMenu > li > a.active {
    border-bottom: 1px solid #383635;
    background-color: #383635;
    color: #7AC533;
    outline: 0;
  }
  
  .UserWallet__buysp.button.hollow {
    border: 1px solid #111111;
    border-radius: 10px 10px 0px 0px;
    margin: 19px 10px -15px 10px;
    padding: 5px 10px 5px 10px;
    background-color: #1F1F1F;
    color: #CDCAC1;
  }
  
  .UserWallet__buysp.button.hollow:hover {
    color: gold;
  }
  
  /* Wallet - Permissions & Password */
  
  .UserProfile__top-nav + div > div > a.float-right {
    float: none !important;
    width: 100px;
    margin-left: auto;
    margin-right: auto;
  }
  
  .UserProfile > div > div > br {
    display: none;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) {
    background-color: #383635;
    border: 1px solid #111111;
    border-radius: 10px;
    padding: 10px 10px 0px 10px;
    margin-bottom: 10px;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div {
    background-color: #1F1F1F;
    border: 1px solid #111111;
    border-radius: 10px;
    padding: 10px 10px 0px 10px;
    margin-bottom: 10px;
    flex: 0 0 100% !important;
    max-width: 100% !important;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > div > div > label {
    color: #7AC533;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div .button {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    width: 9rem;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div .button a {
    color: #7AC533;
    font-size: 0.9rem;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div .button a:hover {
    color: gold;
  }
  
  .ChangePassword__rules > hr {
    border: 1px solid #111111;
  }
  
  .ChangePassword > form > h4 {
    color: #7AC533;
  }
  
  .ChangePassword > form > label {
    color: #7AC533;
  }
  
  .ChangePassword > form > label > span.secondary {
    color: #7AC533;
  }
  
  .ChangePassword > form > div > label {
    color: #7AC533;
  }
  
  .ChangePassword .float-right a {
    margin: 0px 0px 10px 0px;
    padding: 10px;
    border: 1px solid #111111 !important;
    background-color: #1A1A1A;
    color: #7AC533;
    font-size: 0.9rem;
    font-family: sans-serif;
    line-height: 1;
    display: inline-block;
    border-radius: 3px;
    vertical-align: middle;
    text-align: center;
    width: 20rem;
  }
  
  .ChangePassword .float-right a:hover {
    color: gold;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form > center {
    width: 20rem;
    margin-left: auto;
    margin-right: 10px
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form .button {
    border: 1px solid #111111;
    background-color: #1A1A1A;
    color: #7AC533;
    margin: 10px;
    padding: 10px;
    width: 20rem;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form .button:hover {
    color: gold;
  }
  
  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form > div:last-of-type {
    width: 20rem;
    margin-left: auto;
    margin-right: 10px;
  }
  
  /* Settings */
  
  .UserProfile__tab_content .Settings > div > form {
    background-color: #383635;
    border: 1px solid #111111;
    border-radius: 10px;
    padding: 10px;
    max-width: 100% !important;
    flex: 100% !important;
  }
  
  .UserProfile__tab_content .Settings > div > form > h3 {
    color: #7AC533;
  }
  
  .UserProfile__tab_content .Settings > div > form > label {
    background-color: #1F1F1F;
    color: #7AC533;
    border: 1px solid #111111;
    border-bottom: none;
    border-radius: 10px 10px 0px 0px;
    padding: 10px;
  }
  
  .UserProfile__tab_content .Settings > div > form > div {
    background-color: #1F1F1F;
    border: 1px solid #111111;
    border-top: none;
